INSERT INTO `t_check_list` VALUES ('117d8df9-c76c-4d1b-9e56-7c9725e6e2d9', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '线路防护', '穿过冷间保温层的电气线路应相对集中敷设，是否采取可靠的防火和防止产生冷桥的措施。', '现场查看', '穿过冷间保温层的电气线路未套铁管直接穿过库房保温层', '《冷库设计规范》（GB50072）7.3.8; 《冷库安全规程》(GB 28009)7.10', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '穿过冷间保温层的电气线路已套铁管直接穿过库房保温层', 78, '冷库区域', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('dc547a3d-db34-482f-9c85-bb6c45493c00', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '防火墙', '冷藏间与穿堂之间的隔墙是否为防火隔墙，该防火隔墙的耐火极限不应低于3.00。', '现场查看，查阅消防验收资料。', '冷藏间与穿堂之间的隔墙未采用耐火极限不应低于3.00小时的防火隔墙', '《冷库设计规范》（GB50072）4.2.3', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '冷藏间与穿堂之间的隔墙已采用耐火极限不应低于3.00小时的防火隔墙', 79, '冷库区域', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('ec0fc32b-829b-4b88-8b5e-e24ee95a0dd0', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '防护装置', '构成重大危险源的单位是否配备过滤式防毒面具、正压式空气呼吸器、隔离式防护服、橡胶手套、胶靴和化学安全防护眼镜，其中正压式空气呼吸器至少配置2套，其他防护器具应满足岗位人员一人一具。非重大危险源单位应根据实际需要至少配备1套隔离式防护服、防毒面具及岗位人员一人一具橡胶手套、胶靴和化学安全防护眼镜。', '现场查看、询问，查阅相关资料。', '防护装置配备不足', '《危险化学品重大危险源监督管理暂行规定》（国家安全生产监督管理总局令第40号）第20条', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '防护装置配备已经满足要求', 80, '重大危险源', NULL, NULL);
