INSERT INTO `t_check_list` VALUES ('06de7f1a-d7be-408a-a653-ec2fbe53a387', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '应急演练', '生产经营单位应当定期组织演练；每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练', '查阅演练计划、演练记录、演练照片、演练评估报告等', '未按规定定期组织演练', '《安全生产法》第七十八条，《生产安全事故应急预案管理办法》（国家安全生产监督管理总局令第88号）第三十三条', '《安全生产法》第九十四条第六项', '责令限期改正，可以处五万元以下的罚款', '2,5', '已按规定组织演练', 9, '应急管理', '演练计划、演练记录、演练照片、演练评估报告等', NULL);
INSERT INTO `t_check_list` VALUES ('b59edfd5-70f2-4081-aab8-b63a2cb85e19', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '是否在有关设备、设施上设置警示标志', '生产经营单位应当在有粉尘爆炸危险因素的生产经营场所和除尘器、风管等设备上设置明显的安全警示标志', '查阅警示标志一览表、现场查看', '未在有粉尘爆炸危险因素的生产经营场所和除尘器、风管等设备上设置明显的安全警示标志', '《安全生产法》第三十二条', '《安全生产法》第九十六条第一项', '责令限期改正，可以处五万元以下的罚款', '2,5', '已在有粉尘爆炸危险因素的生产经营场所和除尘器、风管等设备上设置明显的安全警示标志', 10, '警示标志', '警示标志一览表、生产车间等重点部位警示标志设置照片', NULL);
INSERT INTO `t_check_list` VALUES ('df0043ea-33d5-4bf7-9af8-24c5f66d65c3', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '应急预案编制', '生产经营单位应当制定本单位生产安全事故应急预案；生产经营单位应急预案分为综合应急预案、专项应急预案和现场处置方案；对于涉爆粉尘等危险性较大的场所、装置或者设施，生产经营单位应当编制现场处置方案', '查看应急救援预案文本、备案回执、评审或论证材料', '未按规定制定生产安全事故应急救援预案', '《安全生产法》第七十八条，《生产安全事故应急预案管理办法》（国家安全生产监督管理总局令第88号）第六条、第十五条', '《安全生产法》第九十四条第六项', '责令限期改正，可以处五万元以下的罚款', '2,5', '已按规定制定生产安全事故应急救援预案', 8, '应急管理', '应急救援预案文本 ', NULL);
