INSERT INTO `t_check_list` VALUES ('67179cbb-e070-4118-b8a0-eb8cf72c7f42', '0c3d9ebf-bac8-4a58-8d92-41f399788657', '供电电压', '架空线电力线路边导线至建筑物的最小距离，满足下列要求：线路电压35KV，最小距离3.0m;线路电压3～10KV，最小距离1.5m；线路电压3KV以下，最小距离1.0m。\n', '现场检查', '架空线电力线路边导线至建筑物的最小距离，不满足下列要求：线路电压35KV，最小距离3.0m；线路电压3～10KV，最小距离1.5m；线路电压3KV以下，最小距离1.0m。', 'GB50061-2010《66KV及以下架空电力线路设计规范》', 'GB50061-2010《66KV及以下架空电力线路设计规范》', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '架空线电力线路边导线至建筑物的最小距离，已满足下列要求：线路电压35KV，最小距离3.0m；线路电压3～10KV，最小距离1.5m；线路电压3KV以下，最小距离1.0m', 19, '供电', '', NULL);
