INSERT INTO `t_check_list` VALUES ('516fa26d-c3cc-434d-a9cc-1e1a0ad5c3db', '99536433-0f23-4938-95af-37eca4d154b2', '应急演练情况', '应当制定应急预案演练计划，每年至少组织一次综合或者专项应急预案演练，每半年至少组织一次现场处置方案演练', '查看企业的演练计划和2016、2017年度应急演练台账、记录', '未按照规定定期组织应急预案演练', '《生产安全事故应急预案管理办法》（总局88号令）第33条', '《生产安全事故应急预案管理办法》（总局88号令）第四十四条第二项', '责令限期改正，可以处五万元以下的罚款；逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2,5', '已按照规定定期组织应急预案演练', 20, '应急管理', '企业的应急演练记录、签到表、照片等复印件', NULL);
INSERT INTO `t_check_list` VALUES ('4e7709fa-74d0-45ee-9621-1a19fe39297b', '99536433-0f23-4938-95af-37eca4d154b2', '应急预案编制、评审、备案情况', '③应当按照隶属关系报所在地县级以上地方人民政府安全生产监督管理部门备案', '查看企业的应急救援预案文本、评审记录以及备案登记表', '未按照规定进行应急预案备案', '《生产安全事故应急预案管理办法》（总局88号令）第六条、第十二条、第二十一条、第二十六条，《生产经营单位生产安全事故应急预案编制导则》（GB/T29639-2013）', '《生产安全事故应急预案管理办法》（总局88号令）第四十五条第三项', '责令限期改正，可以处1万元以上4万元以下罚款', '2,5', '已按照规定进行应急预案备案', 19, '应急管理', '企业应急预案公布文件、备案登记表等复印件', NULL);
INSERT INTO `t_check_list` VALUES ('83ec8172-2159-4fd7-8f84-7620d42a0c90', '99536433-0f23-4938-95af-37eca4d154b2', '应急预案编制、评审、备案情况', '②应当对本单位编制的应急预案进行评审，并形成书面评审纪要', '查看企业的应急救援预案文本、评审记录以及备案登记表', '未按规定开展应急预案评审', '《生产安全事故应急预案管理办法》（总局88号令）第六条、第十二条、第二十一条、第二十六条，《生产经营单位生产安全事故应急预案编制导则》（GB/T29639-2013）', '《生产安全事故应急预案管理办法》（总局88号令）第四十五条第二项', '责令限期改正，可以处1万元以上3万元以下罚款', '2,5', '已按规定开展应急预案评审', 18, '应急管理', '企业应急预案书面评审记录、专家签字复印件、询问笔录等', NULL);
INSERT INTO `t_check_list` VALUES ('5246949c-45f3-400b-826b-cce64431f191', '99536433-0f23-4938-95af-37eca4d154b2', '应急预案编制、评审、备案情况', '①生产经营单位应当根据有关法律、法规和国家标准结合本单位的危险源状况、危险性分析情况和可能发生的事故特点（如火灾、爆炸、触电和毒物逸散等事故类型），制定相应的应急预案，分为综合应急预案、专项应急预案和现场处置方案', '查看企业的应急救援预案文本、评审记录以及备案登记表', '未按照规定制定生产安全事故应急救援预案', '《生产安全事故应急预案管理办法》（总局88号令）第六条、第十二条、第二十一条、第二十六条，《生产经营单位生产安全事故应急预案编制导则》（GB/T29639-2013）', '《安全生产法》第九十四条第六项;《生产安全事故应急预案管理办法》第四十四条第一项', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2,5', '已按照规定制定生产安全事故应急救援预案', 17, '应急管理', '企业应急预案文本、重要设备设施台账等资料的复印件', NULL);
INSERT INTO `t_check_list` VALUES ('539432f9-2980-48b5-9199-4396a4d6095e', '99536433-0f23-4938-95af-37eca4d154b2', '劳动防护用品配备情况', '必须为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用', '查制度、配备标准及采购、发放台账；现场检查实物和佩戴情况', '未为从业人员提供符合国家标准或者行业标准的劳动防护用品', '《安全生产法》第四十二条、《山东省劳动防护用品配备标准》（DB37/1922—2011）、《用人单位劳动防护用品管理规范》', '《安全生产法》第九十六条第四项', '责令限期改正，可以处五万元以下的罚款', '2,5', '已为从业人员提供符合国家标准或者行业标准的劳动防护用品', 16, '劳动防护用品管理', '劳保用品管理制度、配备标准、发放记录、标志、维护保养记录，现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('10aab126-4ba1-49ff-98a1-7153b9830cc8', '99536433-0f23-4938-95af-37eca4d154b2', '安全设备定期维护保养及检测情况', '金属冶炼企业必须对CO气体检测报警仪、可燃气体检测报警仪、空气呼吸器、压力表、安全阀等安全设备进行经常性维护、保养，并定期检测，保证正常运转维护、保养、检测应当作好记录，并由有关人员签字', '检查企业安全设备台账及维护保养记录、安全设备定期检测报告', '未对CO气体检测报警仪、可燃气体检测报警仪、空气呼吸器、压力表、安全阀等安全设备进行经常性维护、保养，并定期检测', '《安全生产法》第三十三条', '《安全生产法》第九十六条第三项', '责令限期改正，可以处五万元以下的罚款', '2,5', '已对CO气体检测报警仪、可燃气体检测报警仪、空气呼吸器、压力表、安全阀等安全设备进行经常性维护、保养，并定期检测', 15, '安全设备管理', '企业的安全设备台账、维护保养记录、检测报告、特种设备合格证等复印件', NULL);
INSERT INTO `t_check_list` VALUES ('59b6cff6-b4cd-4271-bf00-b2de37976146', '99536433-0f23-4938-95af-37eca4d154b2', '企业危险作业审批、实施情况', '②有限空间作业必须办理作业许可证，许可证中应体现作业现场通风、照明、警戒、防护、应急等措施落实情况，作业前对现场有毒有害气体检测情况；配足个体安全防护设备设施配备情况以及安排专人监护等情况③煤气作业环节必须实行严格的审批管理制度', '查看企业的危险作业票，作业票中检测、审批、安全措施确认等内容填写是否齐全', '动火、有限空间作业前未按规定进行检测以及安全措施确认', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第三十一条，《工贸企业有限公司作业安全管理与监督管理规定》（总局60号令）第八条、第九条、第十二条、第十三条', '《工贸企业有限公司作业安全管理与监督管理规定》（总局60号令）第三十条第二、三项，《生产经营单位安全培训规定》第三十条', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2,5', '动火、有限空间作业前已按规定进行检测以及安全措施确认', 14, '危险作业管理', '辨识记录、管理台账、作业票证、作业方案及审批记录、现场照片、监护人员培训记录等', NULL);
INSERT INTO `t_check_list` VALUES ('58ba318f-45c5-48a2-8348-ade768a70c0f', '99536433-0f23-4938-95af-37eca4d154b2', '企业危险作业审批、实施情况', '①动火作业必须按程序办理动火审批，取得作业许可证；许可证中要包括作业对象和环境的危害分析和可燃气体检测分析以及现场安全措施确认等内容', '查看企业的危险作业票，作业票中检测、审批、安全措施确认等内容填写是否齐全', '动火、有限空间等作业未按批准权限履行审批权限', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第三十一条，《工贸企业有限公司作业安全管理与监督管理规定》（总局59号令）第八条、第九条、第十二条、第十三条', '《工贸企业有限公司作业安全管理与监督管理规定》（总局59号令）第三十条第二、三项;《生产经营单位安全培训规定》第三十条', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2,5', '动火、有限空间等作业已按批准权限履行审批权限', 13, '危险作业管理', '辨识记录、管理台账、作业票证、作业方案及审批记录、现场照片、监护人员培训记录等', NULL);
INSERT INTO `t_check_list` VALUES ('67f78785-13f0-4204-b5ea-b73d9a9b5df4', '99536433-0f23-4938-95af-37eca4d154b2', '危险作业管理制度制定情况', '安全生产管理制度应当涵盖本单位的煤气、动火、有限空间、高处作业、抽堵盲板等危险作业管理，以及法律、法规、规章规定的其他内容', '查阅制度文本', '企业未制定动火、有限空间、高处作业、抽堵盲板等危险作业管理制度', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第七条', '《山东省生产经营单位安全生产主体责任规定》（省政府令第303号）第七条;《生产经营单位安全培训规定》第三十条', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2,5', '企业已制定动火、有限空间、高处作业、抽堵盲板等危险作业管理制度', 12, '危险作业管理', '辨识记录、管理台账、作业票证、作业方案及审批记录、现场照片、监护人员培训记录等', NULL);
INSERT INTO `t_check_list` VALUES ('f733cc04-9eac-4adc-8514-aad5bd85e085', '99536433-0f23-4938-95af-37eca4d154b2', '签订安全管理协议情况', '②企业应当与承包单位、承租单位签订专门的安全生产管理协议，或者在承包合同、租赁合同中约定各自的安全生产管理职责', '①查阅承包方资质等相关材料；②查阅安全管理协议或者承包合同，中是否有关于安全管理方面的规定', '未与承包单位、承租单位签订专门的安全生产管理协议，或者未在承包合同、租赁合同中明确各自的安全生产管理职责', '《安全生产法》第四十六条', '《安全生产法》第一百条', '责令限期改正，可以处五万元以下的罚款，对其直接负责的主管人员和其他直接责任人员可以处一万元以下的罚款', '2,5', '已与承包单位、承租单位签订专门的安全生产管理协议，或者已在承包合同、租赁合同中明确各自的安全生产管理职责', 11, '外包管理', '企业的出租、发包合同或协议、相关资质的复印件、询问笔录等', NULL);
INSERT INTO `t_check_list` VALUES ('a06b986f-ed5c-441d-8b50-563319c9ef3a', '99536433-0f23-4938-95af-37eca4d154b2', '签订安全管理协议情况', '①生产经营单位对生产经营项目、场所、设备发包或者出租时，应对承包单位、承租单位的安全生产条件或者资质进行审查', '①查阅承包方资质等相关材料；②查阅安全管理协议或者承包合同，中是否有关于安全管理方面的规定', '将生产经营项目、场所、设备发包或者出租给不具备安全生产条件或相应资质的单位或个人', '《安全生产法》第四十六条', '《安全生产法》第一百条', '责令限期改正，可以处五万元以下的罚款，对其直接负责的主管人员和其他直接责任人员可以处一万元以下的罚款', '2,5', '已将生产经营项目、场所、设备发包或者出租给具备安全生产条件或相应资质的单位或个人', 10, '外包管理', '企业的出租、发包合同或协议、相关资质的复印件、询问笔录等', NULL);
INSERT INTO `t_check_list` VALUES ('39a7073e-0442-4b27-ac8c-ba838cca21e3', '99536433-0f23-4938-95af-37eca4d154b2', '重大危险源登记建档情况', '生产经营单位对重大危险源应当登记建档，进行定期检测、评估、监控；评估报告的有效期为三年，重大危险源的安全设施和安全监测监控系统定期检测并在有效期内', '查看企业重大危险源相关档案资料、评估报告、相关检测报告', '对重大危险源未登记建档，或者未进行定期评估、监控', '《危险化学品重大危险源监督管理暂行规定》（总局40号令）第二十二条、第十一条、第十五条', '《安全生产法》第九十八条第二项', '责令限期改正，可以处十万元以下的罚款', '2,5', '对重大危险源已登记建档，或者已进行定期评估、监控', 8, '重大危险源', '现场照片，辨识记录、评价报告、应急预案、设备设施的设计文件等复印件、询问笔录等', NULL);
INSERT INTO `t_check_list` VALUES ('8a3ff2a3-6136-412c-8304-ffc8aecde775', '99536433-0f23-4938-95af-37eca4d154b2', '重大危险源备案情况', '①企业应将重大危险源安全评估报告或者评价报告、备案申请表、重大危险源档案材料报送所在地县级安监部门备案；②企业重大危险源评估报告满三年、装置设施新（改、扩）建、危险化学品种类、数量、储存方式及重要设备设施发生变化，应重新备案', '查看企业的重大危险源备案回执和评估报告的期限以及与现场相符性', '未按照规定进行重大危险源备案', '《危险化学品重大危险源监督管理暂行规定》（总局40号令）第二十三条', '《危险化学品重大危险源监督管理暂行规定》（总局40号令）第三十四条第四项', '给予警告，可以并处5000元以上3万元以下的罚款', '2,5', '已按照规定进行重大危险源备案', 9, '重大危险源', '现场照片、重大危险源评估报告、备案回执、询问笔录等', NULL);
INSERT INTO `t_check_list` VALUES ('30cba4c9-539d-4634-b8fc-2bd6fa0b2592', '99536433-0f23-4938-95af-37eca4d154b2', '从业人员安全生产教育和培训档案', '②新上岗的从业人员安全培训时间不少于72学时，每年再培训时间不少于20学时③调换工种和脱岗三个月以上重新上岗的人员，应事先进行岗位安全培训，并经考核合格方可上岗', '抽查4名高温熔融、煤气作业等重点岗位人员的安全培训档案，查记录是否齐全及培训范围、学时是否符合要求', '未按照规定对从业人员进行安全生产教育和培训', '《生产经营单位安全培训规定》（总局3号令）第二十二条、第十三条第二款，《炼铁安全规程》4.11', '《生产经营单位安全培训规定》（总局3号令）第三十条第二项', '责令其限期改正,可以处5万元以下的罚款', '2,5', '已按照规定对从业人员进行安全生产教育和培训', 7, '安全培训', '所抽查的员工培训档案、培训记录等资料复印件、询问笔录等', NULL);
