INSERT INTO `t_check_list` VALUES ('a5c4d791-e740-469f-b880-efbba3c52110', '99536433-0f23-4938-95af-37eca4d154b2', '特种作业人员培训档案和持证上岗情况', '②特种作业人员必须经专门的安全技术培训并考核合格，取得《中华人民共和国特种作业操作证》后，方可上岗作业③金属冶炼企业主要涉及电工、电焊工、煤气作业（包括煤气生产、储存、输送、使用、维护检修的作业）', '①检查企业特种作业人员管理档案；②现场抽查部分特种作业人员信息是否与台账一致', '特种作业人员未按规定经专门的安全作业培训并取得相应资格，上岗作业', '《特种作业人员安全技术培训考核管理规定》（总局30号令）第五条、第三十四条', '《安全生产法》第九十四条第七项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2,5', '特种作业人员已按规定经专门的安全作业培训并取得相应资格，上岗作业', 5, '安全培训', '特种作业人员现场照片、特种作业人员操作资格证书复印件、作业票据、询问笔录等', NULL);
