INSERT INTO `t_check_list` VALUES ('7e16f37a-07bf-4edc-85ee-bf0bec0cd6c2', '99536433-0f23-4938-95af-37eca4d154b2', '主要负责人和安全管理人员培训', '主要负责人和安全生产管理人员，自任职之日起6个月内，必须经安全生产监管监察部门对其安全生产知识和管理能力考核合格', '查看主要负责人和安全管理人员的安全合格证书', '主要负责人和安全生产管理人员未按规定经考核合格或安全合格证未按时复审', '《生产经营单位安全培训规定》（总局3号令）第二十四条', '《安全生产法》第九十四条第二项;《生产经营单位安全培训规定》第三十条', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2,5', '主要负责人和安全生产管理人员已按规定经考核合格或安全合格证已复审', 3, '安全培训', '主要负责人、安全管理人员任命文件及培训证书复印件等', NULL);
INSERT INTO `t_check_list` VALUES ('a0f0385a-435d-47c8-b770-2b2b3caeaa4e', '99536433-0f23-4938-95af-37eca4d154b2', '设置安全总监', '从业人员在300人以上的高危生产经营单位应当设置安全总监安全总监协助本单位主要负责人履行安全生产管理职责，专项分管本单位安全生产管理工作', '检查配备安全总监的文件', '未按规定设置安全总监', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第十二条', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第三十五条第四项', '责令限期改正，可处以5000元以上2万元以下的罚款，对其主要负责人处以1000元以上1万元以下的罚款', '2,5', '已按规定设置安全总监', 2, '组织机构', '从业人员花名册、任命文件复印件', NULL);
INSERT INTO `t_check_list` VALUES ('7e5f6bcf-8a92-452b-8fc4-7cff7f1ec3df', '99536433-0f23-4938-95af-37eca4d154b2', '按规定设置安全管理机构或配备安全管理人员情况', '配备原则：一不足100人的，配备专职安全生产管理人员；二100人以上不足300人的，设置安全生产管理机构，并配备2名以上专职安全生产管理人员，其中至少应当有1名注册安全工程师；三300人以上不足1000人的，设置专门的安全生产管理机构，并按不低于从业人员5‰但最低不少于3名的比例配备专职安全生产管理人员，其中至少应当有2名注册安全工程师；四1000人以上的，应当设置专门的安全生产管理机构，并按不低于从业人员5‰的比例配备专职安全生产管理人员，其中至少应当有3名注册安全工程师', '检查企业员工花名册、安全管理机构或配备安全管理人员的文件和配备注册安全工程师的文件', '未按规定设置安全管理机构或配备安全管理人员', '《安全生产法》第二十四条第三款；《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第九条', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2,5', '已按规定设置安全管理机构或配备安全管理人员', 1, '组织机构', '公司员工花名册、安全管理机构设置和安全管理人员任命文件、注安师的证书及聘用合同', NULL);
