INSERT INTO `t_check_list` VALUES ('cf03a4e8-96ae-42dd-b0d9-12525b475a4b', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '起重机械', '吊运重罐铁水、钢水、液渣，应确认挂钩挂牢，方可通知起重机司机起吊；起吊时，人员应站在安全位置，并尽量远离起吊地点。', '查看检查记录，现场验证', '吊运重罐铁水、钢水、液渣，未可靠确认挂钩情况，即进行起吊作业；起吊时，人员未站在远离起吊地点的安全位置。', '《炼钢安全规程》8.4.6', '《炼钢安全规程》8.4.6', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '吊运重罐铁水、钢水、液渣，已可靠确认挂钩情况，即进行起吊作业；起吊时，人员已站在远离起吊地点的安全位置。', 12, '高温熔融金属', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('580df615-bd2d-4057-91c2-7a1de6032191', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气柜', '①煤气柜应有容积指示装置，柜位达到上限时应关闭煤气入口阀，并设有放散设施，还应有煤气柜位降到下限时，自动停止向外输出煤气或自动充压的装置。', '现场检查验证', '①煤气柜未设容积指示装置以及柜容上下限安全联锁装置。', '《工业煤气安全规程》（GB6222-2005）9.1.2.3款、9.1.2.4款', '《工业煤气安全规程》（GB6222-2005）9.1.2.3条、9.1.2.4条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①煤气柜已设容积指示装置以及柜容上下限安全联锁装置。', 13, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('f6a7dfc0-3083-4439-be0a-7ec367fcbbd5', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气柜', '②煤气柜应设操作室，室内设有压力计、流量计、高、低位指示计，容积上、下声光讯信号装置和联系电话。', '现场检查验证', '②煤气柜操作室内未设有压力计、流量计、高、低位指示计，容积上、下声光讯信号装置和联系电话。', '《工业煤气安全规程》（GB6222-2005）9.1.2.3款、9.1.2.4款', '《工业煤气安全规程》（GB6222-2005）9.1.2.3条、9.1.2.4条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②煤气柜操作室内已设有压力计、流量计、高、低位指示计，容积上、下声光讯信号装置和联系电话。', 14, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('c35fcb3a-7302-4bad-823e-39769a38a956', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '发生炉煤气', '①中央控制室应设有调度电话和一般电话，并设有煤气发生炉进口饱和空气压力计、温度计、流量计、煤气发生炉出口煤气压力计、温度计、煤气高低压和空气低压报警装置、主要自动控制调节装置、连锁装置及灯光信号等。', '现场检查验证', '①中央控制室未设有煤气发生炉进口饱和空气压力计、温度计、流量计、煤气发生炉出口煤气压力计、温度计、煤气高低压和空气低压报警装置、主要自动控制调节装置、连锁装置及灯光信号等。', '《工业煤气安全规程》（GB6222-2005）5.1.2.3款、5.1.3.4款', '《工业煤气安全规程》（GB6222-2005）5.1.2.3条、5.1.3.4条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①中央控制室已设有煤气发生炉进口饱和空气压力计、温度计、流量计、煤气发生炉出口煤气压力计、温度计、煤气高低压和空气低压报警装置、主要自动控制调节装置、连锁装置及灯光信号等。', 15, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('48c0cc97-1f61-411b-81ba-b2455f9b8008', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '发生炉煤气', '②水套集汽包应设有安全阀、自动水位控制器，进水管应设止回阀，严禁在水夹套与集汽包连接管上加装阀门。', '现场检查验证', '②水套集汽包未设有安全阀，进水管未设止回阀，在水夹套与集汽包连接管上加装阀门。', '《工业煤气安全规程》（GB6222-2005）5.1.2.3款、5.1.3.4款', '《工业煤气安全规程》（GB6222-2005）5.1.2.3条、5.1.3.4条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②水套集汽包已设有安全阀，进水管未设止回阀，在水夹套与集汽包连接管上加装阀门。', 16, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('57faf185-1e2d-4125-94f9-18b75e5dec4a', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉煤气', '①高炉煤气电除尘应设有当高炉煤气含氧量达到1%时，能自动切断电源的装置；电除尘器应设有放散管、蒸汽管、泄爆装置。', '现场检查验证', '①高炉煤气电除尘未设有当高炉煤气含氧量达到1%时能自动切断电源的装置；电除尘器未设有放散管、蒸汽管、泄爆装置。', '《工业煤气安全规程》（GB6222-2005）5.3.2.4款、5.3.2.5款、5.3.2.6款', '《工业煤气安全规程》（GB6222-2005）5.3.2.4条、5.3.2.5款、5.3.2.6条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①高炉煤气电除尘已设有当高炉煤气含氧量达到1%时能自动切断电源的装置；电除尘器未设有放散管、蒸汽管、泄爆装置。', 17, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('a60a92d4-2361-4cc2-b182-c512c217dc32', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉煤气', '②布袋除尘器每个出入口应设有可靠的隔断装置；布袋除尘器应设有煤气高、低温报警和低压报警装置；布袋除尘器箱体应采用泄爆装置。', '现场检查验证', '②布袋除尘器每个出入口未设有可靠的隔断装置；布袋除尘器未设有煤气高、低温报警和低压报警装置；布袋除尘器箱体未采用泄爆装置。', '《工业煤气安全规程》（GB6222-2005）5.3.2.4款、5.3.2.5款、5.3.2.6款', '《工业煤气安全规程》（GB6222-2005）5.3.2.4条、5.3.2.5款、5.3.2.6条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②布袋除尘器每个出入口已设有可靠的隔断装置；布袋除尘器未设有煤气高、低温报警和低压报警装置；布袋除尘器箱体未采用泄爆装置。', 18, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('90cab6ef-14f0-4d57-98a2-48c73df3bfcf', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉煤气', '③余压透平进出口煤气管道上应设有可靠的隔断装置。', '现场检查验证', '③余压透平进出口煤气管道上未设有可靠的隔断装置。', '《工业煤气安全规程》（GB6222-2005）5.3.2.4款、5.3.2.5款、5.3.2.6款', '《工业煤气安全规程》（GB6222-2005）5.3.2.4条、5.3.2.5款、5.3.2.6条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '③余压透平进出口煤气管道上已设有可靠的隔断装置。', 19, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('5f76e947-e85b-45a5-93db-390b9069c942', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '转炉煤气', '①转炉煤气回收设施应设充氮装置及微氧量和一氧化碳含量的连续测定装置。当煤气含氧量超过2%或煤气柜位高度达到上限时应停止回收。', '现场检查验证', '①转炉煤气回收设施未设充氮装置及微氧量和一氧化碳含量的连续测定装置。', '《工业煤气安全规程》（GB6222-2005）5.6.2.2款、5.6.2.11款', '《工业煤气安全规程》（GB6222-2005）5.6.2.2条、5.6.2.11条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①转炉煤气回收设施已设充氮装置及微氧量和一氧化碳含量的连续测定装置。', 20, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('a26110a6-5e04-4efe-a904-3fc839362c85', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '转炉煤气', '②转炉煤气电除尘器入口、出口管道应设可靠的隔断装置；应设有当转炉煤气含氧量达到1%时，能自动切断电源的装置；电除尘器应设有放散管及泄爆装置。', '现场检查验证', '②转炉煤气电除尘器入口、出口管道未设可靠的隔断装置；未设有当转炉煤气含氧量达到1%时，能自动切断电源的装置；电除尘器未设有放散管及泄爆装置。', '《工业煤气安全规程》（GB6222-2005）5.6.2.2款、5.6.2.11款', '《工业煤气安全规程》（GB6222-2005）5.6.2.2条、5.6.2.11条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②转炉煤气电除尘器入口、出口管道已设可靠的隔断装置；已设有当转炉煤气含氧量达到1%时，能自动切断电源的装置；电除尘器已设有放散管及泄爆装置。', 21, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('6a7e529c-3645-4a86-8899-007183037f94', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气加压与混合', '站房内应设有一氧化碳监测装置，并把信号传送到管理室内。', '现场检查验证', '站房内未设有一氧化碳监测装置并把信号传送到管理室内。', '《工业煤气安全规程》（GB6222-2005）8.2.4款', '《工业煤气安全规程》（GB6222-2005）8.2.4条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '站房内已设有一氧化碳监测装置并把信号传送到管理室内。', 22, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('c75a7b4f-b80c-4d64-bccc-0edd8c6713bb', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气管道', '架空煤气管道应敷设在非燃烧体的支柱或栈桥上；不能穿过不使用煤气的建筑物及易燃易爆物品的堆场和仓库区；煤气管道下面，不应修建与煤气管道无关的建筑物和存放易燃、易爆物品。', '现场检查验证', '架空煤气管道未敷设在非燃烧体的支柱或栈桥上；煤气管道穿过不使用煤气的建筑物及易燃易爆物品的堆场和仓库区；煤气管道下面修建与煤气管道无关的建筑物和存放易燃、易爆物品。', '《工业煤气安全规程》（GB6222）6.2.1.2款以及《冶金企业安全生产标准化》（煤气）6.1规定', '《工业煤气安全规程》（GB6222-2005）6.2.1.2条以及《冶金企业安全生产标准化》（煤气）6.1规定', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '架空煤气管道已敷设在非燃烧体的支柱或栈桥上；煤气管道没穿过不使用煤气的建筑物及易燃易爆物品的堆场和仓库区；煤气管道下面修建与煤气管道已没有无关的建筑物和存放易燃、易爆物品。', 23, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('5f80e2e6-e5d1-4f88-8a1d-65ba060f46cc', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气水封（含排水器）', '水封装置（含排水器）必须能够检查水封高度和高水位溢流的排水口；严防水封装置的清扫孔（排污闸阀或旋塞）出现泄漏。', '现场检查验证', '水封装置（含排水器）不能够检查水封高度和高水位溢流的排水口；水封装置的清扫孔（排污闸阀或旋塞）出现泄漏。', '安监总管四〔2010〕125号《关于印发进一步加强冶金企业煤气安全技术管理有关规定的通知》第六款', '《安全生产法》第九十六条、安监总管四〔2010〕125号第六款', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '水封装置（含排水器）已能够检查水封高度和高水位溢流的排水口；水封装置的清扫孔（排污闸阀或旋塞）未出现泄漏。', 24, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('a9253926-5572-4da4-a994-4821075e7a79', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气隔断装置', '检修的煤气设施，包括煤气加压机、抽气机、鼓风机、布袋除尘器、煤气余压发电机组（TRT）、电捕焦油器、煤气柜、脱硫塔、洗苯塔、煤气加热器、煤气净化器等，煤气输入、输出管道必须采用可靠的隔断装置。', '现场检查验证', '检修的煤气设施，包括煤气加压机、抽气机、鼓风机、布袋除尘器、煤气余压发电机组（TRT）、电捕焦油器、煤气柜、脱硫塔、洗苯塔、煤气加热器、煤气净化器等，煤气输入、输出管道未采用可靠的隔断装置。', '安监总管四〔2010〕125号《关于印发进一步加强冶金企业煤气安全技术管理有关规定的通知》第七款', '《安全生产法》第九十六条、安监总管四〔2010〕125号第七款', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '检修的煤气设施，包括煤气加压机、抽气机、鼓风机、布袋除尘器、煤气余压发电机组（TRT）、电捕焦油器、煤气柜、脱硫塔、洗苯塔、煤气加热器、煤气净化器等，煤气输入、输出管道已采用可靠的隔断装置。', 25, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('eb67929b-7349-4b10-bfc5-637083cec64e', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气泄漏检测装置', '煤气危险区域，包括高炉风口及以上平台、转炉炉口以上平台、煤气柜活塞上部、烧结点火器及热风炉、加热炉、管式炉、燃气锅炉等燃烧器旁等易产生煤气泄漏的区域和焦炉地下室、加压站房、风机房等封闭或半封闭空间等，应设固定式一氧化碳监测声光报警装置，并把信号传送到管理室。', '现场检查验证', '煤气危险区域，包括高炉风口及以上平台、转炉炉口以上平台、煤气柜活塞上部、烧结点火器及热风炉、加热炉、管式炉、燃气锅炉等燃烧器旁等易产生煤气泄漏的区域和焦炉地下室、加压站房、风机房等封闭或半封闭空间等，未设固定式一氧化碳监测声光报警装置并把信号传送到管理室。', '安监总管四〔2010〕125号第二款以及《冶金企业安全生产标准化》（煤气）6.1规定', '《安全生产法》第九十六条、安监总管四〔2010〕125号第二款以及《冶金企业安全生产标准化》（煤气）6.1规定', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '煤气危险区域，包括高炉风口及以上平台、转炉炉口以上平台、煤气柜活塞上部、烧结点火器及热风炉、加热炉、管式炉、燃气锅炉等燃烧器旁等易产生煤气泄漏的区域和焦炉地下室、加压站房、风机房等封闭或半封闭空间等，已设固定式一氧化碳监测声光报警装置并把信号传送到管理室。', 26, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('4000d575-f86c-4440-a46e-66ff97574382', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气岗位人员值班室设置', '①煤气区域严禁设置人员密集的会议室、活动室、休息室、更衣室等。', '现场检查验证', '①煤气区域设置人员密集的会议室、活动室、休息室、更衣室等。', '《关于印发山东省冶金煤气安全生产重点措施的通知》鲁安监发[2011]141号', '《工业煤气安全规程》（GB6222-2005）5.3.1.4,12条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①煤气区域已不再设置人员密集的会议室、活动室、休息室、更衣室等。', 27, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('2ae505f5-f789-4603-9c5c-e839e8b3cf15', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气岗位人员值班室设置', '②煤气岗位值班室必须配置隔离式呼吸保护器具和安全检测报警仪器。', '现场检查验证', '②煤气岗位值班室未配置隔离式呼吸保护器具和安全检测报警仪器。', '《关于印发山东省冶金煤气安全生产重点措施的通知》鲁安监发[2011]141号', '《工业煤气安全规程》（GB6222-2005）5.3.1.4,12条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②煤气岗位值班室已配置隔离式呼吸保护器具和安全检测报警仪器。', 28, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('0c9b7dbe-964d-4c4b-82c3-b95b37eb6c58', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '煤气防护站器材配置', '煤气防护站应配备呼吸器、通风式防毒面具、充填装置、万能检查器、自动苏生器、隔离式自救器、担架、各种有毒气体分析仪、防爆测定仪及供危险作业和抢救用的其他设施（如对讲电话），并应配备救护车和作业车等，且应加强维护，使之经常处于完好状态。', '现场检查验证', '煤气防护站未配备呼吸器、通风式防毒面具、充填装置、万能检查器、自动苏生器、隔离式自救器、担架、各种有毒气体分析仪、防爆测定仪及供危险作业和抢救用的其他设施（如对讲电话），未配备救护车和作业车等，未处于完好状态。', '《工业煤气安全规程》（GB6222-2005）12.2.4款', '《工业煤气安全规程》（GB6222-2005）12.2.4条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '煤气防护站已配备呼吸器、通风式防毒面具、充填装置、万能检查器、自动苏生器、隔离式自救器、担架、各种有毒气体分析仪、防爆测定仪及供危险作业和抢救用的其他设施（如对讲电话），已配备救护车和作业车等，并处于完好状态。', 29, '煤气单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('9ed0e70a-76e4-4258-9a8f-f861033ad46e', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉本体', '炉基周围应保持清洁干燥，不应积水和堆积废料。', '现场验证', '炉基周围积水，堆积废料以及原材物料。', '《炼铁安全规程》9.1.3', '《炼铁安全规程》9.1.3', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '炉基周围无积水，堆积废料以及原材物料。', 30, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('a8e18ece-4a21-4232-955e-1498d64a03b2', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉本体', '炉基周围设置疏导防护装置，防止铁、渣与炉基周围高压水管接触。', '现场验证', '炉基周围未设置疏导防护装置。', '《关于印发山东省高炉液态渣铁安全生产重点防范措施的通知》第二条', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '炉基周围已设置疏导防护装置。', 31, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('d111714c-df6f-4835-bc3b-6fe9d45a02f2', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉本体', '炉体冷却系统应制定因冷却水压降低，高炉减风或休风后的具体操作规程。', '查看规程及应急预案，询问作业人员', '未制定炉体冷却系统因冷却水压降低，高炉减风或休风后的具体操作规程或应急预案。', '《炼铁安全规程》9.1.8', '《炼铁安全规程》9.1.8', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '已制定炉体冷却系统因冷却水压降低，高炉减风或休风后的具体操作规程或应急预案。', 32, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('327e84a3-b468-46fe-be6d-96e2b593a24b', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '高炉本体', '热电偶应对整个炉底进行自动、连续测温，其结果应正确显示于中控室（值班室）。', '查看中控室测温数据', '未对整个炉底进行自动、连续测温，或部分测温点损坏未及时修复。', '《炼铁安全规程》9.1.9', '《炼铁安全规程》9.1.9', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '已对整个炉底进行自动、连续测温，或部分测温点损坏未及时修复。', 33, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('e4be1237-0659-45da-ab43-1218ddf7e2ef', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '休息、浴室、更衣室设置', '不应设在风口平台和出铁场的下部，且应避开铁口、渣口。', '现场验证', '设在风口平台和出铁场的下部，未避开铁口、渣口。', '《炼铁安全规程》5.4', '《炼铁安全规程》5.4', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '设在风口平台和出铁场的下部，已避开铁口、渣口。', 34, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('15e770bb-51dc-4430-83e1-3748ad854992', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '操作室、值班室设置', '不应设在热风炉燃烧器、除尘器清灰口等可能泄漏煤气的危险区；不应在氧气、煤气管道上方设置值班室。', '现场验证', '设在热风炉燃烧器、除尘器清灰口等可能泄漏煤气的危险区；在氧气、煤气管道上方设置值班室。', '《炼铁安全规程》5.5', '《炼铁安全规程》5.5', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '未设在热风炉燃烧器、除尘器清灰口等可能泄漏煤气的危险区；在氧气、煤气管道上方未设置值班室。', 35, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('fbe26ba1-a512-401a-9a68-89733901fce7', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '泥炮和开口机操作室', '应能清楚地观察到泥炮的工作情况和铁口的状况，并应保证发生事故时操作人员能安全撤离。', '现场验证', '操作室位置设置不当或防护不完善，铁水能够喷溅或溢流到操作室。', '《炼铁安全规程》13.7', '《炼铁安全规程》13.7', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '操作室位置设置已改，铁水无法喷溅或溢流到操作室。', 36, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('83fd5a2b-e880-4ac7-923a-5e8008cd4027', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '炉前出铁场', '渣、铁沟应有供横跨用的活动小桥，出铁、出渣期间，人员不应跨越渣、铁沟，必要时应从横跨小桥通过。', '现场验证', '渣、铁沟未设置横跨用的活动小桥。出铁、出渣期间，人员跨越渣、铁沟', '《炼铁安全规程》13.3', '《炼铁安全规程》13.3', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '渣、铁沟已设置横跨用的活动小桥。出铁、出渣期间，无人员跨越渣、铁沟', 37, '炼铁单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('33d65eb6-b69e-4980-8107-cfd12c4f22ed', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '炉前出铁场', '加强对铁沟、渣沟的温度检测与维护，定期修补渣铁沟，防止渗漏铁、渣。必要时，放大闸检查主沟。', '查看记录及防范措施，现场验证', '未制定铁沟、渣沟的温度检测与维护计划，无检查记录，未定期修补渣铁沟', '《关于印发山东省高炉液态渣铁安全生产重点防范措施的通知》第二条', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '已制定铁沟、渣沟的温度检测与维护计划，已有检查记录，已定期修补渣铁沟', 38, '炼铁单元', '现场照片', NULL);
