INSERT INTO `t_check_list` VALUES ('0033bcb8-1d18-4cce-b326-53ae3f02a23d', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '入铝作业', '不应向抬包内加入带有水分、潮气和油垢的固体铝及其他物品，防止爆炸伤人。', '查看规程、检查记录，现场验证', '向抬包内加入带有水分、潮气和油垢的固体铝及其他物品。', '《铝电解安全生产规范》4.3.1.7', '《铝电解安全生产规范》4.3.1.7', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '未向抬包内加入带有水分、潮气和油垢的固体铝及其他物品。', 73, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('032e6f4b-b519-4d3c-8924-c6af4a2f7739', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '①铸造机及熔炼炉周围严禁存水或含水物质，发现有水必须立即清理干净，凡接触铝液的原材料、工器具、铸模、抬包及箱体等使用前必须进行干燥预热处理，确保无水后才能使用。', '查看安全评价报告，检查记录，现场验证', '①铸造机及熔炼炉周围存水或含水物质，接触铝液的原材料、工器具、铸模、抬包及箱体等使用前未全部进行干燥预热处理。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①铸造机及熔炼炉周围存水或含水物质，接触铝液的原材料、工器具、铸模、抬包及箱体等使用前已全部进行干燥预热处理。', 74, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('e8b8b92e-181b-4adb-b679-b6bc0711737d', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '②生产人员须穿劳保防护用品，引锭须戴面罩。', '查看安全评价报告，检查记录，现场验证', '②生产人员劳保防护用品穿戴不齐全。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②生产人员劳保防护用品穿戴已齐全。', 75, '电解铝冶炼单元', '劳保用品管理制度、配备标准、发放记录、标志、维护保养记录，现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('55edcb14-7d8e-4396-bfc4-3064eb1cdda6', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '③铸锭开始前检查冷却水能否进入结晶器内，若发现往里面反水，应及时调整，引锭头周围的石棉绳要塞紧。', '查看安全评价报告，检查记录，现场验证', '③铸锭开始前未检查冷却水情况。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '③铸锭开始前已检查冷却水情况。', 76, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('527ab8ad-7c80-4f35-9a37-538e0177c9a3', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '④发现铸锭缺陷堵流时必须堵死，并采用干燥的同牌号碎铝块填入铸锭中，确保铸锭在脱离结晶器前完全凝固。', '查看安全评价报告，检查记录，现场验证', '④铸锭缺陷堵流作业安全操作规程制定不完善，或执行不到位。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '④铸锭缺陷堵流作业安全操作规程制定完善。', 77, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('2129bff5-5255-4d6f-9375-c1d625a715c0', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '⑤铸造结束后停车不易过晚，停水时必须关严，在浇口部完全凝固后方可操作翻转架。', '查看安全评价报告，检查记录，现场验证', '⑤铸造结束后停车、停水、操作翻转架安全操作规程制定不完善，或执行不到位。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '⑤铸造结束后停车、停水、操作翻转架安全操作规程制定完善。', 78, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('7b872eef-01f6-4177-a00a-964fbbaa5139', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '⑥熔铸车间应制定冷却循环用水应急保障措施。', '查看安全评价报告，检查记录，现场验证', '⑥熔铸车间未制定冷却循环用水应急保障措施。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '⑥熔铸车间已制定冷却循环用水应急保障措施。', 79, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('904b4fbb-fbae-4627-8ec5-eb4314e820ee', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '⑦熔炼炉放铝口附近要配备必要的防泄漏、堵漏工器具或材料，如塞子、耐火毡帽、耐火泥、应急防护手套、面罩等。', '查看安全评价报告，检查记录，现场验证', '⑦熔炼炉放铝口附近未配备防泄漏、堵漏工器具或材料，如塞子、耐火毡帽、耐火泥、应急防护手套、面罩等。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '⑦熔炼炉放铝口附近已配备防泄漏、堵漏工器具或材料，如塞子、耐火毡帽、耐火泥、应急防护手套、面罩等。', 80, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('b0b334c2-0b42-47c5-b11d-9dc275f79466', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '⑧熔炼炉周围应设置防止铝液泄露漫延挡墙。', '查看安全评价报告，检查记录，现场验证', '⑧熔炼炉周围未设置防止铝液泄漏漫延挡墙。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '⑧熔炼炉周围已设置防止铝液泄漏漫延挡墙。', 81, '电解铝冶炼单元', '现场照片', NULL);
INSERT INTO `t_check_list` VALUES ('c0e93973-0258-4e19-9262-5dccf5328f5d', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '通用安全要求', '⑨爆炸事故发生时，应迅速撤离爆炸区人员至安全处，并立即对天然气、氯气进行关闭隔离，必要时通知关闭燃气总阀门。', '查看安全评价报告，检查记录，现场验证', '⑨爆炸事故应急措施不完善，未制定人员撤离、天然气、氯气关闭等内容。', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条《铝电解安全生产规范》4.1.1.2', '《山东省有色企业铝粉尘、铝液爆炸事故防范重点措施》第二条；《铝电解安全生产规范》4.1.1.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '⑨爆炸事故应急措施完善，已制定人员撤离、天然气、氯气关闭等内容。', 82, '电解铝冶炼单元', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('65ac960b-7147-4cc6-8472-d4be27db3da4', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '警示标志', '标志设置', '在有较大危险因素的生产经营场所和有关设施、设备上应设置明显的安全警示标志', '现场检查', '在有较大危险因素的生产经营场所和有关设施、设备上未设置明显安全警示标志', '《安全生产法》第三十二条《金属非金属矿山安全规程》（GB16423-2006）4.6、4.13', '《安全生产法》第九十六条第一项', '2,5', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 28, '28.0', '在有较大危险因素的生产经营场所和有关设施、设备上已设置明显安全警示标志', NULL);
INSERT INTO `t_check_list` VALUES ('9f007084-a303-4770-86a0-497103bc9c86', '5e22bc4c-bcb9-4385-b819-c5c3acd4ab22', '原料加工', '①原料粗碎、粉磨、混合、干燥、输送、包装等设备应设密闭罩或外部排风罩；易放散粉尘的加料点、卸料点及物料转运点应设置密闭罩或外部排风罩，并减少物料的落差高度；', '现场查看除尘设施运行情况，查阅职业病危害因素定期检测报告、防护设施台账及维护保养记录。重点对照本要点检查粉尘逸散的作业岗位是否按规定设置了粉尘防护设施，粉尘防护设施是否满足防尘要求，是否正常运行', '原料粗碎、粉磨、混合、干燥、输送、包装等设备未设密闭罩或外部排风罩；易放散粉尘的加料点、卸料点及物料转运点未设置密闭罩或外部排风罩，物料的落差高度过高', '《陶瓷生产防尘技术规程》（GB 13691-2008）7.1.2.2和7.1.2.1', '《陶瓷生产防尘技术规程》（GB 13691-2008）7.1.2.2和7.1.2.1', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '原料粗碎、粉磨、混合、干燥、输送、包装等设备应设密闭罩或外部排风罩；易放散粉尘的加料点、卸料点及物料转运点应设置密闭罩或外部排风罩，并减少物料的落差高度', 1, '陶瓷生产企业粉尘防护设施', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('e003acd9-074f-4133-9467-05c3fa8bfc45', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '通信联络系统', '通信线缆应分设两条，并从不同的井筒进入井下配线设备，其中任何一条通信线缆发生故障时，另外一条线缆容量应能担负井下通信终端的通信能力', '现场检查', '通信线缆未分设两条，并从不同的井筒进入井下配线设备，其中任何一条通信线缆发生故障时，另外一条线缆容量应能担负井下通信终端的通信能力', '《金属非金属地下矿山通信联络系统建设规范》（AQ2036-2011）4.5', '《金属非金属地下矿山通信联络系统建设规范》（AQ2036-2011）4.5', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 27, '27.0', '通信线缆已分设两条，并从不同的井筒进入井下配线设备，其中任何一条通信线缆发生故障时，另外一条线缆容量应能担负井下通信终端的通信能力', NULL);
INSERT INTO `t_check_list` VALUES ('ba357297-1645-474c-9a97-9e0dfbcc6675', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '通信联络系统', '通信联络系统应具有由控制中心发起的组呼、全呼、选呼、强拆、强插、紧呼及监听功能', '现场检查', '通信联络系统不具有由控制中心发起的组呼、全呼、选呼、强拆、强插、紧呼及监听功能', '《金属非金属地下矿山通信联络系统建设规范》（AQ2036-2011）4.3', '《金属非金属地下矿山通信联络系统建设规范》（AQ2036-2011）4.3', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 25, '25.0', '通信联络系统已具有由控制中心发起的组呼、全呼、选呼、强拆、强插、紧呼及监听功能', NULL);
INSERT INTO `t_check_list` VALUES ('7abcbbbe-dc81-4bdf-8861-aa1b4c2af290', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '通信联络系统', '井底车场、井下变电所、井下各中段采区、主要泵房、提升机房等地点应安装通信联络终端设备', '现场检查', '井底车场、井下变电所、井下各中段采区、主要泵房、提升机房等地点未安装通信联络终端设备', '《金属非金属地下矿山通信联络系统建设规范》（AQ2036-2011）4.4', '《金属非金属地下矿山通信联络系统建设规范》（AQ2036-2011）4.4', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 26, '26.0', '井底车场、井下变电所、井下各中段采区、主要泵房、提升机房等地点已安装通信联络终端设备', NULL);
INSERT INTO `t_check_list` VALUES ('41e3d5a5-1072-4576-a662-11cdae56659e', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '压风自救系统', '压风自救系统应进行设计，并按照设计要求进行建设', '查阅资料及现场检查', '压风自救系统未进行设计，未按照设计要求进行建设', '《金属非金属地下矿山压风自救系统建设规范》（AQ2034-2011）4.2', '《金属非金属地下矿山压风自救系统建设规范》（AQ2034-2011）4.2', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 23, '23.0', '压风自救系统已进行设计，已按照设计要求进行建设', NULL);
INSERT INTO `t_check_list` VALUES ('c5b9543e-cf6e-45da-830c-cb7d6b6cdf69', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '供水施救系统', '供水施救系统应进行设计，并按照设计要求进行建设', '查阅资料及现场检查', '供水施救系统未进行设计，未按照设计要求进行建设', '《金属非金属地下矿山供水施救系统建设规范》（AQ2035-2011）4.2', '《金属非金属地下矿山供水施救系统建设规范》（AQ2035-2011）4.2', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 24, '24.0', '供水施救系统已进行设计，已按照设计要求进行建设', NULL);
INSERT INTO `t_check_list` VALUES ('8492e758-85eb-4cac-9087-a7743b5b94c7', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '紧急避险系统', '应为入井人员配备额定防护时间不少于30min的自救器，并按入井总人数的10%配备备用自救器。生产中段在地面最低安全出口以下垂直距离超过300米的矿山，应在最低生产中段设置紧急避险设施', '查阅记录及现场检查', '未为入井人员配备额定防护时间不少于30min的自救器，未按入井总人数的10%配备备用自救器。生产中段在地面最低安全出口以下垂直距离超过300米的矿山，未在最低生产中段设置紧急避险设施', '《金属非金属地下矿山紧急避险系统建设规范》（AQ2033-2011）4.4，5.3', '《金属非金属地下矿山紧急避险系统建设规范》（AQ2033-2011）4.4,5.3', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 22, '22.0', '已为入井人员配备额定防护时间不少于30min的自救器，已按入井总人数的10%配备备用自救器。生产中段在地面最低安全出口以下垂直距离超过300米的矿山，已在最低生产中段设置紧急避险设施', NULL);
INSERT INTO `t_check_list` VALUES ('c05b4445-e30e-4a1a-ac07-0be9c5a9f157', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '人员定位系统', '井下最多同时作业人数不少于30人的金属非金属地下矿山应建立完善人员定位系统；井下最多同时作业人数少于30人的金属非金属地下矿山应建立完善人员出入井信息管理制度，准确掌握井下各个区域作业人员的数量。人员定位系统应取得矿用产品安全标志', '现场检查', '井下最多同时作业人数不少于30人的金属非金属地下矿山未建立完善人员定位系统；井下最多同时作业人数少于30人的金属非金属地下矿山未建立完善人员出入井信息管理制度，准确掌握井下各个区域作业人员的数量。人员定位系统未取得矿用产品安全标志', '《金属非金属地下矿山人员定位系统建设规范》（AQ2032-2011）4.1,4.14', '《金属非金属地下矿山人员定位系统建设规范》（AQ2032-2011）4.1,4.14', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 21, '21.0', '井下最多同时作业人数不少于30人的金属非金属地下矿山已建立完善人员定位系统；井下最多同时作业人数少于30人的金属非金属地下矿山已建立完善人员出入井信息管理制度，准确掌握井下各个区域作业人员的数量。人员定位系统已取得矿用产品安全标志', NULL);
INSERT INTO `t_check_list` VALUES ('dcbf68a6-8f0c-4e0c-bfa7-c4b6233979f8', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '“六大系统”', '监测监控系统', '井下总回风巷、各个生产中段和分段的回风巷应设置风速传感器。提升人员的井口信号房、提升机房，以及井口、马头门等人员进出场所应设置视频监控。紧急避险设施及井下爆破器材库、油库、中央变电所等主要硐室，应设视频监控。存在大面积空气、工程地质复杂、有严重地压活动的矿山应设地压监控', '现场检查', '井下总回风巷、各个生产中段和分段的回风巷未设置风速传感器。提升人员的井口信号房、提升机房，以及井口、马头门等人员进出场所未设置视频监控。紧急避险设施及井下爆破器材库、油库、中央变电所等主要硐室，未设视频监控。存在大面积空气、工程地质复杂、有严重地压活动的矿山未设地压监控', '《金属非金属地下矿山监测监控系统建设规范》（AQ2031-2011）6.1,7.1,8.2', '《金属非金属地下矿山监测监控系统建设规范》（AQ2031-2011）6.1,7.1,8.2', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 20, '20.0', '井下总回风巷、各个生产中段和分段的回风巷已设置风速传感器。提升人员的井口信号房、提升机房，以及井口、马头门等人员进出场所已设置视频监控。紧急避险设施及井下爆破器材库、油库、中央变电所等主要硐室，已设视频监控。存在大面积空气、工程地质复杂、有严重地压活动的矿山已设地压监控', NULL);
INSERT INTO `t_check_list` VALUES ('112f0584-311f-460b-bcf1-ae4c38155aec', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '排水', '水泵房', '井底主要泵房的出口应不少于两个，一个用斜巷与井筒连通，斜巷上口应高出泵房地面标高7m以上；其他通往井底车场，其出口要装设防水门，水文地质条件复杂的矿井，应在关键巷道内设置防水门', '现场检查', '井底主要泵房的出口少于两个，一个用斜巷与井筒连通，斜巷上口未高出泵房地面标高7m以上；其他通往井底车场，其出口未装设防水门，水文地质条件复杂的矿井，未在关键巷道内设置防水门', '《金属非金属矿山安全规程》（GB16423-2006）6.6.3.36.6.4.2', '《金属非金属矿山安全规程》（GB16423-2006）6.6.3.3,6.6.4.2', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 19, '19.0', '井底主要泵房的出口不少于两个，一个用斜巷与井筒连通，斜巷上口已高出泵房地面标高7m以上；其他通往井底车场，其出口已装设防水门，水文地质条件复杂的矿井，已在关键巷道内设置防水门', NULL);
INSERT INTO `t_check_list` VALUES ('acd39673-4802-40b8-839b-5558cf1089ef', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '排水', '排水设施', '井下主要排水设备，至少应由同类型的三台泵组成，一台工作，一台备用，一台检修；工作水泵应能在20h内排出一昼夜的正常涌水量，水仓的容积应满足设计要求；井筒内应装设相同的排水管，其中一条工作，一条备用', '现场检查', '井下主要排水设备，不是由同类型的三台泵组成，一台工作，一台备用，一台检修；工作水泵不能在20h内排出一昼夜的正常涌水量，水仓的容积不满足设计要求；井筒内未装设相同的排水管，其中一条工作，一条备用', '《金属非金属矿山安全规程》（GB16423-2006）6.6.4.1', '《金属非金属矿山安全规程》（GB16423-2006）6.6.4.1', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 18, '18.0', '井下主要排水设备，是由同类型的三台泵组成，一台工作，一台备用，一台检修；工作水泵能在20h内排出一昼夜的正常涌水量，水仓的容积已满足设计要求；井筒内已装设相同的排水管，其中一条工作，一条备用', NULL);
INSERT INTO `t_check_list` VALUES ('d5b406ca-60f0-40d5-a310-42c1d3d43d93', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '电气设施', '保护接地', '所有需要接地的设备和局部接地极，均与接地干线连接；接地干线应与主接地极连接。各中段的接地干线均应与主接地极连接', '现场检查', '所有需要接地的设备和局部接地极，均未与接地干线连接；接地干线未与主接地极连接。各中段的接地干线均未与主接地极连接', '《金属非金属矿山安全规程》（GB16423-2006）6.5.6.3', '《金属非金属矿山安全规程》（GB16423-2006）6.5.6.3', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 17, '17.0', '所有需要接地的设备和局部接地极，均已与接地干线连接；接地干线已与主接地极连接。各中段的接地干线均已与主接地极连接', NULL);
INSERT INTO `t_check_list` VALUES ('28b405ff-75ac-48f8-a5f8-246b2de4c1e2', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '电气设施', '电气保护', '从井下中央变电所或采区配电所引出的低压馈出线，应装设带有过电流保护的断路器', '现场检查', '从井下中央变电所或采区配电所引出的低压馈出线，未装设带有过电流保护的断路器', '（GB16423-2006）6.5.3.2', '《金属非金属矿山安全规程》（GB16423-2006）6.5.3.2', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 14, '14.0', '从井下中央变电所或采区配电所引出的低压馈出线，已装设带有过电流保护的断路器', NULL);
INSERT INTO `t_check_list` VALUES ('ba63c939-ed35-4864-b1fa-dc6efbae5c8b', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '电气设施', '电气保护', '井下变配电所高压馈出线，应装设单相接地保护装置；低压馈出线，应装设漏电保护装置；有爆炸危险的矿井，保护装置应能实现有选择性的切断故障线路或能实现漏电检测', '现场检查', '井下变配电所高压馈出线，未装设单相接地保护装置；低压馈出线，未装设漏电保护装置；有爆炸危险的矿井，保护装置未能实现有选择性的切断故障线路或能实现漏电检测', '《金属非金属矿山安全规程》（GB16423-2006）6.5.3.3', '《金属非金属矿山安全规程》（GB16423-2006）6.5.3.3', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 15, '15.0', '井下变配电所高压馈出线，已装设单相接地保护装置；低压馈出线，已装设漏电保护装置；有爆炸危险的矿井，保护装置已能实现有选择性的切断故障线路或能实现漏电检测', NULL);
INSERT INTO `t_check_list` VALUES ('a45fa1c9-2255-4766-b691-825a8786389f', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '电气设施', '保护接地', '井下所有电气设备的金属外壳、电缆的配件、金属外皮、轨道中接近电缆线路的金属构筑物等都应接地。主接地极应设在井下水仓或积水坑内，且应不少于两组', '现场检查', '井下所有电气设备的金属外壳、电缆的配件、金属外皮、轨道中接近电缆线路的金属构筑物等都未接地。主接地极未设在井下水仓或积水坑内，且少于两组', '《金属非金属矿山安全规程》（GB16423-2006）6.5.6.1，6.5.6.5', '《金属非金属矿山安全规程》（GB16423-2006）6.5.6.1,6.5.6.5', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 16, '16.0', '井下所有电气设备的金属外壳、电缆的配件、金属外皮、轨道中接近电缆线路的金属构筑物等都已接地。主接地极已设在井下水仓或积水坑内，且不少于两组', NULL);
INSERT INTO `t_check_list` VALUES ('21202034-bdc3-42b2-a8b3-38d136ccda1d', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '电气设施', '供电', '井下电气设备不应接零。井下应采用矿用变压器，若用普通变压器，其中性点不应直接接地，变压器二次侧的中性点不应引出载流中性线(N线)。地面中性点直接接地的变压器或发电机，不应用于向井下供电', '现场检查', '井下电气设备接零。井下未采用矿用变压器，用普通变压器，其中性点直接接地，变压器二次侧的中性点引出载流中性线(N线)。地面中性点直接接地的变压器或发电机，用于向井下供电', '《金属非金属矿山安全规程》（GB16423-2006）6.5.1.4', '《金属非金属矿山安全规程》（GB16423-2006）6.5.1.4', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 13, '13.0', '井下电气设备没有接零。井下已采用矿用变压器，用普通变压器，其中性点没有直接接地，变压器二次侧的中性点没有引出载流中性线(N线)。地面中性点直接接地的变压器或发电机，没有用于向井下供电', NULL);
INSERT INTO `t_check_list` VALUES ('b1ffc0a4-7257-4113-80e3-011cf3260c61', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '通风', '主扇', '每年至少进行一次反风试验，并测定主要风路反风后的风量。独头工作面有人工作时，局扇应连续运转；采场形成通风系统之前，不应进行回采作业', '现场检查', '每年未进行过反风试验，未测定主要风路反风后的风量。独头工作面有人工作时，局扇未连续运转；采场形成通风系统之前，进行回采作业', '《金属非金属矿山安全规程》（GB16423-2006）6.4.2.3,6.4.3.1,6.4.3.3,6.4.4.3', '《金属非金属矿山安全规程》（GB16423-2006）6.4.2.3，6.4.3.1,6.4.3.3，6.4.4.3', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 11, '11.0', '每年已进行过反风试验，已测定主要风路反风后的风量。独头工作面有人工作时，局扇已连续运转；采场形成通风系统之前，不进行回采作业', NULL);
INSERT INTO `t_check_list` VALUES ('371f4c20-7305-4b09-8063-aa9562bebe5a', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '电气设施', '供电', '由地面到井下中央变电所或主排水泵房的电源电缆，至少应敷设两条独立的线路，应引自地面主变电所的不同母线段。下井主动力电缆应选用阻燃电缆，动力电缆、变压器不应超负荷运行，高压电气设备设施应定期进行预防性试验', '现场检查', '由地面到井下中央变电所或主排水泵房的电源电缆，未敷设两条独立线路，未引自地面主变电所的不同母线段。下井主动力电缆未选用阻燃电缆，动力电缆、变压器超负荷运行，高压电气设备设施未定期进行预防性试验', '《金属非金属矿山安全规程》（GB16423-2006）6.5.1.3', '《金属非金属矿山安全规程》（GB16423-2006）6.5.1.3', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 12, '12.0', '由地面到井下中央变电所或主排水泵房的电源电缆，已敷设两条独立线路，已引自地面主变电所的不同母线段。下井主动力电缆已选用阻燃电缆，动力电缆、变压器超负荷运行，高压电气设备设施已定期进行预防性试验', NULL);
INSERT INTO `t_check_list` VALUES ('f1945491-b66e-479a-a151-484e7dc2e019', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '通风', '主扇', '矿井应建立机械通风系统，矿井通风系统的有效风量率，应不低于60%；井下炸药库应有独立的回风巷，主要回风巷不应作人行道，矿井主通风机应有反风装置，每台主扇应具有相同型号和规格的备用电动机', '现场检查', '矿井未建立机械通风系统，矿井通风系统的有效风量率，低于60%；井下炸药库没有独立的回风巷，主要回风巷作人行道，矿井主通风机没有反风装置，每台主扇不具有相同型号和规格的备用电动机', '《金属非金属矿山安全规程》（GB16423）6.4.2.1，6.4.2.2，6.4.2.5，6.4.2.6', '《金属非金属矿山安全规程》（GB16423-2006）6.4.2.1,6.4.2.2,6.4.2.5,6.4.2.6', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 10, '10.0', '矿井已建立机械通风系统，矿井通风系统的有效风量率，不低于60%；井下炸药库已设有独立的回风巷，主要回风巷作人行道，矿井主通风机已有反风装置，每台主扇已具有相同型号和规格的备用电动机', NULL);
INSERT INTO `t_check_list` VALUES ('d6df739b-75b4-4fe6-84f7-ce4fafd033b3', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '运输', '无轨运输', '电机车运输的滑触线应设分段开关，分段距离应不超过500m。每一条支线也应设分段开关。上下班时间，距井筒51m以内的滑触线应切断电源。电机车应不得超负荷运行，电机车架线的高度应满足要求，有轨及无轨运输的巷道人行安全距离应满足规程要求，无轨运输设备应使用矿用安标合格产品，无轨设备安全制动性能应可靠', '现场检查', '电机车运输的滑触线未设分段开关，分段距离超过500m。每一条支线未设分段开关。上下班时间，距井筒50m以内的滑触线未切断电源。电机车超负荷运行，电机车架线的高度不满足要求，有轨及无轨运输的巷道人行安全距离不满足规程要求，无轨运输设备未使用矿用安标合格产品，无轨设备安全制动性能不可靠', '《金属非金属矿山安全规程》（GB16423-2006）6.3.1.16', '《金属非金属矿山安全规程》（GB16423-2006）6.3.1.15', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 9, '9.0', '电机车运输的滑触线已设分段开关，分段距离不超过500m。每一条支线已设分段开关。上下班时间，距井筒50m以内的滑触线已切断电源。电机车未超负荷运行，电机车架线的高度已满足要求，有轨及无轨运输的巷道人行安全距离已满足规程要求，无轨运输设备已使用矿用安标合格产品，无轨设备安全制动性能可靠', NULL);
INSERT INTO `t_check_list` VALUES ('28e0f7c0-3dfe-46e7-b4e9-6b1276573bda', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '运输', '有轨运输', '电机车运输的滑触线应设分段开关，分段距离应不超过500m。每一条支线也应设分段开关。上下班时间，距井筒50m以内的滑触线应切断电源。电机车应不得超负荷运行，电机车架线的高度应满足要求，有轨及无轨运输的巷道人行安全距离应满足规程要求，无轨运输设备应使用矿用安标合格产品，无轨设备安全制动性能应可靠', '现场检查', '电机车运输的滑触线未设分段开关，分段距离超过500m。每一条支线未设分段开关。上下班时间，距井筒50m以内的滑触线未切断电源。电机车超负荷运行，电机车架线的高度不满足要求，有轨及无轨运输的巷道人行安全距离不满足规程要求，无轨运输设备未使用矿用安标合格产品，无轨设备安全制动性能不可靠', '《金属非金属矿山安全规程》（GB16423-2006）6.3.1.15', '《金属非金属矿山安全规程》（GB16423-2006）6.3.1.15', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 8, '8.0', '电机车运输的滑触线已设分段开关，分段距离未超过500m。每一条支线已设分段开关。上下班时间，距井筒50m以内的滑触线已切断电源。电机车未超负荷运行，电机车架线的高度已满足要求，有轨及无轨运输的巷道人行安全距离已满足规程要求，无轨运输设备已使用矿用安标合格产品，无轨设备安全制动性能可靠', NULL);
INSERT INTO `t_check_list` VALUES ('609c0fa6-7995-4619-8c2b-457a73cd5f93', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '提升', '提升装置机电控制与闭锁', '提升系统有关安全设施应满足批复的安全设施设计要求。提升装置的机电控制系统应设置以下保护与电气闭锁装置：限速保护装置、主传动电机短路及断电保护装置、松绳保护装置、过卷保护装置、过速保护装置、过负荷和欠电压保护装置、闸瓦磨损保护装置、提升机与信号系统之间的闭锁装置、直流提升主电动机励磁失流保护装置。安全门与提升信号之间的闭锁，提升机、提升容器、防坠器、提升钢丝绳等应选用矿用安标合格产品，提升钢丝绳安全系数应满足规程要求，混合井提升中间隔离板应选用阻燃材料，升降人员的提升机钢丝绳缠绕层数应满足规程要求', '现场检查', '提升系统有关安全设施不满足批复的安全设施设计要求。提升装置机电控制系统未设置以下保护与电气闭锁装置：限速保护装置、主传动电机短路及断电保护装置、松绳保护装置、过卷保护装置、过速保护装置、过负荷和欠电压保护装置、闸瓦磨损保护装置、提升机与信号系统之间的闭锁装置、直流提升主电动机励磁失流保护装置。安全门与提升信号之间闭锁，提升机、提升容器、防坠器、提升钢丝绳等未选用矿用安标合格产品，钢丝绳安全系数不满足规程要求，混合井提升中间隔离板未选用阻燃材料，升降人员的提升机钢丝绳缠绕层数不满足规程要求', '《金属非金属矿山安全规程》（GB16423-2006）4.7,6.3.5.10', '《金属非金属矿山安全规程》（GB16423-2006）4.7、6.3.5.10', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 7, '7.0', '提升系统有关安全设施已满足批复的安全设施设计要求。提升装置机电控制系统已设置以下保护与电气闭锁装置：限速保护装置、主传动电机短路及断电保护装置、松绳保护装置、过卷保护装置、过速保护装置、过负荷和欠电压保护装置、闸瓦磨损保护装置、提升机与信号系统之间的闭锁装置、直流提升主电动机励磁失流保护装置。安全门与提升信号之间闭锁，提升机、提升容器、防坠器、提升钢丝绳等已选用矿用安标合格产品，钢丝绳安全系数已满足规程要求，混合井提升中间隔离板已选用阻燃材料，升降人员的提升机钢丝绳缠绕层数已满足规程要求', NULL);
INSERT INTO `t_check_list` VALUES ('7bcc880e-bfd3-4053-8579-8f08e69666b5', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '设备及工艺', '淘汰设备', '禁止使用国家安全监管总局发布的《金属非金属矿山禁止使用的设备及工艺目录》中的设备及工艺。（第一批、第二批）', '现场检查', '使用国家安全监管总局发布的《金属非金属矿山禁止使用的设备及工艺目录》中的设备及工艺', '国家安全监管总局《金属非金属矿山禁止使用的设备及工艺目录》《安全生产法》第三十五条', '《安全生产法》第九十六条第六项', '2,5', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 5, '5.0', '未使用国家安全监管总局发布的《金属非金属矿山禁止使用的设备及工艺目录》中的设备及工艺', NULL);
INSERT INTO `t_check_list` VALUES ('81bdf3cf-8604-4c0e-a4d0-b95161e9e56f', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '提升', '斜井提升保护装置', '提升矿车斜井应设常闭式防跑车装置，应保持完好；斜井上部和中间车场，应设阻车器或挡车栏，矿车通过时打开，矿车通过后关闭；斜井下部车场应设躲避硐室。斜井升降人员的信号装置应满足规程要求', '现场检查', '提升矿车斜井未设常闭式防跑车装置，未保持完好；斜井上部和中间车场，未设阻车器或挡车栏，矿车通过时打开，矿车通过后关闭；斜井下部车场未设躲避硐室。斜井升降人员的信号装置不满足规程要求', '《金属非金属矿山安全规程》（GB16423-2006）6.3.2.3,6.3.2.6', '《金属非金属矿山安全规程》（GB16423-2006）6.3.2.3,6.3.2.6', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 6, '6.0', '提升矿车斜井已设常闭式防跑车装置，已保持完好；斜井上部和中间车场，已设阻车器或挡车栏，矿车通过时打开，矿车通过后关闭；斜井下部车场已设躲避硐室。斜井升降人员的信号装置已满足规程要求', NULL);
INSERT INTO `t_check_list` VALUES ('dd6ec530-aeec-47f1-80ea-fa409340045d', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '采矿', '矿柱与空区', '应严格保持矿柱（含顶柱、底柱和间柱等）的尺寸，形状和直立度，应有专人检查和管理，以保证在利用期间的稳定性；采用留矿法、空场法采矿的矿山，应采取充填、隔离或强制崩落围岩的措施，及时处理采空区', '对照设计与作业规程查看现场', '未严格保持矿柱（含顶柱、底柱和间柱等）的尺寸，形状和直立度，没有专人检查和管理，以保证在利用期间的稳定性；采用留矿法、空场法采矿的矿山，未采取充填、隔离或强制崩落围岩的措施，及时处理采空区', '《金属非金属矿山安全规程》（GB16423-2006）6.2.1.4,6.2.1.10', '《金属非金属矿山安全规程》（GB16423-2006）6.2.1.4,6.2.1.10', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 4, '4.0', '已严格保持矿柱（含顶柱、底柱和间柱等）的尺寸，形状和直立度，已有专人检查和管理，以保证在利用期间的稳定性；采用留矿法、空场法采矿的矿山，已采取充填、隔离或强制崩落围岩的措施，及时处理采空区', NULL);
INSERT INTO `t_check_list` VALUES ('55561999-2659-4507-892e-232b5a1469c9', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '采矿', '采矿工艺', '每个生产矿井至少应有两个独立的直达地表的安全出口，安全出口的间距应不小于30米；每个生产水平（中段），均应至少有两个便于行人的安全出口，并应同通往地表的安全出口相通，每个采区（盘区、矿块）、均应有便于两个行人的安全出口；地下开采，应按设计要求进行', '对照设计与作业规程查看现场', '每个生产矿井没有两个独立的直达地表的安全出口，安全出口的间距小于30米；每个生产水平（中段），没有两个便于行人的安全出口，未同通往地表的安全出口相通，每个采区（盘区、矿块）、没有便于两个行人的安全出口；地下开采，未按设计要求进行', '《金属非金属矿山安全规程》（GB16423-2006）6.1.1.3,6.2.1.1 ', '《金属非金属矿山安全规程》（GB16423-2006）6.1.1.3,6.2.1.1', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 3, '3.0', '每个生产矿井已有两个独立的直达地表的安全出口，安全出口的间距不小于30米；每个生产水平（中段），已有两个便于行人的安全出口，已同通往地表的安全出口相通，每个采区（盘区、矿块）、已有便于两个行人的安全出口；地下开采，已按设计要求进行', NULL);
INSERT INTO `t_check_list` VALUES ('c63f3841-a6fb-418d-87c9-ba36b2aaf21e', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '技术资料', '现状图纸', '矿山应保存完整以下图纸，并根据实际情况的变化及时更新：矿区地形地质和水文地质图；井上、下对照图；中段平面图；通风系统图；提升运输系统图；风、水管网系统图；充填系统图；井下通讯系统图；井上、下配电系统图；井下电气设备布置图；井下避灾路线图；安全避险“六大系统”布置图等', '按图纸对照现场进行抽查', '矿山未保存完整以下图纸，未根据实际情况的变化即时更新：矿区地形地质和水文地质图；井上、下对照图；中段平面图；通风系统图；提升运输系统图；风、水管网系统图；充填系统图；井下通讯系统图；井上、下配电系统图；井下电气设备布置图；井下避灾路线图；安全避险“六大系统”布置图等', '《金属非金属矿山安全规程》（GB16423-2006）4.16', '《金属非金属矿山安全规程》（GB16423-2006）4.16', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 1, '1.0', '矿山已保存完整以下图纸，已根据实际情况的变化即时更新：矿区地形地质和水文地质图；井上、下对照图；中段平面图；通风系统图；提升运输系统图；风、水管网系统图；充填系统图；井下通讯系统图；井上、下配电系统图；井下电气设备布置图；井下避灾路线图；安全避险“六大系统”布置图等', NULL);
INSERT INTO `t_check_list` VALUES ('b75cf7f9-0491-49e3-b029-5e63ce1db114', 'cd6c3e3f-ae88-46d9-926c-8dfbf2fe0870', '矿山井巷', '井巷支护', '需要支护的井巷，支护方法、支护与工作面间的距离，应在施工设计中确定；对所有支护的井巷应定期检查，井下安全出口和升降人员的井筒，每月应检查一次；报废的井巷和硐室的入口，应及时封闭', '现场检查', '需要支护的井巷，支护方法、支护与工作面间的距离，未在施工设计中确定；对所有支护的井巷未定期检查，井下安全出口和升降人员的井筒，每月未检查一次；报废的井巷和硐室的入口，未及时封闭', '《金属非金属矿山安全规程》（GB16423-2006）6.1.5.2,6.1.6.1,6.1.6.5', '《金属非金属矿山安全规程》（GB16423-2006）6.1.5.2,6.1.6.1,6.1.6.5', '2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', 2, '2.0', '需要支护的井巷，支护方法、支护与工作面间的距离，已在施工设计中确定；对所有支护的井巷已定期检查，井下安全出口和升降人员的井筒，每月已检查一次；报废的井巷和硐室的入口，已及时封闭', NULL);
INSERT INTO `t_check_list` VALUES ('7c686a12-ebad-4117-815f-90e8d4bfa483', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '工艺技术及工艺装置的安全控制', '①装置可能引起火灾、爆炸等严重事故的部位是否设置超温、超压等检测仪表、声和/或光报警、泄压设施和安全联锁装置等设施；', '现场检查、查阅设计资料、记录、控制系统相关设置', '装置可能引起火灾、爆炸等严重事故的部位未设置超温、超压等检测仪表、声和/或光报警、泄压设施和安全联锁装置等设施', '《危险化学品企业事故隐患排查治理实施导则》安监总管三[2012]103号文第4.3.2条', '国家安监总局41号令（修订）第九条第三款 《蒸馏系统安全控制指导意见》鲁安监发[2011]140号 《石油化工企业设计防火规范》GB50160-2008第5.5条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '装置可能引起火灾、爆炸等严重事故的部位已设置超温、超压等检测仪表、声光报警、泄压设施和安全联锁装置等设施', 1, '工艺安全', '', NULL);
INSERT INTO `t_check_list` VALUES ('176898a4-79f2-4dca-8af2-0a99aa157836', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '工艺技术及工艺装置的安全控制', '②针对温度、压力、流量、液位等工艺参数设计的安全泄压系统以及安全泄压措施的完好性；', '现场检查、查阅设计资料、记录、控制系统相关设置', '针对温度、压力、流量、液位等工艺参数设计的安全泄压系统以及安全泄压措施不完善', '国家安监总局41号令（修订）第九条第三款', '国家安监总局41号令（修订）第九条第三款 《蒸馏系统安全控制指导意见》鲁安监发[2011]140号 《石油化工企业设计防火规范》GB50160-2008第5.5条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '针对温度、压力、流量、液位等工艺参数设计的安全泄压系统以及安全泄压措施已完善', 2, '工艺安全', '', NULL);
