INSERT INTO `t_check_list` VALUES ('01a0454a-0971-404c-859b-2b65fe75ad4e', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '设备及管理', '生产经营单位是否对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测应当作好记录，并由有关人员签字。', '查阅检验检测及维护保养资料，现场检查相关设施的运行情况。', '生产经营单位未对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测未作记录，未签字。', '《安全生产法》第33条', '《安全生产法》第33条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '生产经营单位已对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测已作记录，并签字。', 23, '设备安全', '', NULL);
