INSERT INTO `t_check_list` VALUES ('e4a6c77e-1000-455c-b7fa-8fea308b05f4', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '配电室', '绝缘靴、绝缘手套、绝缘拉杆等绝缘器材是否定期检测。', '现场检查', '绝缘靴、绝缘手套、绝缘拉杆等绝缘器材未定期检测。', '《带电作业用绝缘工具试验导则》（DL/T878-2004）第5.6条', '《带电作业用绝缘工具试验导则》（DL/T878-2004）第5.6条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '绝缘靴、绝缘手套、绝缘拉杆等绝缘器材已定期检测。', 30, '电气安全', NULL, NULL);
INSERT INTO `t_check_list` VALUES ('c8ea8377-09a8-4d61-b384-f25264a103cb', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '危险化学品管理', '检查危险化学品储存是否在专用仓库、专用场地或者专用储存室（专用仓库）内，并由专人负责管理；剧毒化学品以及储存数量构成重大危险源的其他危险化学品，是否在专用仓库内单独存放，并实行双人收发、双人保管制度。剧毒化学品是否实行“五双”管理，即“双人保管、双人领取、双人使用、双把锁、双本帐”的管理制度。储存危险化学品的单位是否建立危险化学品出入库核查、登记制度。', '现场检查，查制度；着重询问相关人员对仓库/罐区物质的了解程度，如果相关人员无法准确回答所储存的物料名称、应急处置措施等内容，要加强对该人员教育培训的内容的检查。', '检查危险化学品储存未在专用仓库、专用场地或者专用储存室（专用仓库）内，并由专人负责管理；剧毒化学品以及储存数量构成重大危险源的其他危险化学品，未在专用仓库内单独存放，未实行双人收发、双人保管制度。剧毒化学品未实行“五双”管理，即“双人保管、双人领取、双人使用、双把锁、双本帐”的管理制度。储存危险化学品的单位未建立危险化学品出入库核查、登记制度。', '《危险化学品安全管理条例》第二十四、二十五条', '《危险化学品安全管理条例》第二十四、二十五条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '检查危险化学品储存已在专用仓库、专用场地或者专用储存室（专用仓库）内，并由专人负责管理；剧毒化学品以及储存数量构成重大危险源的其他危险化学品，已在专用仓库内单独存放，已实行双人收发、双人保管制度。剧毒化学品已实行“五双”管理，即“双人保管、双人领取、双人使用、双把锁、双本帐”的管理制度。储存危险化学品的单位已建立危险化学品出入库核查、登记制度。', 31, '储运安全', '', NULL);
INSERT INTO `t_check_list` VALUES ('dda73127-6174-4de7-8e7f-217d54fb4ed7', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '①重大危险源是否配备温度、压力、液位、流量、组份等信息的不间断采集和监测系统以及可燃气体和有毒有害气体泄漏检测报警装置，并具备信息远传、连续记录、事故预警、信息存储等功能；', '现场检查，查阅记录', '①重大危险源未配备温度、压力、液位、流量、组份等信息的不间断采集和监测系统以及可燃气体和有毒有害气体泄漏检测报警装置，并具备信息远传、连续记录、事故预警、信息存储等功能；一级或者二级重大危险源，不具备紧急停车功能；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '①重大危险源已配备温度、压力、液位、流量、组份等信息的不间断采集和监测系统以及可燃气体和有毒有害气体泄漏检测报警装置，并具备信息远传、连续记录、事故预警、信息存储等功能；一级或者二级重大危险源，已具备紧急停车功能；', 32, '重大危险源', '', NULL);
INSERT INTO `t_check_list` VALUES ('60a62c7f-4842-42dc-8253-fe9ec6fb51ff', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '②重大危险源的化工生产装置装备是否满足安全生产要求的自动化控制系统；一级或者二级重大危险源，装备紧急停车系统；', '现场检查，查阅记录', '②重大危险源的化工生产装置装备不满足安全生产要求的自动化控制系统；一级或者二级重大危险源，未装备紧急停车系统；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '②重大危险源的化工生产装置装备已满足安全生产要求的自动化控制系统；一级或者二级重大危险源，已装备紧急停车系统；', 33, '重大危险源', '', NULL);
INSERT INTO `t_check_list` VALUES ('c3bf1954-af3f-4edb-b3e3-55bc949dcb68', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '③对重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施，是否设置紧急切断装置；毒性气体的设施，是否设置泄漏物紧急处置装置。涉及毒性气体、液化气体、剧毒液体的一级或者二级重大危险源，是否配备独立的安全仪表系统（SIS）；', '现场检查，查阅记录', '③对重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施，未设置紧急切断装置；毒性气体的设施，未设置泄漏物紧急处置装置。涉及毒性气体、液化气体、剧毒液体的一级或者二级重大危险源，未配备独立的安全仪表系统（SIS）；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '③对重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施，已设置紧急切断装置；毒性气体的设施，已设置泄漏物紧急处置装置。涉及毒性气体、液化气体、剧毒液体的一级或者二级重大危险源，已配备独立的安全仪表系统（SIS）；', 34, '重大危险源', '', NULL);
INSERT INTO `t_check_list` VALUES ('99cbb52e-fe65-4c2a-8593-9540cf74b862', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '④重大危险源中储存剧毒物质的场所或者设施，是否设置视频监控系统。', '现场检查，查阅记录', '④重大危险源中储存剧毒物质的场所或者设施，未设置视频监控系统；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '④重大危险源中储存剧毒物质的场所或者设施，已设置视频监控系统；', 35, '重大危险源', '', NULL);
