INSERT INTO `t_check_list` VALUES ('c74235ce-e67c-4ae4-af40-3b301732efef', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '是否在重大危险源所在场所设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '现场检查', '危险化学品单位未在重大危险源所在场所设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十八条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十八条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '危险化学品单位已在重大危险源所在场所设置明显的安全警示标志，写明紧急情况下的应急处置办法。', 39, '重大危险源', '', NULL);
INSERT INTO `t_check_list` VALUES ('231d74d3-f156-4596-b330-ebf0c1790515', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '是否落实特殊作业审批制度；特殊作业票填写是否符合规范，作业票与作业内容是否一致，签审是否齐全。', '查阅记录和现场检查、询问', '未落实特殊作业审批制度；特殊作业票填写不符合规范，作业票与作业内容不一致，签审不齐全。', '《化学品生产单位特殊作业安全规范》GB30871-2014', '《化学品生产单位特殊作业安全规范》GB30871-2014', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '已落实特殊作业审批制度；特殊作业票填写已符合规范，作业票与作业内容一致，签审已齐全。', 40, '特殊作业', '', NULL);
INSERT INTO `t_check_list` VALUES ('24b49bef-6f8f-41a4-9b09-cfbcf6a7914b', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '特殊作业场所是否配备可燃、有毒气体（物质）检测报警仪、氧含量分析仪器、空气呼吸器。', '查阅记录和现场检查、询问', '特殊作业场所未配备可燃、有毒气体（物质）检测报警仪、氧含量分析仪器、空气呼吸器。', '《化学品生产单位特殊作业安全规范》GB30871-2014', '《化学品生产单位特殊作业安全规范》GB30871-2014', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '特殊作业场所已配备可燃、有毒气体（物质）检测报警仪、氧含量分析仪器、空气呼吸器。', 41, '特殊作业', '', NULL);
INSERT INTO `t_check_list` VALUES ('4cf17dc6-f655-4863-8f8e-cc5cc6402c0f', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '动火作业是否按规定进行可燃气体分析，受限空间作业是否按规定进行可燃气体、氧含量和有毒气体分析，检验、检测项目与作业票规定是否一致。', '查阅记录和现场检查、询问', '动火作业未按规定进行可燃气体分析，受限空间作业按规定进行可燃气体、氧含量和有毒气体分析，检验、检测项目与作业票规定不一致。', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '动火作业已按规定进行可燃气体分析，受限空间作业按规定进行可燃气体、氧含量和有毒气体分析，检验、检测项目与作业票规定一致。', 42, '特殊作业', '', NULL);
INSERT INTO `t_check_list` VALUES ('a8660fc0-2ce7-452b-b761-ae73fb8694a8', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '特殊作业现场是否向作业人员进行安全技术交底；特殊作业过程是否有人持续监护，安全防护措施是否落实，是否持特殊作业票进行作业。', '查阅记录和现场检查、询问', '特殊作业现场未向作业人员进行安全技术交底；特殊作业过程无人员持续监护，安全防护措施未落实，未持特殊作业票进行作业。', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '特殊作业现场已向作业人员进行安全技术交底；特殊作业过程已有人员持续监护，安全防护措施已落实，并持特殊作业票进行作业。', 43, '特殊作业', '', NULL);
INSERT INTO `t_check_list` VALUES ('fe727208-a6b1-467e-9ca4-6433a57b50e6', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '作业现场是否配备相应的安全防护用品及应急救援器材。', '查阅记录和现场检查、询问', '作业现场未配备相应的安全防护用品及应急救援器材。', '《化学品生产单位特殊作业安全规范》GB30871-2014', '《化学品生产单位特殊作业安全规范》GB30871-2014', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2,5', '作业现场已配备相应的安全防护用品及应急救援器材。', 44, '特殊作业', '', NULL);
INSERT INTO `t_check_list` VALUES ('1001', '1', '13.2 事故控制与人员救治', '发生事故后，用人单位立即采取有效措施，减少或者消除职业病危害因素，防止事故扩大对遭受或可能遭受急性职业病危害的劳动者，应及时组织救治、进行健康检查和医学观察，并承担所需费用用人单位不得故意破坏事故现场、毁灭有关证据', '调查询问', '发生或可能发生急性职业病危害事故时，未立即采取应急救援和控制措施的', '《职业病防治法》第38条、《监督管理规定》第35条', '《职业病防治法》第73条（七）项、《监督管理规定》第51条（六）项', '《职业病防治法》给予警告，责令限期改正；逾期不改的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业，或提请人民政府关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1002', '1', '4.4 高毒作业场所 高毒作业场所隔离', '高毒作业场所与其他作业场所隔离，此隔离应当指屏障或距离隔离，不仅仅以标志线或标志隔离', '查阅职业卫生档案、职业病危害项目申报表、原辅材料清单、化学品安全数据说明书（MSDS）、高毒物品作业场所台账，按照《高毒物品目录》，核对其认定的高毒作业场所，现场检查', '高毒作业场所未与其他作业场所有效隔离的', '《有毒作业条例》第11条第2款（二）项', '《有毒作业条例》第66条（三）项', '《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪、危险物品肇事罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1003', '1', '8.2 明令禁止使用', '任何单位和个人不得生产、经营或者进口国家明令禁止使用的可能产生职业病危害的设备和材料', '查阅生产工艺流程图、原材料采购计划和库存原材料清单、生产设备台账、有毒有害物质清单，查阅危险化学品管理档案和化学品安全数据说明书（MSDS），查阅职业卫生档案，现场检查', '生产、经营或者进口国家明令禁止使用的可能产生职业病危害的设备或材料', '《职业病防治法》第31条', '《职业病防治法》第77条', '生产、经营或者进口国家明令禁止使用的可能产生职业病危害的设备或者材料的，依照有关法律、行政法规的规定给予处罚', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1004', '1', '3.2 建设项目职业病防护设施设计和审查', '建设单位应当会同设计单位对职业病防护设施设计专篇进行完善，并对其真实性、合法性和实用性负责。', '检查职业病防护设施设计专篇、评审专家组成、评审会相关材料。', '建设单位在职业病防护设施设计评审中弄虚作假的。', '《“三同时”监督管理暂行办法》第18条第2款。', '《“三同时”监督管理暂行办法》第36条', '责令改正，并处5千元以上3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1005', '1', '7.2.有毒作业的防护 有毒作业的控制', '对产生毒物的生产过程和设备，应优先采用机械化和自动化，避免直接人工操作为防止物料跑、冒、滴、漏，其设备和管道应采取有效的密闭措施，密闭形式应根据工艺流程、设备特点、生产工艺、安全要求及便于操作、维修等因素确定，并应结合生产工艺采取通风和净化措施应根据生产工艺和毒物特性，参照GBZ/T194的规定设计相应的防毒通风控制措施，使劳动者活动的工作场所有害物质浓度符合GBZ2.1要求', '查阅生产工艺流程图、有害物质清单、防毒设施台账及有关设施设计说明，查阅职业病危害检测和防毒设施评价报告、防护用品的发放和领取记录，现场检查', '未提供职业病防护设施和个人使用的职业病防护用品，或者提供的职业病防护设施和个人使用的职业病防护用品不符合国家职业卫生标准和卫生要求的', '《职业病防治法》第23条、《有毒作业条例》第11条（三）项、第21条，《监督管理规定》第12条（三）项、第16条（一）项', '《职业病防治法》第73条（二）项、《有毒作业条例》第60条（一）项、第59条（六）项、《监督管理规定》第51条(二）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业或者提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1006', '1', '4.1 职业病危害因素强（浓）度', '工作场所职业病危害因素强度或者浓度符合国家职业卫生标准和行业标准的要求', '查看职业卫生档案，根据生产工艺流程图、职业病危害因素分布点，查阅工作场所职业病危害因素日常监测结果、检测与评价报告，现场监督检测', '工作场所职业病危害因素强度或浓度超过国家职业卫生标准', '《职业病防治法》第15条（一）项、《监督管理规定》第12条（四）项', '《职业病防治法》第73条第（一）项、《监督管理规定》第51条（一）项', '给予警告，责令限期改正；逾期不改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1007', '1', '9.2 现场告知  警示标识和说明', '存在或产生高毒物品的作业岗位，应当按照《高毒物品作业岗位职业病危害告知规范》（GBZ/T203)的规定，在醒目位置设置高毒物品告知卡，告知卡应当载明高毒物品的名称、理化特性、健康危害、防护措施及应急处理等告知内容与警示标识', '查看使用有毒物品作业场所登记表，按照《高毒物品目录》核实作业岗位存在或产生的职业病危害因素是否为高毒物品，现场检查高毒物质告知卡', '使用高毒物品作业场所未按照规定设置高毒物品告知卡的', '《职业病防治法》第25条第2款、《有毒作业条例》第12条第2款、《监督管理规定》第15条第3款', '《职业病防治法》第73条（八）项、《有毒作业条例》第59条（一）项、（五）项、《监督管理规定》第51条（七）项', '《职业病防治法》《监督管理规定》给予警告，责令限期改正，逾期未改的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭。 《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1008', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收（续）', '职业病危害严重的建设项目竣工验收时，建设单位应当按照规定向安全生产监督管理部门申请建设项目职业病防护设施竣工验收，并提交下列文件、资料：（一）建设项目职业病防护设施竣工验收申请书；（二）建设项目职业病防护设施设计审查批复文件（复印件）；（三）建设项目职业病危害控制效果评价机构资质证明（影印件）；（四）建设项目职业病危害控制效果评价报告；（五）职业卫生专家对职业病危害控制效果评价报告的审查意见；（六）建设单位对职业病危害控制效果评价报告的评审意见；（七）建设项目职业病防护设施施工单位和监理单位资质证明（影印', '查看安全生产监督管理部门职业病防护设施竣工验收批文', '职业病危害严重的建设项目职业病防护设施未经安监部门验收或验收不合格，擅自投入使用的', '《职业病防治法》第18条第3款、 《有毒作业条例》第13条第1款、 《监督管理规定》第14条、《“三同时”监督管理暂行办法》第30条', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭。 《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1009', '1', '4.2 生产布局', '生产布局合理，符合有害与无害作业分开的原则 生产布局应按照《工业企业设计卫生标准》（GBZ1）的规定，尽量考虑机械化、自动化和远端操作，加强密闭，避免直接操作，并应结合生产工艺采取相应的防护措施。 车间内生产工艺设备布局应重点考虑达到防尘、防毒、防暑、防寒、防噪声与振动、防电离辐射、防非电离辐射等的要求', '查阅建设项目职业病危害预评价报告、控制效果评价报告、现状评价报告，检查平面布置图、竖向布置图，查看现场', '生产布局不合理，未将有害作业与无害作业分开', '《职业病防治法》第15条（三）项、《有毒作业条例》第11条第2款（二）项、《监督管理规定》第12条（一）项', '《有毒作业条例》第66条（二）项、《监督管理规定》第48条（一）项', '《监督管理规定》给予警告，责令限期改正，可以并处5千元以上2万元以下的罚款。 《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪、危险物品肇事罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1010', '1', '5.1 职业病危害因素日常监测', '用人单位单位应当实施由专人负责职业病危害因素日常监测，并确保监测系统处于正常运转状态。 按照《工作场所空气中有害物质监测的采样规范》（GBZ 159）设置日常监测装置，按照《工作场所空气中粉尘测定》（GBZ/T 192）、《工作场所空气有毒物质测定》（GBZ/T 160）、《工作场所物理因素测量》（GBZ/T 189）等监测职业病危害因素浓度变化情况，并记录存档职业病危害因素监测、采样、布点符合国家标准从事可能发生急性职业中毒的作业人员（岗位）应当配备有毒物质快速检测报警仪', '认定应当进行日常监测的职业病危害因素种类和监测点数，现场检查现有的监测仪器、实际监测的有害因素种类，检查监测人员书面聘用文件，查看有关监测记录', '未实施由专人负责的职业病危害因素日常监测或监测系统不能正常监测', '《职业病防治法》第27条第1款、《监督管理规定》第19条', '《职业病防治法》第72条（二）项、《监督管理规定》第50条（二）项', '责令限期改正，给予警告，可并处5万元以上10万元以下罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1011', '1', '7.8 微波与电磁场防护', '产生微波或者高频电磁场的设备，应采取有效的防止电磁辐射能泄漏的措施产生非电离辐射的设备应有良好的屏蔽措施超高压输电设备，在人通常不去的地方应当用屏蔽网、罩等设备遮挡起来', '查阅职业卫生档案，查阅职业病危害监测、检测及评价报告，现场检查', '', '', '', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1012', '1', '1.2 职业卫生管理人员', '\"用人单位应配备专职或者兼职的职业卫生管理人员。 职业病危害严重的用人单位，应配备专职职业卫生管理人员其他存在职业病危害的用人单位，劳动者超过100人的，应当配备专职职业卫生管理人员；劳动者在100人以下的，应当配备专职或者兼职的职业卫生管理人员。 从事使用高毒物品作业的用人单位，应当配备专职的或者兼职的职业卫生医师和护士；不具备配备专职的或者兼职的职业卫生医师和护士条件的，应当与依法取得资质认可的职业卫生技术服务机构签订合同，由其提供职业卫生服务。 \"', '检查职业卫生管理人员的书面聘用文件、个人资质（职业卫生专业知识背景、工作经历和执业医师资格）文件和专业档案，查阅与服务机构签订的合同。', '未配备专职或者兼职职业卫生管理人员。', '\"《职业病防治法》第21条(一)项、 《有毒作业条例》第17条第2款、 《监督管理规定》第8条。\"', '《职业病防治法》第71条（二）项、《有毒作业条例》第69条（一）项、《监督管理规定》第49条（二）项。', '\"《职业病防治法》、《监督管理规定》给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款。 《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭。\"', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1013', '1', '1.1 职业卫生管理机构或者组织', '用人单位应设置或者指定职业卫生管理机构或者组织，负责本单位的职业病防治工作。 职业病危害严重的用人单位，应当设置或者指定职业卫生管理机构或者组织；其他存在职业病危害的用人单位，劳动者超过100人的，应当设置或者指定职业卫生管理机构或者组织。', '查阅组织机构及职责文件，查阅工作情况记录，询问有关人员。', '未设置或者指定职业卫生管理机构或者组织。', '《职业病防治法》第21条(一)项、《监督管理规定》第8条。', '《职业病防治法》第71条（二）项、《监督管理规定》第49条（二）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1014', '1', '2.1 及时、如实申报职业病危害项目', '\"用人单位工作场所存在职业病目录所列职业病的危害因素的，应当按照《申报办法》的规定，及时、如实向所在地安全生产监督管理部门申报职业病危害项目，职业病危害因素按照《职业病危害因素分类目录》确定用人单位申报职业病危害项目时，应当提交《职业病危害项目申报表》和下列文件、资料： （1）用人单位的基本情况； （2）工作场所职业病危害因素种类、分布情况以及接触人数； （3）法律、法规和规章规定的其他文件、资料。\"', '查看申报回执，结合职业卫生档案，现场检查接害岗位和种类，查阅申报表，核对工作场所职业病危害状况和申报内容。', '未及时、如实申报产生职业病危害项目。', '《职业病防治法》第16条、《申报办法》第2条、《监督管理规定》第13条。', '《职业病防治法》第72条（一）项、《申报办法》第14条、《监督管理规定》第50条（一）项。', '责令限期改正，给予警告，可以并处5万元以上10万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1015', '1', '3.1 建设项目职业病危害预评价', '职业病危害预评价报告编制完成后，建设单位应当组织有关职业卫生专家，对职业病危害预评价报告进行评审。', '检查职业病危害预评价报告、评价单位资质证明、评审专家组成、评审会相关材料。', '未对职业病预评价报告进行评审的。', '《“三同时”监督管理暂行办法》第11条第1款。', '《“三同时”监督管理暂行办法》第35条（一）项。', '给予警告，限期责令改正；逾期不改正的，处3万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1016', '1', '3.2 建设项目职业病防护设施设计和审查', '存在职业病危害的建设项目，建设单位应当委托具有相应资质的设计单位编制职业病防护设施设计专篇建设单位在职业病防护设施设计专篇编制完成后，应当组织有关职业卫生专家，对职业病防护设施设计专篇进行评审。', '检查职业病防护设施设计专篇、评审专家组成、评审会相关材料。', '未对职业病防护设施设计进行评审的。', '《“三同时”监督管理暂行办法》第18条第1款。', '《“三同时”监督管理暂行办法》第35条(一)项', '给予警告，限期责令改正；逾期不改正的，处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1017', '1', '1.5 操作规程', '建立健全各工种（岗位）职业卫生操作规程，岗位操作规程应经科学论证，并与岗位职责相对应，其内容还应包括职业卫生防护的内容，可张贴或以其他方式，方便劳动者了解、提示劳动者遵守。', '现场查看公告栏。', '未公布岗位操作规。', '《职业病防治法》第25条第1款、《监督管理规定》第15条第1款。', '《职业病防治法》第71条（三）项、《监督管理规定》）第49条（六）项。', '给予警告，责令限期改正；逾期未改正的，处10万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1018', '1', '12.5 提供职业病诊断、鉴定所需要资料', '劳动者健康出现损害需要进行职业病诊断、鉴定的，用人单位应当如实提供职业病诊断、鉴定所需的劳动者职业史和职业病危害接触史、工作场所职业病危害因素检测结果和放射工作人员个人剂量监测结果等资料', '检查职业健康监护档案、职业卫生档案，借阅登记，复印记录，投诉记录', '不提供职业病诊断、鉴定所需要资料的', '《职业病防治法》第48条、《监督管理规定》第32条', '《职业病防治法》第73条（十）项、《监督管理规定》第51条（九）项、《健康监护监督管理办法》第28条(二)项', '给予警告，责令限期改正；逾期未改正的，处5万元以上20万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1019', '1', '3.1 建设项目职业病危害预评价', '建设项目职业病危害预评价报告经安全生产监督管理部门备案或者审核同意后，建设项目的选址、生产规模、工艺或者职业病危害因素的种类、职业病防护设施等发生重大变更的，建设单位应当对变更内容重新进行职业病危害预评价，办理相应的备案或者审核手续。', '对照原评价报告检查建设项目的选址、生产规模、工艺或者职业病危害因素的种类、职业病防护设施等，查看是否发生重大变更，查看建设项目批文、清单、安监部门批准文件等相关材料。', '建设项目选址、生产规模、工艺、职业病危害因素种类、职业病防护设施发生重大变更时，未对变更内容重新进行职业病危害预评价并办理有关手续，进行施工的。', '《“三同时”监督管理暂行办法》第14条。', '《“三同时”监督管理暂行办法》第35条（二）项。', '给予警告，责令限期改正；逾期不改正的，处3万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1020', '1', '5.4 检测、评价结果处理', '用人单位应当落实职业病危害检测、现状评价报告中提出的建议和措施，并将检测、评价报告及整改情况存入用人单位的职业卫生档案，定期向所在地安全生产监督管理部门报告并向劳动者公布', '查看职业卫生档案和安监部门的备案记录，检测、评价报告复印件，现场检查公告栏', '1、工作场所职业病危害因素检测评价结果没有存档、上报、公布', '《职业病防治法》第27条第2款、《有毒作业条例》第26条第1款、《监督管理规定》第15条第1款和第20条第3款', '《职业病防治法》第71条（一）项、《监督管理规定》第49条(八)项', '给予警告，责令限期改正；逾期未改正的，处10万元以下罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1021', '1', '7.9 井下采掘作业防护', '井下采掘作业必须采用湿式凿岩，并应采取冲帮、洗顶、喷雾、洒水等综合防尘措施井下气象条件应达到卫生要求从事井下采掘作业条件达不到国家标准和要求的，应加强劳动者的个体防护', '查阅职业卫生档案、井下巷道与采掘作业面分布图、井下采掘作业面防尘设施台账及防尘操作规程，查阅职业病危害监测、检测和评价报告，现场检查', '没有提供职业病防护设施或提供的职业危害防护设施不符合国家标准、行业标准', '《职业病防治法》第15条（二）项、《监督管理规定》第12条（三）项', '《职业病防治法》第73条（二）项、《监督管理规定》第51条(二）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业，或提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1022', '1', '10.2 劳动者培训 在岗期间职业卫生培训', '用人单位应定期对在岗期间的劳动者进行职业卫生培训做出明确规定培训的内容应包括职业卫生法律、法规、规章、操作规程、所在岗位的职业病危害及其防护设施、个人职业病防护用品的使用和维护、应急救援知识、劳动者所享有的职业卫生权利等内容根据用人单位实际情况制定培训计划，确定培训周期应做好记录及存档工作，存档内容包括培训通知、教材、教员、试卷、考核成绩等，档案资料应有专人负责保管', '查阅人事工作与季度工作计划 ，核查新招工人、转岗工人人数与去向，查阅培训通知和培训计划，查阅培训签到簿、记录和培训教材、培训教员、考核成绩等培训档案资料，现场询问劳动者', '未按照规定组织劳动者进行职业卫生培训', '《职业病防治法》第35条第2款、《有毒作业条例》第19条第2款、《监督管理规定》第10条第1款、3款', '《职业病防治法》第71条（四）项、《监督管理规定》第49条（七）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1023', '1', '8.1 隐瞒生产技术、工艺、设备和材料所产生的职业病危害', '用人单位不得隐瞒生产技术、工艺、设备和材料所产生的职业病危害用人单位应在醒目位置对有职业病危害的技术、工艺、设备和材料用中文公示，并采取相应的防护措施并告知劳动者，包括以职业卫生培训的方式告知对有职业病危害的技术、工艺、设备和材料，故意隐瞒其危害而采取的，用人单位对其所造成的职业病危害后果承担责任', '查阅用人单位原材料采购计划和生产设备改造或更新计划，询问有关技术人员，查阅生产工艺流程图及产生职业病危害的设备登记表、有毒有害物质清单、危险化学品管理档案和化学品安全技术说明书（MSDS）', '隐瞒技术、工艺、设备和材料所产生的职业病危害', '《职业病防治法》第33条、《监督管理规定》第28条', '《职业病防治法》第76条（一）项、《监督管理规定》第52条（一）项', '责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业,或者提请人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1024', '1', '11.4 离岗时的职业健康检查', '对准备脱离所从事的职业病危害作业或者岗位的劳动者，用人单位应当在劳动者离岗前30日内组织劳动者进行离岗时的职业健康检查劳动者离岗前90日内的在岗期间的职业健康检查可以视为离岗时的职业健康检查', '检查职业健康监护制度、离岗劳动者名册、离岗劳动者职业健康监护档案、承担职业健康检查的医疗卫生机构资质证明、职业健康体检结果报告', '未按照规定组织离岗时的职业健康检查', '《职业病防治法》第36条第1款、《有毒作业条例》第33条第1款、《监督管理规定》第30条第1款、《健康监护监督管理办法》第15条第1款', '《职业病防治法》第72条（四）项、《有毒作业条例》第68条(三)项、《监督管理规定》第50条第（四）项、《健康监护监督管理办法》第27条(一)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期改正，给予警告，可并处5万元以上10万元以下罚款。 《有毒作业条例》给予警告，责令限期改正，处2万元以上5万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1025', '1', '', '建设项目试运行期间，建设单位应当对职业病防护设施运行的情况和工作场所的职业病危害因素进行监测，并委托具有相应资质的职业卫生技术服务机构进行职业病危害控制效果评价', '', '', '《有毒作业条例》第13条第1款、', '', '《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1026', '1', '6.5 督促指导劳动者正确佩戴', '用人单位应当督促、指导劳动者按照使用规则正确佩戴、使用，不得发放钱财物替代发放职业病防护用品', '查阅培训记录，现场检查及询问劳动者', '未对劳动者个体防护采取有效的指导、督促措施的', '《职业病防治法》第35条第2款、《监督管理规定》第16条第1款', '《职业病防治法》71条（四）项《监督管理规定》第49条（七）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1027', '1', '5.3 职业病危害现状评价', '职业病危害严重的用人单位每三年至少进行一次职业病危害现状评价存在职业病危害的用人单位，有下述情形之一的，应当及时委托具有相应资质的职业卫生技术服务机构进行职业病危害现状评价：（一）初次申请职业卫生安全许可证，或者职业卫生安全许可证有效期届满申请换证的；（二）发生职业病危害事故的；（三）国家安全生产监督管理总局规定的其他情形高毒作业场所每半年进行一次职业中毒控制效果评价职业卫生技术服务机构所作评价应当客观真实', '检查用人单位职业病危害评价工作计划，查看服务合同、评价机构资质证明及评价报告，职业卫生专家现场评审', '未对工作场所职业病危害因素进行现状评价', '《职业病防治法》第27条第2、3款，《有毒作业条例》第26条第1、2款，《监督管理规定》第20条第2款、第21条', '《职业病防治法》第73条（四）项、《有毒作业条例》第59条（三）项，《监督管理规定》第51条（四）项', '《职业病防治法》、《监督管理规定》给予警告，责令限期改正；逾期未改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭                        《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1028', '1', '11.5 职业健康检查报告处理措施', '（二）对健康损害可能与所从事的职业相关的劳动者，进行妥善安置；', '检查职业健康检查结果报告，查看劳动者职业健康监护档案，查看调离通知单', '未根据职业健康检查情况采取相应措施的', '《健康监护监督管理办法》第17条（二）项', '《健康监护监督管理办法》第26条(五)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1029', '1', '7.7 低温作业防护', '低温作业是指在生产劳动过程中，其工作地点平均温度等于或低于5℃的作业低温作业车间（冷库）应附设工作服干燥室及淋浴室为劳动者配备防护服和防护手套等防寒的个人防护用品', '查阅职业卫生档案和认定的低温作业场所，现场检查', '', '', '', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1030', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收（续）', '职业病危害较重的建设项目竣工验收时，建设单位应当按规定向安全生产监督管理部门申请建设项目职业病防护设施竣工验收，并提交下列文件、资料：（一）建设项目职业病防护设施竣工验收申请书；（二）建设项目职业病危害预评价报告审核批复文件；（三）建设项目职业病危害控制效果评价机构资质证明（影印件）；（四）建设项目立项审批文件（复印件）；（五）建设项目职业病防护设施设计专篇；（六）建设项目职业病危害控制效果评价报告；（七）职业卫生专家对职业病危害控制效果评价报告的审查意见； （八）建设单位对职业病危害控制效果评价报告的评', '查看安全生产监督管理部门职业病防护设施竣工验收批文', '职业病危害较重的建设项目职业病防护设施未经安监部门验收或验收不合格，擅自投入使用的', '《职业病防治法》第18条第3款、《有毒作业条例》第13条第1款、《监督管理规定》第14条、《“三同时”监督管理暂行办法》第29条', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1031', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收', '建设项目完工后，需要进行试运行的，其配套建设的职业病防护设施必须与主体工程同时投入试运行试运行时间应当不少于30日，最长不得超过180日，国家有关部门另有规定或者特殊要求的行业除外', '查看职业病防护设施试运行工作报告，现场监督检查', '需要试运行的职业病防护设施未与主体工程同时试运行的', '《“三同时”监督管理暂行办法》第25条', '《“三同时”监督管理暂行办法》第35条(三)项', '给予警告，责令限期改正；逾期不改正的，处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1032', '1', '13.1 职业病危害事故报告', '用人单位发生职业病危害事故，应当按照有关规定及时向所在地安全生产监督管理部门和有关部门报告', '查看相关记录', '发生或可能发生急性职业病危害事故时，未按照规定及时报告的', '《职业病防治法》第38条、《监督管理规定》第35条', '《职业病防治法》第73条（七）项、《监督管理规定》第51条（六）项', '《职业病防治法》给予警告，责令限期改正；逾期不改的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业，或提请人民政府关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1033', '1', '6.2 防护设施有效', '职业病防护设施有效是指设施符合产品自身的质量标准，应该是经过国家质量监督检验合格的正规产品；设施符合特定使用场所职业病防护要求，能消除或降低职业病危害因素对劳动者健康的影响职业病防护设施应保证确实有效并应建立相应的保管制度保证责任到位，有人负责，定期检查，及时维修，每天上班之前应有人检查防护设施是否能正常运转', '查阅防护设施产品质量检验合格证、防护设施效果评价报告、防护设施日常运转记录、防护设施定期检查记录和防护设施使用维修记录等，现场检查防护设施', '提供的职业病危害防护设施不符合国家标准、卫生标准', '《职业病防治法》第23条第1款、 《有毒作业条例》第11条(三）项、 《监督管理规定》第12条（三）项', '《职业病防治法》第73条（二）项 、《有毒作业条例》第60条（一）项、《监督管理条例》第51条(二）项', '《职业病防治法》《监督管理规定》给予警告，责令限期改正；逾期不改正的处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业或者提请有关人民政府按国务院规定的权限关闭。 《有毒作业条例》给予警告，责令限期改正，处5万元以上30万元以下的罚款；逾期不改正的，提请人民政府关闭；造成严重职业中毒危害的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1034', '1', '11.11 孕期和哺乳期女职工管理', '用人单位不得安排孕期、哺乳期的女职工从事对其本人和胎儿、婴儿有危害的作业应制定相应的规定，建立女职工档案，包括育龄女职工、孕期女职工或者哺乳期女职工', '检查劳动者名册、职业健康监护制度，检查女职工档案，询问女工委员会或计划生育管理人员，现场核实', '安排孕期、哺乳期女职工从事接触职业病危害的作业', '《职业病防治法》第39条、《有毒作业条例》第7条、《监督管理规定》第33条、《健康监护监督管理办法》第12条第2款', '《职业病防治法》第76条(七)项、《有毒作业条例》第63条第(四)项、《监督管理规定》第52条(七)项、《健康监护监督管理办法》第29条(三)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止作业或者提请人民政府关闭。 《有毒作业条例》给予警告，责令限期改正；逾期不改正的，处2万元以上5万元以下的罚款；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1035', '1', '7.3 密闭作业的防护  高毒物品密闭作业的防护', '进入存在高毒物品的设备、容器或者狭窄封闭场所作业时，保持作业场所良好的通风状态，确保职业中毒危害因素浓度符合国家职业卫生标准，配备合格的防护用品，设置现场监护人员和现场救援设备', '查阅职业卫生档案和认定的存在或产生高毒物品的设备、容器及密闭场所清单，查阅进入存在高毒物品场所工作方案和操作规程及相关记录，查阅通风、检测、照明、通讯设备和防护装备、应急救援设施的配备及台账，查阅职业病危害监测、检测和评价报告', '未按照规定采取措施，安排劳动者进入存在高毒物品的设备、容器或者狭窄封闭场所作业的', '《有毒作业条例》第25条', '《有毒作业条例》第61条（三）项 ', '给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1036', '1', '13.3 职业病危害事故处理', '安全生产监督部门接到报告后，应当及时会同有关部门组织调查处理；必要时，可以采取临时控制措施事故调查处理应当坚持实事求是、尊重科学的原则，及时、准确地查清事故经过、事故原因和事故损失，查明事故性质，认定事故责任，总结事故教训，提出整改措施，并对事故责任者依法追究责任', '查明事故发生的经过、原因、人员伤亡情况及直接经济损失；认定事故的性质和事故责任；提出对事故责任者的处理建议；总结事故教训，提出防范和整改措施；提交事故调查报告', '违反《职业病防治法》规定，已经对劳动者生命健康造成严重损害的', '《职业病防治法》相关规定', '《职业病防治法》第78条、《监督管理规定》第53条第1款', '责令停止作业，或提请政府关闭，并处10万元以上50万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1037', '1', '11.5 职业健康检查报告处理措施', '（五）对存在职业病危害的岗位，立即改善劳动条件，完善职业病防护设施，为劳动者配备符合国家标准的职业病危害防护用品', '现场检查完善职业病防护设施及职业病危害防护用品配备情况', '未根据职业健康检查情况采取相应措施的', '《健康监护监督管理办法》第17条（五）项', '《健康监护监督管理办法》第26条(五)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1038', '1', '4.8 应急撤离通道', '应急通道须保持通畅，设置应急照明设施，并在醒目位置设置明显的警示标识撤离通道的宽窄应根据需要设置，如需用车辆、担架的，宽度应能保证车辆、担架顺利通过 应建立相应的管理制度，责任到位，有人负责，定期检查，保证应急通道畅通', '查阅职业卫生档案，现场查看应急通道设置有无及是否畅通，醒目位置是否设置应急通道警示标识，应急照明设施设置情况，检查相应的管理文件', '可能产生急性职业损伤的有毒、有害工作场所，未设置撤离通道和泄险区', '《职业病防治法》第26条第1款、 《有毒作业条例》第11条第2 款（四）项、《监督管理规定》第17条第1款', '《职业病防治法》第76条（三）项、《有毒作业条例》第59条（四）项、《监督管理规定》第52条（三）项', '《职业病防治法》、《监督管理规定》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭                                《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1039', '1', '11.7 如实、无偿为劳动者提供职业健康监护档案复印件', '用人单位有义务在劳动者离岗时提供职业健康监护档案复印件，并在所提供的复印件上签章，不得弄虚作假，不得向劳动者收取任何费用', '检查职业健康监护制度，借阅登记，复印记录，投诉记录', '劳动者离开用人单位时，用人单位未如实、无偿提供职业健康监护档案复印件的', '《职业病防治法》第37条第3款、《有毒作业条例》第40条、《监督管理规定》第31条第3款、《健康监护监督管理办法》第20条第2款', '《职业病防治法》第72条（五）项、《有毒作业条例》第68条(八)项、《监督管理规定》第50条（五）项、《健康监护监督管理办法》第27条(二)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期改正，给予警告，可并处5万元以上10万元以下罚款。 《有毒作业条例》给予警告，责令限期改正，处2万元以上5万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1040', '1', '6.1 防护设施设置', '产生粉尘、毒物的生产过程和设备，应尽量机械化和自动化，加强密闭，并结合生产工艺，在所有产生粉尘、毒物的岗位及设备，安装通风排毒除尘装置放散粉尘的生产过程尽量采用湿式作业', '查阅原辅材料清单及其化学品安全数据说明书（MSDS)，查阅主要生产设备、工艺流程图及其说明 ，查阅职业病危害检测和评价报告、防护设施台账及档案，现场核查应当设置职业病防护设施的岗位及设备', '没有与职业病危害防护相适应的设施', '《职业病防治法》第15条（二）项、《监督管理规定》第12条（三）项', '《职业病防治法》第73条（二）项、《监督管理规定》第51条(二）项', '给予警告，责令限期改正；逾期不改正的处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业或者提请有关人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1041', '1', '7.4 可能发生急性职业损伤的有毒、有害工作场所的防护', '可能发生急性职业损伤的有毒、有害工作场所，是指可能发生毒物、强腐蚀物质、刺激性物质泄漏等对劳动者生命健康造成急性危害的工作场所劳动者在进入可能发生急性职业损伤的有毒、有害工作场所，应使用个人职业病防护用品，如携气式呼吸器、全封闭式化学防护服、防护手套、防护鞋靴等以及对被救者施救所需的急救用品，在使用可能导致皮肤粘膜或眼睛灼烧、有腐蚀性、刺激性化学物质发生事故时能在10秒内立即使用冲眼器、流动水龙头以及冲淋设备等冲洗设备有毒、有害物质发生泄漏或腐蚀时应使用应急救援通道和必要的泄险区', '查阅职业卫生档案和可能发生急性职业损伤的作业场所登记表，查阅报警装置、应急救援设施和事故通风装置的台账和现场急救用品的清单，现场检查', '可能发生急性职业损伤的有毒、有害工作场所未设置报警装置，未配置现场急救用品、冲洗设备、应急撤离通道和必要的泄险区的', '《职业病防治法》第26条第1款、《监督管理规定》第17条第1、2款', '《职业病防治法》第76条（三）项、《监督管理规定》第52条（三）项', '责令限期治理，并处5万元以上30万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1042', '1', '11.5 职业健康检查报告处理措施', '（三）对需要复查的劳动者，按照职业健康检查机构要求的时间安排复查和医学观察；', '检查职业健康检查结果报告，复查报告，查看职业健康监护档案', '未根据职业健康检查情况采取相应措施的', '《健康监护监督管理办法》第17条（三）项', '《健康监护监督管理办法》第26条(五)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1043', '1', '7.6 高温作业防护', '用人单位应优先采用有利于控制高温的新技术、新工艺、新材料、新设备，使作业人员远离热源对高温强热辐射作业、高温高气湿作业、夏季露天作业等不同的高温作业类型采取相应的防治措施向劳动者提供必需的高温防护设备和符合要求的个人防护用品，并加强对高温防护设备的维护和个人防护用品的管理在高温天气期间应根据生产特点和具体条件，采取合理安排工作时间、轮换作业', '查阅职业卫生档案和认定的高温作业场所，查阅防暑降温设施台账，查阅职业病危害监测、检测、评价报告，现场检查', '没有提供职业病防护设施或提供的职业危害防护设施不符合国家标准、行业标准', '职业病防治法》第15条（二）项、《监督管理规定》第12条（三）项', '《职业病防治法》第73条（二）项、《监督管理规定》第51条(二）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业或提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1044', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收（续）', '职业病危害严重的建设项目竣工验收时，建设单位应当按照规定向安全生产监督管理部门申请建设项目职业病防护设施竣工验收，并提交下列文件、资料：（一）建设项目职业病防护设施竣工验收申请书；（二）建设项目职业病防护设施设计审查批复文件（复印件）；（三）建设项目职业病危害控制效果评价机构资质证明（影印件）；（四）建设项目职业病危害控制效果评价报告；（五）职业卫生专家对职业病危害控制效果评价报告的审查意见；（六）建设单位对职业病危害控制效果评价报告的评审意见；（七）建设项目职业病防护设施施工单位和监理单位资质证明（影印', '查看安全生产监督管理部门职业病防护设施竣工验收批文', '职业病危害严重的建设项目职业病防护设施未经安监部门验收或验收不合格，擅自投入使用的', '《职业病防治法》第18条第3款、 《有毒作业条例》第13条第1款、 《监督管理规定》第14条、《“三同时”监督管理暂行办法》第30条', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭。 《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1045', '1', '1.5 操作规程', '建立健全各工种（岗位）职业卫生操作规程，岗位操作规程应经科学论证，并与岗位职责相对应，其内容还应包括职业卫生防护的内容，可张贴或以其他方式，方便劳动者了解、提示劳动者遵守。', '检查职业病危害因素相关的作业岗位清单和书面的岗位操作规程，现场抽查职业病危害严重的岗位加以验证。', '未建立、健全职业卫生操作规程。', '《职业病防治法》第21条（三）项、《监督管理规定》第11条（十二）项。', '《职业病防治法》第71条（二）项、《监督管理规定》第49条（三）项。', '给予警告，责令限期改正；逾期未改正的，处10万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1046', '1', '9.3 检测评价结果告知', '用人单位应当将职业病危害因素检测、评价结果向劳动者公布用人单位可通过公告栏、书面通知或其他有效方式告知劳动者工作场所职业病危害因素检测及评价结果', '现场检查公告栏，查阅书面通知书，询问劳动者', '未按照规定向劳动者公布职业病危害因素检测结果的', '《职业病防治法》第25条第1款、第27条第2款，《监督管理规定》第15条第1款、20条第3款', '《职业病防治法》第71条（三）项、《监督管理规定》第49条（八）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1047', '1', '10.1 主要负责人与管理人员培训', '用人单位的主要负责人和职业卫生管理人员应当具备与本单位所从事的生产经营活动相适应的职业卫生知识和管理能力，并接受职业卫生培训培训的主要内容：(一）、职业卫生相关法律、法规和国家职业卫生标准；（二）职业病危害预防和控制的基本知识；（三）职业卫生管理相关知识；（四)国家安全生产监督管理总局规定的其他内容', '查阅培训证明', '用人单位主要负责人、职业卫生管理人员未接受安全生产监督管理部门职业卫生培训的', '《职业病防治法》第35条第1款、《监督管理规定》第9条', '《监督管理规定》第48条（二）项', '给予警告，责令限期改正，可以并处5千元以上2万元以下罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1048', '1', '8.4 用人单位在关停并转时对高毒物品的管理', '使用有毒物品作业的用人单位应按规定在转产、停产、停业或者解散破产时采取有效措施，妥善处理留存或者残留高毒物品的设备、包装物或者容器', '查阅用人单位关停并转的政府批复、用人单位资产清算和对高毒物品的处理方案，对原使用高毒物品作业现场检查', '使用有毒物品作业的单位未按规定在转产、停产、停业或者解散破产时采取有效措施，妥善处理留存或者残留高毒物品的设备、包装物或者容器的', '《有毒作业条例》第29条', '《有毒作业条例》第65条', '责令改正，处2万元以上10万元以下罚款；触犯刑律的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大环境污染事故罪、危险物品肇事罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1049', '1', '4.6 现场急救用品', '急性职业损伤的有毒、有害工作场所配置现场急救用品现场急救用品包括发生事故时急救人员所用的个人职业病防护用品，如携气式呼吸器、全封闭式化学防护服、防护手套、防护鞋靴等，以及对被救者施救所需的急救用品，如做人工呼吸所需单向阀防护口罩、现场止血用品、防暑降温用品、给氧器，有特殊需求的可配备急救车、防护小药箱等急救用品的配置应根据现场防护的需要，在专业人员的指导下考虑生产条件、化学物质的理化性质和用量急救用品应存放在车间内或临近车间的地方，一旦发生事故，保证方便获取急救用品存放地的醒目位置应有警示标识，确保劳动者', '查阅职业卫生档案，确定发生急性职业损伤的有毒、有害工作场，现场检查配备的急救用品是否符合国家质量监督检验合格的正规产品，查阅急救用品配备档案、使用记录、定期检查和维修记录，现场对劳动者抽查能否正确使用急救用品', '可能产生急性职业损伤的有毒、有害工作场所，未配置现场急救用品', '《职业病防治法》第26条第1款、 《监督管理规定》第17条第1和2款', '《职业病防治法》第76条（三）项、《监督管理规定》第52条（三）项', '责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1050', '1', '2.2 变更申报', '因技术、工艺、设备或者材料等发生变化导致原申报的职业病危害因素及其相关内容发生重大变化的，自发生变化之日起15日内进行申报。', '检查工作场所，查看技术、工艺、材料变更的相关资料。', '未申报变更职业病危害项目内容。', '《申报办法》第8条(二)项。', '《申报办法》第15条。', '责令限期改正，可以并处5千元以上3万元以下罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1051', '1', '6.4 防护用品配备', '用人单位应根据工作场所的职业病危害因素的种类、危害程度、对人体的影响途径以及现场生产条件、职业病危害因素的接触水平以及个人的生理和健康状况等特点，为劳动者配备适宜的个人职业病防护用品所使用的个人职业病防护用品应是由有生产个人职业病防护用品资质的厂家生产的符合国家或行业标准的产品生产特种劳动防护用品（如防尘口罩、防毒面具）必须取得安全标志（LA）', '查阅防护用品的采购合同和计划，查阅发放登记账目、个人职业病防护用品领取记录，现场检查，询问劳动者', '未给劳动者提供个人使用的职业病防护用品、用人单位为劳动者个人提供的职业病防护用品不符合防治职业病的要求', '《职业病防治法》第23条、《有毒作业条例》第21条、《监督管理规定》第16条第1款', '《职业病防治法》第73条（二）项《有毒作业条例》第59条（六）项《监督管理规定》第51条（二）项', '《职业病防治法》《监督管理规定》给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下的的罚款；情节严重的，责令停止产生职业病危害作业,或者提请人民政府按照国务院规定的权限关闭《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1052', '1', '11.5 职业健康检查报告处理措施', '用人单位应当根据职业健康检查报告，采取下列措施：（一）对有职业禁忌的劳动者，调离或者暂时脱离原工作岗位；', '检查职业健康检查结果报告，查看职业禁忌调离名单、复查报告，查看调离通知单', '未根据职业健康检查情况采取相应措施的', '《健康监护监督管理办法》第17条（一）项', '《健康监护监督管理办法》第26条(五)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1053', '1', '2.2 变更申报', '用人单位工作场所、名称、法定代表人或者主要负责人发生变化的，自发生变化之日起15日内进行申报。', '查看工商执照和组织机构代码证。', '未申报变更职业病危害项目内容。', '《申报办法》第8条(三)项。', '《申报办法》第15条。', '责令限期改正，可以并处5千元以上3万元以下罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1054', '1', '11.9 如实提供职业健康检查文件、资料', '用人单位在委托职业健康检查机构对从事接触职业病危害作业的劳动者进行职业健康检查时，应当如实提供下列文件、资料：（一）用人单位的基本情况；（二）工作场所职业病危害因素种类及其接触人员名册；（三）职业病危害因素定期检测、评价结果', '检查职业健康监护制度、劳动者职业健康监护档案，与职业健康检查机构核实', '未如实提供职业健康检查文件、资料的', '《健康监护监督管理办法》第10条', '《健康监护监督管理办法》第26条(四)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1055', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收', '建设项目在竣工验收前，建设单位应当进行职业病危害控制效果评价', '检查建设项目清单，建设项目立项批文、评价单位的资质证明、职业病危害控制效果评价报告书', '未按照规定对职业病防护设施进行职业病危害控制效果评价', '《职业病防治法》第18条第3款、', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1056', '1', '4.4 高毒作业场所 应设置更衣室', '高毒作业场所应按规定设置更衣室更衣室应配置闭锁式衣柜更衣室中便服、工作服应分柜存放以避免工作服污染便服离开高毒作业场所时，应更换衣服，不可将工作服带出车间', '查看职业卫生档案、高毒物品作业场所台账、原辅材料清单、化学品安全数据说明书（MSDS），对照有毒有害物质清单，按照《高毒物品目录》，结合高毒作业劳动者名册，现场检查', '未为从事使用高毒物品作业的劳动者设置淋浴间、更衣室或者未设置清洗、存放和处理工作服、工作鞋帽等物品的专用间，或者不能正常使用', '《有毒作业条例》第27条', '《有毒作业条例》第69条（二）项', '《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1057', '1', '8.3 职业病危害作业转移 不得转嫁职业病危害作业', '任何单位和个人不得将产生职业病危害的作业转移给不具备职业病危害防护条件的单位和个人用人单位若将具有职业病危害的作业外包或对外协作时，应告知承包者或协作单位，其承包或协作的作业所存在的职业病危害以及应当采取的防护条件，并要求承包者或协作单位采取措施达到防护要求，如配置通风、除尘、防毒、消（隔）声、防暑、隔离等防护设施，或配备个人职业病防护用品如承包者或协作单位没有条件或不愿采取措施的，用人单位不能将具有职业病危害的作业外包或外协', '查阅档案，调查生产工艺流程缺失环节和岗位，查阅承包和外协协议书、用人单位职业病危害告知及应当采取防护措施的内容，对外包作业现场检查，对外协单位现场检查', '将产生职业病危害作业转移给没有职业病防护条件的单位和个人，或没有职业病防护条件的单位和个人接受产生职业病危害作业', '《职业病防治法》第32条、《监督管理规定》第26条', '《职业病防治法》第76条（五）项、《监督管理规定》第52条（五）项', '责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1058', '1', '3.2 建设项目职业病防护设施设计和审查', '建设项目职业病防护设施设计经审查同意后，建设项目的生产规模、工艺或者职业病危害因素的种类等发生重大变更的，建设单位应当根据变更的内容，重新进行职业病防护设施设计，并在变更之日起30日内按规定办理相应的审查手续。', '对照原防护设计，检查建设项目的生产规模、工艺或者职业病危害因素的种类等查看是否发生重大变更，查看职业病防护设施设计专篇、安全生产监督管理部门批准文件。', '建设项目选址、生产规模、工艺、职业病危害因素种类、职业病防护设施发生重大变更时，未对变更内容重新进行职业病防护设施设计并办理有关手续，进行施工的。', '《“三同时”监督管理暂行办法》第22条。', '《“三同时”监督管理暂行办法》第35条（二）项。', '给予警告，责令限期改正；逾期不改正的，处3万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1059', '1', '10.3 特殊岗位培训', '用人单位应当对职业病危害严重的岗位的劳动者，进行专门的职业卫生培训，经培训合格后方可上岗作业', '查阅培训记录和培训档案资料，查阅考核成绩', '使用未经培训考试合格的劳动者从事高毒等职业病危害严重作业的', '《有毒作业条例》第19条第3款、《监督管理条例》第10条第2款', '《有毒作业条例》第63条(一）项', '给予警告，责令限期改正；逾期不改正的，处5万元以上30万元以下的罚款；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1060', '1', '11.6 建立职业健康监护档案并妥善保管', '用人单位应当为劳动者个人建立符合要求的职业健康监护档案，并按照有关规定妥善保存职业健康监护档案包括下列内容：（一）劳动者姓名、性别、年龄、籍贯、婚姻、文化程度、嗜好等情况；（二）劳动者职业史、既往病史和职业病危害接触史；（三）历次职业健康检查结果及处理情况；（四）职业病诊疗资料；（五）需要存入职业健康监护档案的其他有关资料。 用人单位同时应建立相应的管理制度，责任到位，有人负责职业健康监护档案保存工作，并根据有关病案的保密原则，保护劳动者的隐私权劳动者或者其近亲属、劳动者委托的代理人有权查阅、复印劳动者的', '检查职业健康监护制度，借阅登记，复印记录，投诉记录', '未建立职业健康监护档案', '《职业病防治法》第37条、《有毒作业条例》第36条、《监督管理规定》第31条、《健康监护监督管理办法》第19条', '《职业病防治法》第72条（四）项、《有毒作业条例》第68条(七)项、《监督管理规定》第50条（四）项、《健康监护监督管理办法》第27条(一)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期改正，给予警告，可并处5万元以上10万元以下罚款。 《有毒作业条例》给予警告，责令限期改正，处2万元以上5万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1061', '1', '11.1 职业健康检查', '用人单位应承担职业健康检查费用', '通过询问当事人，查看生产经营管理或制造费用报表，职业卫生投入的列支情况，查询当地依法取得职业健康检查资质的医疗卫生机构', '不承担职业健康检查费用的', '《监督管理规定》第30条第2款、《健康监护监督管理办法》第8条', '《健康监护监督管理办法》第26条(六)项', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1062', '1', '11.8 应急健康检查', '发生急性职业病危害事故后，用人单位应及时组织救治遭受急性职业病危害的劳动者，同时应对遭受或可能遭受急性职业病危害的劳动者进行健康检查和医学观察可能遭受急性职业病危害的劳动者是指在发生急性职业病危害事故现场工作的、直接或间接接触了职业病危害因素的劳动者，或者是参与急性职业病危害事故应急救援而接触了职业病危害因素但未出现危害后果或危害后果不明显的劳动者出现下列情况之一的，用人单位应当立即组织有关劳动者进行应急职业健康检查：（一）接触职业病危害因素的劳动者在作业过程中出现与所接触职业病危害因素相关的不适症状的；', '检查职业健康监护制度，对照事故台账，检查事故档案、调查报告和劳动者职业健康监护档案', '对受到或者可能受到急性职业病危害的劳动者，未及时组织进行健康检查和医学观察的', '《职业病防治法》第38条第2款、《有毒作业条例》第34条、《监督管理规定》第35条第1款、《健康监护监督管理办法》第14条', '《职业病防治法》第72条(四)项、《有毒作业条例》第68条(六)项、《监督管理规定》第50条（四）项、《健康监护监督管理办法》第27条(一)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期改正，给予警告，可并处5万元以上10万元以下罚款《有毒作业条例》给予警告，责令限期改正，处2万元以上5万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1063', '1', '', '建设单位在职业病危害控制效果评价报告编制完成后，应当组织有关职业卫生专家对职业病危害控制效果评价报告进行评审', '查看职业病危害控制效果评价报告，评审会专家组成及评审会相关材料', '未按照规定对职业病危害控制效果评价报告进行评审的', '《“三同时”监督管理暂行办法》第27条第1款', '《“三同时”监督管理暂行办法》第35条（一）项', '给予警告，限期责令改正；逾期不改正的，处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1064', '1', '5.2 职业病危害因素检测', '存在职业病危害的用人单位，应当委托具有相应资质的职业卫生技术服务机构，每年至少进行一次职业病危害因素检测，高毒作业场所每月进行一次检测职业卫生技术服务机构所作检测应当客观、真实', '检查用人单位检测工作计划，查看服务合同、检测机构资质证明及检测报告，现场核对职业病危害因素检测点选择与报告的符合性', '未对工作场所职业病危害因素进行检测', '《职业病防治法》第27条第2、3款，《有毒作业条例》第26条第1、2款，《监督管理规定》第20条第1款', '《职业病防治法》第73条（四）项、《有毒作业条例》第59条（三）项、《监督管理规定》第51条（四）项', '《职业病防治法》、《监督管理规定》：给予警告，责令限期改正；逾期未改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭。 《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1065', '1', '2.2 变更申报', '进行新建、改建、扩建、技术改造或者技术引进建设项目的，自建设项目竣工验收之日起30日内进行申报。', '查阅安监部门对建设项目的批复文件、验收报告、职业病危害控制效果评价报告，现场检查。', '未申报变更职业病危害项目内容。', '《申报办法》第8条（一）项。', '《申报办法》第15条。', '责令限期改正，可以并处5千元以上3万元以下罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1066', '1', '4.7 冲洗设备', '急性职业损伤的有毒、有害工作场所配置冲洗设备.冲洗设备主要指冲眼器、流动水龙头以及冲淋设备在可能发生皮肤粘膜或眼睛灼伤、有腐蚀性、刺激性化学物质的工作场所应配备上述冲洗设备，特别强调冲洗设备应用取方便，且不妨碍工作，保证在发生事故时，劳动者能及时得到冲洗冲洗用水应安全并保证是流动水设置冲洗设备的地方应有明显标识，醒目易找冲洗设备应保证能正常使用，并应建立相应的管理制度，责任到位，有人负责，及时维修', '查阅职业卫生档案，确定发生急性职业损伤的有毒、有害工作场，检查冲洗设备的配备情况，检查定期检查记录、维修记录，现场检查冲洗设备使用是否有效', '可能发生急性职业损伤的有毒、有害工作场所未配置冲洗设备', '《职业病防治法》第26条第1款、《监督管理规定》第17条第1和2款', '《职业病防治法》第76条（三）项、《监督管理规定》第52条（三）项', '责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1067', '1', '8.6 供应商义务 提供设备的', '向用人单位提供可能产生职业病危害的设备时，应当提供中文说明书，并在设备醒目位置设置警示标识和中文警示说明警示说明中应载明设备性能、可能产生的职业病危害、安全操作和维护注意事项、职业病防护以及应急救治措施等内容', '核查用人单位生产、销售和使用可能产生职业病危害的设备的台账，查阅中文说明书、现场检查警示标识设置及中文警示说明', '向用人单位提供可能产生职业病危害的设备，未按照规定提供中文说明书或者设置警示标识和中文警示说明的', '《职业病防治法》第29条、《监督管理规定》第23条', '《职业病防治法》第74条、《监督管理规定》第54项', '责令限期改正，给予警告，并处五万元以上二十万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1068', '1', '11.3 在岗期间的职业健康检查', '用人单位应根据劳动者所从事的工种和工作岗位存在的职业病危害因素及其对人体健康的影响规律，对劳动者进行动态健康观察，按《职业健康监护技术规范》(GBZ188)等确定接触职业病危害的劳动者的检查项目和检查周期需要复查的，应当根据复查要求增加相应的检查项目', '检查职业健康监护制度、在岗劳动者名册，查阅在岗劳动者职业健康监护档案、承担职业健康检查的医疗卫生机构资质证明、委托书，职业健康检查体检结果报告', '未按照规定组织在岗期间的职业健康检查', '《职业病防治法》第36条第1款、《有毒作业条例》第32条第1款、《监督管理规定》第30条第1款、《健康监护监督管理办法》第13条', '《职业病防治法》第72条（四）项、《有毒作业条例》第68条(二)项、《监督管理规定》第50条第（四）项、《健康监护监督管理办法》第27条(一)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期改正，给予警告，可并处5万元以上10万元以下罚款。 《有毒作业条例》给予警告，责令限期改正，处2万元以上5万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1069', '1', '2.2 变更申报', '经过职业病危害因素检测、评价，发现原申报内容发生变化的，自收到有关检测、评价结果之日起15日内进行申报。', '查阅申报表，核对工作场所职业病危害因素检测和评价报告，现场检查接害岗位和接害种类。', '未申报变更职业病危害项目内容。', '《申报办法》第8条（四）项。', '《申报办法》第15条。', '责令限期改正，可以并处5千元以上3万元以下罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1070', '1', '11.12 职业禁忌证人员管理（续）', '职业健康监护应涵盖对职业禁忌证的处理应根据职业健康监护结果，按照国家的有关规定，对患有职业禁忌证的劳动者进行妥善处理如果是在上岗前体检发现的，不能安排患有职业禁忌证的劳动者从事其所禁忌的作业；如果是在岗期间发现的，应从劳动者禁忌的作业岗位调离', '检查职业健康监护制度、体检报告、职业健康监护档案、职业禁忌证名单、职业禁忌证处理记录档案、调离记录、妥善安置记录', '用人单位发现有职业禁忌或者有与所从事职业相关的健康损害的劳动者，未将其调离原工作岗位，并妥善安置的', '《职业病防治法》第36条第2款、《有毒作业条例》第32条第2款', '《有毒作业条例》第63条(三)项', '《有毒作业条例》给予警告，责令限期改正；逾期不改正的，处5万元以上30万元以下的罚款；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1071', '1', '6.3 防护设施检修与维护 维护、检修和检测', '职业病危害防护设施、设备应及时维护、检修，定期检测其性能和效果，并保证正常运行职业病防护设施对于保护劳动者的健康意义重大，如果不能正常运转势必影响防护效果，应建立相应的制度，明确维修的响应时间、责任人、维护周期，保证防护设施能正常运转', '查阅职业病危害防护设施日常运转记录、防护设施定期检查记录和防护设施使用维修记录等，现场检查职业病危害防护设施、设备和应急救援设施并核对防护设施台账', '对职业病危害防护设备、应急救援设施未按照规定进行维护、检修、检测，或不能保持正常运行、使用状态的', '《职业病防治法》第26条第3款、《监督管理规定》第18条', '《职业病防治法》第73条（三）项、《监督管理规定》第51条（三）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1072', '1', '14.1 拒绝安全生产监督管理部门依法履行监督检查', '', '', '', '《职业病防治法》第67条、《监督管理规定》第38条', '《职业病防治法》第73条（九）项、《监督管理规定》第51条（八）项', '给予警告，责令限期改正；逾期不改的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业，或提请人民政府关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1073', '1', '11.10 未成年工管理', '用人单位不得安排未成年工从事接触职业病危害的作业。 注：未成年工指年满十六周岁、未满十八周岁的劳动者', '检查劳动者名册、职业健康监护制度，检查职工档案，询问管理人员，现场核实', '安排未成年人从事接触职业病危害的作业', '《职业病防治法》第39条、《有毒作业条例》第7条、《监督管理规定》第33条、《健康监护监督管理办法》第12条第2款', '《职业病防治法》第76条(七)项、《有毒作业条例》第63条第(四)(五)项、《监督管理规定》第52条(七)项、《健康监护监督管理办法》第29条(二)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止作业或者提请人民政府关闭。 《有毒作业条例》给予警告，责令限期改正；逾期不改正的，处2万元以上5万元以下的罚款；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1074', '1', '12.2疑似职业病病人诊断', '用人单位应及时安排对疑似职业病病人进行诊断,在疑似职业病病人诊断或者医学观察期间，用人单位不得解除或者终止与其订立的劳动合同', '按照劳动者名册检查劳动者体检报告，查看疑似职业病病人诊断书等，询问当事人，查询当地职业病诊断机构', '未及时安排对疑似职业病病人进行诊断的', '《职业病防治法》第56条第2款', '《职业病防治法》第73条（六）项、《健康监护监督管理办法》第28条(一)项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1075', '1', '11.5 职业健康检查报告处理措施', '（四）对疑似职业病病人，按照职业健康检查机构的建议安排其进行医学观察或者职业病诊断；', '检查职业健康检查结果报告，对照疑似职业病病人名单查看劳动者职业健康监护档案', '未根据职业健康检查情况采取相应措施的', '《健康监护监督管理办法》第17条（四）项', '《健康监护监督管理办法》第26条(五)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1076', '1', '1.3 职业病防治计划和实施方案', '用人单位应当制定职业病防治计划和实施方案，明确职业病防治工作的目标，措施以及保障条件等内容。', '检查用人单位年度职业病防治计划和实施方案，及贯彻执行情况。', '未制定职业病防治计划和实施方案。', '《职业病防治法》第21条（二）项、《监督管理规定》第11条。', '《职业病防治法》第71条（二）项、《监督管理规定》第49条（一）项。', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1077', '1', '8.3 职业病危害作业转移 不得接受职业病危害作业', '不具备职业病危害防护条件的单位和个人不得接受产生职业病危害的作业用人单位若承包具有职业病危害的作业时应要求发包商书面告知工作场所存在的职业病危害以及相关的防护要求，并采取措施达到防护条件若达不到相应条件，用人单位不能承包该作业', '查阅档案，调查生产工艺流程缺失环节和岗位，查阅承包和外协协议书、用人单位职业病危害告知及应当采取防护措施的内容，对外包作业现场检查，对外协单位现场检查', '将产生职业病危害作业转移给没有职业病防护条件的单位和个人，或没有职业病防护条件的单位和个人接受产生职业病危害作业', '', '', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1078', '1', '11.1 职业健康检查', '用人单位应当组织劳动者进行职业健康检查用人单位应委托依法取得相应资质的医疗卫生机构根据劳动者拟从事的工种和工作岗位，按《职业健康监护技术规范》(GBZ188)、《放射工作人员职业健康监护技术规范》（GBZ235）等国家职业卫生标准的要求，确定特定的健康检查项目和检查周期，进行职业健康检查劳动者接受职业健康检查应当视同正常出勤', '检查职业健康监护制度、年度计划、实施方案、职业健康检查机构委托书、职业卫生投入的列支情况，对照劳动者名册，查阅劳动者职业健康监护档案，体检报告', '安排未经职业健康检查的劳动者从事接触职业病危害作业的', '《职业病防治法》第36条第1款、《监督管理规定》第30条、《健康监护监督管理办法》第8条', '《职业病防治法》第72条（四）项、《监督管理规定》第50条(四)项、《健康监护监督管理办法》第27条(一)项', '责令限期改正，给予警告，可以并处5万元以上10万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1079', '1', '9.4 职业健康检查结果告知', '对从事接触职业病危害作业的劳动者，用人单位应按照国务院安全生产监督管理部门的规定组织上岗前、在岗期间、离岗时和应急职业健康检查，并将检查结果以书面形式如实告知劳动者', '查阅体检报告，查阅书面告知书，查阅职业健康档案，询问劳动者', '未将职业健康检查结果如实告知劳动者', '《职业病防治法》第36条第1款、《监督管理规定》第30条', '《职业病防治法》第72条（四）项、《监督管理规定》第50条（四）项', '责令限期改正，给予警告，可以并处5万元以上10万元以下罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1080', '1', '10.2 劳动者培训 上岗前的职业卫生培训', '用人单位应对上岗前或变更工艺、技术、设备或材料，或者岗位调整导致劳动者接触的职业病危害因素发生变化的，应当对劳动者进行上岗前职业卫生培训培训内容包括职业卫生法律、法规、规章、操作规程、所在岗位的职业病危害及其防护设施、个人职业病防护用品的使用和维护、应急救援知识、劳动者所享有的职业卫生权利等内容应做好记录及存档工作，存档内容包括培训通知、教材、教员、试卷、考核成绩等，档案资料应有专人负责保管', '查阅人事工作与季度工作计划 ，核查新招工人、转岗工人人数与去向，查阅培训通知和培训计划，查阅培训签到簿、记录和培训教材、培训教员、考核成绩等培训档案资料，现场询问劳动者', '未按照规定组织劳动者进行职业卫生培训', '《职业病防治法》第35条第2款、《有毒作业条例》第19条第2款、《监督管理规定》第10条第1款、3款', '《职业病防治法》第71条（四）项、《监督管理规定》第49条（七）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1081', '1', '7.2 有毒作业的防护（续） 高毒作业维护检修时的防护', '维护、检修存在高毒物品的生产装置时必须事先制订维护、检修方案并严格按照维护、检修方案和操作规程进行维修、检修现场应当有专人监护并设置警示标志', '查阅职业卫生档案和认定的高毒作业场所，查阅用人单位的生产装置维护与检修计划，查阅进入存在高毒物品场所工作方案和操作规程及工作记录，现场检查', '未依照本条例的规定维护、检修存在高毒物品的生产装置的', '《有毒作业条例》第24条', '《有毒作业条例》第61条（二）项', '责令限期改正，处5万元以上30万元以下的罚款；逾期不改正的，提请人民政府按照国务院规定的权限关闭；造成严重职业中毒危害事故的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1082', '1', '4.10 生产、销售、使用、贮存放射性同位素和射线装置的场所', '应设置警示标识，警示标识的设置按照GBZ158使用指南设定警示标识包括图形标识（禁止、警告、指令和提示标识）、警示线（红、黄、绿）、警示语句和放射工作场所和放射源储存场所职业病危害告知卡。 设置安全和防护设施以及必要的防护安全联锁、报警装置或者工作信号放射性装置的生产调试和使用场所，应当具有防止误操作、防止工作人员受到意外照射的安全措施用人单位必须配备与辐射类型和辐射水平相适应的防护用品和监测仪器，包括个人剂量测量报警、固定式和便携式辐射监测、表面污染监测、流出物监测等设备，并保证可能接触放射线的工作人员', '查阅职业卫生档案确定生产、销售、使用、贮存放射性同位素和射线装置的场所，按照GBZ158使用指南和GBZ/T 203设置进行现场核对，检查工业探伤（γ射线工业、X射线）报警装置（声光的或剂量的）、亮灯或响铃等方式报警检查报警装置的国家质量监督检验合格证及配备档案、定期检查、维修记录，现场检查个人剂量计测定值及有效校正期限', '生产、销售、使用、贮存放射性同位素和射线装置的场所，未设置警示标识、防护安全联锁、报警装置、防护用品、监测仪器、个人剂量计', '《职业病防治法》第26条第2款、 《监督管理规定》第17条第四款', '《职业病防治法》第76条（三）项、《监督管理规定》第52条（三）项', '《职业病防治法》、《监督管理规定》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1083', '1', '12.1 职业病及疑似职业病病人报告', '用人单位应建立职业病报告制度，责任到位，有人负责，职业健康监护中出现新发生职业病（职业中毒）或者两例以上疑似职业病（职业中毒）的，用人单位应当及时向所在地安全生产监督管理部门报告不得虚报、漏报、拒报、迟报、伪造和篡改', '检查职业健康监护制度、劳动者职业健康监护档案，对照职业病、疑似职业病病人名单，与所在地职业病诊断机构核对', '未按照规定报告职业病、疑似职业病病人的', '《职业病防治法》第51条、《监督管理规定》第36条、《健康监护监督管理办法》第18条', '《职业病防治法》第75条、《监督管理规定》第55条、《健康监护监督管理办法》第30条', '责令限期改正，给予警告，可以并处1万元以下的罚款；弄虚作假的，并处2万元以上5万元以下的罚款；对直接负责的主管人员和其他直接责任人员，可以依法给予降级或者撤职的处分', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1084', '1', '4.4 高毒作业场所 应设置车间淋浴间', '高毒作业应设置车间淋浴间．男女分别设置，淋浴间由更衣间、浴室和管理间组成淋浴间内部构造应易于使用清扫卫生设备，并采取防水、防潮、排水和排气措施应设置不断水的供水设备并保证用水卫生淋浴器的数量应根据高毒作业的人数确定，一般4-8人设1个淋浴器', '查看职业卫生档案、高毒物品作业场所台账、原辅材料清单、化学品安全数据说明书（MSDS），对照有毒有害物质清单，按照《高毒物品目录》，结合高毒作业劳动者名册，现场检查', '未为从事使用高毒物品作业的劳动者设置淋浴间、更衣室或者未设置清洗、存放和处理工作服、工作鞋帽等物品的专用间，或者不能正常使用', '《有毒作业条例》第27条', '《有毒作业条例》第69条（二）项', '《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1085', '1', '6.3 防护设施检修与维护  擅自拆除', '不得擅自拆除、停止使用职业病防护设备或应急救援设施', '查阅职业病危害防护设施日常运转记录、防护设施定期检查记录和防护设施使用维修记录等，现场检查职业病危害防护设施、设备和应急救援设施并核对防护设施台账', '擅自拆除、停止使用职业病危害防护设备或应急救援设施的', '', '《职业病防治法》第76条（六）项、《监督管理规定》第52条（六）项', '责令限期治理，并处5万元以上30万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1086', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收', '建设单位对职业病危害控制效果评价报告的真实性和合法性负责', '查看职业病危害控制效果评价报告，评审会专家组成及评审会相关材料', '建设单位在职业病危害控制效果评价报告评审中弄虚作假的', '《“三同时”监督管理暂行办法》第27条第2款', '《“三同时”监督管理暂行办法》第36条', '责令改正，并处5千元以上3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1087', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收', '职业病危害一般的建设项目竣工验收时，由建设单位自行组织职业病防护设施的竣工验收，并自验收完成之日起30日内按照规定向安全生产监督管理部门申请职业病防护设施竣工备案提交下列文件、资料：（一）建设项目职业病防护设施竣工备案申请书；（二）建设项目职业病危害预评价报告备案通知书（复印件）；（三）建设项目立项审批文件（复印件）；（四）建设项目职业病防护设施设计专篇；（五）建设项目职业病危害控制效果评价机构的资质证明（影印件）；（六）建设项目职业病危害控制效果评价报告；（七）职业卫生专家对职业病危害控制效果评价报告的', '查看安全生产监督管理部门职业病防护设施竣工备案回执等相关文件', '职业病危害一般的建设项目竣工验收后，未经安全监管部门备案同意，擅自投入使用的', '《职业病防治法》第18条第3款、 《有毒作业条例》第13条第1款、 《监督管理规定》第14条、《“三同时”监督管理暂行办法》第28条', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭。 《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1088', '1', '9.2 现场告知 警示标识和说明', '存在或产生职业病危害的工作场所、作业岗位、设备、设施，应当按照《工作场所职业病危害警示标识》(GBZ158) 规定，在醒目位置设置图形（禁止、警告、指令和提示标识）、警示线（红、黄、蓝）、警示语句等警示标识和中文警示说明警示说明应当载明产生职业病危害的种类、后果、预防和应急处置措施等内容', '查阅有毒、有害工作场所警示标识设置情况一览表、查阅职业卫生档案与职业病危害项目申报表、现场检查警示标识设置及中文警示说明', '未按照规定在产生严重职业病危害的作业岗位醒目位置设置警示标识和中文警示说明', '《职业病防治法》第25条第2款、《有毒作业条例》第12条、《监督管理规定》第15条第2款', '《职业病防治法》第73条（八）项、《有毒作业条例》第59条（一）、（五）项、《监督管理规定》第51条（七）项', '《职业病防治法》《监督管理规定》给予警告，责令限期改正，逾期未改的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭。 《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1089', '1', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收', '建设单位在职业病危害控制效果评价报告编制完成后，应当组织有关职业卫生专家对职业病危害控制效果评价报告进行评审', '查看职业病危害控制效果评价报告，评审会专家组成及评审会相关材料', '未按照规定对职业病危害控制效果评价报告进行评审的', '《“三同时”监督管理暂行办法》第27条第1款', '《“三同时”监督管理暂行办法》第35条（一）项', '给予警告，限期责令改正；逾期不改正的，处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1090', '1', '12.3 疑似职业病人诊疗费用', '用人单位应承担疑似职业病人在诊断、医学观察期间的费用', '询问当事人，查询当地职业病诊断机构', '未按规定承担职业病诊断、鉴定费用和职业病病人的医疗、生活保障费用的', '《职业病防治法》第56条第3款', '《职业病防治法》第73条(十一)项、《监督管理规定》第51条(十)项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1091', '1', '8.2 明令禁止使用', '任何单位和个人不得使用国家明令禁止使用的可能产生职业病危害的设备和材料', '查阅生产工艺流程图、原材料采购计划和库存原材料清单、生产设备台账、有毒有害物质清单，查阅危险化学品管理档案和化学品安全数据说明书（MSDS），查阅职业卫生档案，现场检查', '使用国家明令禁止使用的可能产生职业病危害的设备或材料', '《职业病防治法》第31条、《有毒作业条例》第4条、《监督管理规定》第25条', '《职业病防治法》第76条第（四）项、《有毒作业条例》第62条、《监督管理规定》第52条（四）项', '《职业病防治法》《监督管理规定》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭。 《有毒作业条例》责令立即停止使用，处5万元以上30万元以下的罚款；情节严重的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于危险物品肇事罪、重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1092', '1', '9.1 合同告知', '用人单位与劳动者订立劳动合同时，应当如实告知工作过程中可能存在或产生职业病危害及其后果、职业病防护措施和待遇等并在劳动合同中写明，不得隐瞒或欺骗劳动者在已订立劳动合同期间因工作岗位或工作内容变更，劳动者接触的职业病危害变化时，用人单位应如实告知，并协商变更原劳动合同用人单位违反前款规定，劳动者有权拒绝从事存在职业病危害的作业，用人单位不得因此解除与劳动者所订立的劳动合同', '查阅接害工人名录、劳动合同，现场询问', '订立或变更劳动合同时未告知劳动者职业病危害真实情况的', '《职业病防治法》第34条、《有毒作业条例》第18条、《监督管理规定》第29条', '《职业病防治法》第72条（三）项、《有毒作业条例》第68条（九）项、《监督管理规定》第50条（三）项', '《职业病防治法》《监督管理规定》责令限期改正，给予警告，可并处5万元以上10万元以下罚款。 《有毒作业条例》给予警告，责令限期改正，处2万元以上5万元以下罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请人民政府按照国务院规定的权限予以关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1093', '1', '8.6 供应商义务  提供化学品及放射性材料的', '向用人单位提供可能产生职业病危害的化学品、放射性同位素和含放射性物质的材料的，应提供中文说明书说明书应当载明产品特性、主要成份、存在的有害因素、可能产生的危害后果、安全使用注意事项、职业病防护以及应急救治措施等内容  产品包装应当有醒目的警示标识和中文警示说明', '核查用人单位购进或售出可能产生或存在职业病危害的化学品、放射性同位素和含放射性物质台账、要求用人单位提供采购物质的中文MSDS （化学品安全技术说明书）、现场检查储存处、产品包装设置的警示标识及中文警示说明', '向用人单位提供可能产生职业病危害的材料，未按照规定提供中文说明书或者设置警示标识和中文警示说明的', '《职业病防治法》第30条第1款、《监督管理规定》第24条', '', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1094', '1', '11.2 上岗前的职业健康检查', '拟从事接触职业病危害作业的新录用劳动者和拟从事有特殊健康要求作业的劳动者，包括转岗到该作业岗位的劳动者在上岗前，用人单位应委托依法取得相应资质的医疗卫生机构根据劳动者拟从事的工种和工作岗位，按《职业健康监护技术规范》(GBZ188)等确定特定的健康检查项目，进行职业健康检查', '检查职业健康监护制度，新录用、拟从事有特殊健康要求作业的劳动者、变更工作岗位或工作内容的劳动者名册，查阅上岗前劳动者职业健康检查报告、承担职业健康检查的医疗卫生机构资质证明、委托书', '未按照规定组织上岗前的职业健康检查', '《职业病防治法》第36条第1、2款，《有毒作业条例》第31条第1、2款，《监督管理规定》第30条第1款，《健康监护监督管理办法》第11、12条', '《职业病防治法》第76条（七）项、《有毒作业条例》第68条(一)项、《监督管理规定》第52条第（七）项、《健康监护监督管理办法》第29条(一)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止作业或者提请人民政府关闭。 《有毒作业条例》给予警告，责令限期改正；逾期不改正的，处2万元以上5万元以下的罚款；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1095', '1', '7.10 噪声与振动防护', '产生噪声较大的设备尽量将噪声源与操作人员隔离，工艺允许远距离控制的，设置隔声操作（控制）室产生强烈振动的车间，设置防止振动传播的措施', '查阅职业卫生档案、认定的产生噪声与振动的设备和工作场所，查阅防噪、降噪减振技术改造计划及其实施情况的工作总结，查阅职业病危害监测、检测和评价报告，现场检查', '没有提供职业病防护设施或提供的职业危害防护设施不符合国家标准、行业标准', '《职业病防治法》第15条（二）项、《监督管理规定》第12条（三）项', '《职业病防治法》第73条（二）项、《监督管理规定》第51条(二）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业，或提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1096', '1', '9.2 现场告知  公告栏', '产生职业病危害的用人单位应当在醒目位置设置公告栏，公布职业病危害防治规章制度、操作规程、职业病危害事故应急救援措施', '查阅职业卫生管理制度是否包含应当公示的内容和必须公示的规定，现场检查公告设置及公告内容', '未按照规定公布有关职业病防治的规章制度、操作规程、职业病危害事故应急救援措施的', '《职业病防治法》第25条第1款、《监督管理规定》第15条第1款', '《职业病防治法》第71条（三）项、《监督管理规定》49条（六）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1097', '1', '11.1 职业健康检查', '用人单位应当组织劳动者进行职业健康检查用人单位应委托依法取得相应资质的医疗卫生机构根据劳动者拟从事的工种和工作岗位，按《职业健康监护技术规范》(GBZ188)、《放射工作人员职业健康监护技术规范》（GBZ235）等国家职业卫生标准的要求，确定特定的健康检查项目和检查周期，进行职业健康检查劳动者接受职业健康检查应当视同正常出勤', '检查职业健康监护制度、年度计划、实施方案、职业健康检查机构委托书、职业卫生投入的列支情况，对照劳动者名册，查阅劳动者职业健康监护档案，体检报告', '弄虚作假，指使他人冒名顶替参加职业健康检查的', '《健康监护监督管理办法》第9条', '《健康监护监督管理办法》第26条(三)项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1098', '1', '6.6 防护用品检修与维护', '个人使用的职业病防护用品应当进行经常性检修、维护，并定期检测防护性能和效果，确保其处于正常状态', '查阅督促使用检查记录及维修检测记录，查阅培训记录，询问劳动者，现场检查', '个人使用的职业病防护用品未按照规定进行经常性维护、检修、检测，或者不能保持正常运行、使用状态的', '《职业病防治法》第26条第3款、《有毒物品条例》第19条第2款、《监督管理规定》第16条第2款', '《职业病防治法》第73条（三）项、《有毒作业条例》第59条（六）项、《监督管理规定》第51条（三）项', '《职业病防治法》《监督管理规定》给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下的的罚款；情节严重的，责令停止产生职业病危害作业或者提请人民政府按国务院规定的权限关闭。 《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1099', '1', '4.4 高毒作业场所 应设置有毒物品存放专用间', '有毒物品应实行分类存放对于高毒物品，应根据生产条件、所使用化学品的理化特性和用量来考虑有毒物品存放专用间设置的位置、大小和选材。 有毒物品存放专用间应在醒目的位置设置明显的警示标识，其内部存放的物品不能相互发生燃烧、爆炸等化学反应应建立相应的制度，明确相关人员负责有毒物品存放专用间的日常管理，并保证无关人员不能进入物品存放专用间', '', '', '', '', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1100', '1', '7.1 粉尘作业的防护', '对于逸散粉尘的生产过程，应对产尘设备采取密闭措施；设置适宜的局部排风除尘设施对尘源进行控制；作业场所应配备负压式清扫设备和冲洗地面、墙壁的设施，以防止二次扬尘；生产工艺和粉尘性质可采取湿式作业的，应采取湿法抑尘当湿式作业仍不能满足卫生要求时，应采取其他通风、除尘方式使劳动者活动的工作场所有害物质浓度符合GBZ2.1要求', '查阅生产工艺流程图、职业病危害检测和评价报告，查阅职业卫生档案和防尘设备台账，现场检查', '未提供职业病防护设施和个人使用的职业病防护用品，或者提供的职业病防护设施和个人使用的职业病防护用品不符合国家职业卫生标准和卫生要求的', '《职业病防治法》第23条、《监督管理规定》第12条（三）项、第16条（一）项', '《职业病防治法》第73条（二）项、《监督管理规定》第51条(二）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害作业或者提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1101', '1', '4.9 必要的泄险区', '根据生产条件、所使用化学品的理化特性和用量考虑泄险区设置的位置、大小和选材泄险区周围不能存在可能与排放到泄险区的有毒有害物质发生易燃、易爆等化学反应的物质，泄险区四周的选材不应与泄险物发生反应，泄露物质和冲洗水应纳入工业废水处理系统。 应在泄险区周围的醒目位置设置明显的警示标识以及中文警示说明定期泄险要在中文警示说明中说明定期泄险的时间、泄险的物质和注意事项；事故性泄险应制定泄险预案，明确泄险的条件、泄险命令的发布人、泄险时如何进行人群疏散、泄险物质的无害化处理、消除发生次生事故的危险、泄险后的善后处理工', '查阅职业卫生档案，现场调查认定需设置的场所，检查泄险区管理文件，泄险区设置是否合理、可行，现场查看醒目位置是否设置泄险区警示标识或中文警示说明，中文警示说明内容是否完整', '可能产生急性职业损伤的有毒、有害工作场所，未设置撤离通道和泄险区', '《职业病防治法》第26条第1款、 《有毒作业条例》第11条第2 款（四）项、《监督管理规定》第17条第1款', '《职业病防治法》第76条（三）项、《有毒作业条例》第59条（四）项、《监督管理规定》第52条（三）项', '《职业病防治法》、《监督管理规定》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭                                《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1102', '1', '8.5 首次使用的化学材料管理', '国内首次使用或者首次进口与职业病危害有关的化学材料，使用单位或进口单位按照国家规定经国务院有关部门批准后，应当向国务院卫生行政部门、安全生产监管部门报送该化学材料的毒性鉴定以及经有关部门登记注册或者批准进口的文件等资料', '查阅生产计划、技改计划和化学材料采购计划，查阅毒性鉴定资料及批准进口批文，对首次使用的化学材料现场检查', '国内首次使用或者首次进口与职业病危害有关的化学材料，未按规定报送毒性鉴定资料以及经有关部门登记注册或者批准进口的文件的', '《职业病防治法》第30条第2款', '《职业病防治法》第71条（五）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1103', '1', '4.5 自动报警装置和事故通风设施', '可能突然泄漏大量有毒物品或者易造成急性中毒的作业场所，设置自动报警装置和事故通风设施，并有效联动。 具体有毒有害物质的确定参考《高毒物品目录》及《工作场所有害因素职业接触限值》(GBZ2.1、GBZ2.2）。 通风换气次数不小于12次/h，报警装置依据《工作场所有毒气体检测报警装置设置规范》（GBZ/T223），必须经相关部门检定通过，并应建立相应的制度，责任到位，有人负责，班前及定期检查，及时维修保证报警装置能够正常运转', '查阅职业卫生档案、职业病危害项目申报表，认定相应的易发生急性中毒作业场所，检查报警装置和通风设施的国家质量监督检验合格证及配备档案，班前、定期检查、维修记录，现场检查', '可能产生急性职业损伤的有毒、有害工作场所，未设置自动报警装置和事故通风设施', '《职业病防治法》第26条第1款、 《有毒作业条例》第11条第2款（三）项、《监督管理规定》第17条第1、3、4款', '《职业病防治法》第76条（三）项、《有毒作业条例》第60条（一）项、《监督管理规定》第52条（三）项', '《职业病防治法》、《监督管理规定》责令限期治理，并处5万元以上30万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭。 《有毒作业条例》给予警告，责令限期改正，处5万元以上30万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪、重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1104', '1', '13.3 职业病危害事故处理', '安全生产监督部门接到报告后，应当及时会同有关部门组织调查处理；必要时，可以采取临时控制措施事故调查处理应当坚持实事求是、尊重科学的原则，及时、准确地查清事故经过、事故原因和事故损失，查明事故性质，认定事故责任，总结事故教训，提出整改措施，并对事故责任者依法追究责任', '查明事故发生的经过、原因、人员伤亡情况及直接经济损失；认定事故的性质和事故责任；提出对事故责任者的处理建议；总结事故教训，提出防范和整改措施；提交事故调查报告', '违反《职业病防治法》规定，造成重大职业病危害事故或者其他严重后果，构成犯罪的', '《职业病防治法》相关规定', '《职业病防治法》第79条、《监督管理规定》第53条第2款', '对直接负责的主管人员和其他直接责任人员，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1105', '1', '3.1 建设项目职业病危害预评价', '建设单位对职业病危害预评价报告的真实性、合法性负责。', '检查职业病危害预评价报告、评价单位资质证明、评审专家组成、评审会相关材料。', '建设单位在职业病危害预评价报告评审中弄虚作假的。', '《“三同时”监督管理暂行办法》第11条第2款。', '《“三同时”监督管理暂行办法》第36条。', '责令改正，并处5千元以上3万元以下的罚款。', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1106', '1', '11.12 职业禁忌证人员管理', '职业健康监护应涵盖对职业禁忌证的处理应根据职业健康监护结果，按照国家的有关规定，对患有职业禁忌证的劳动者进行妥善处理如果是在上岗前体检发现的，不能安排患有职业禁忌证的劳动者从事其所禁忌的作业；如果是在岗期间发现的，应从劳动者禁忌的作业岗位调离', '检查职业健康监护制度、体检报告、职业健康监护档案、职业禁忌证名单、职业禁忌证处理记录档案、调离记录、妥善安置记录', '安排有职业禁忌的劳动者从事其所禁忌的作业', '《职业病防治法》第36条第2款、《有毒作业条例》第31条第2款、《监督管理规定》第33条、《健康监护监督管理办法》第12条第1款', '《职业病防治法》第76条(七)项、《有毒作业条例》第63条(二)项、《监督管理规定》第52条(七)项、《健康监护监督管理办法》第29条(四)项', '《职业病防治法》《监督管理规定》《健康监护监督管理办法》责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止作业或者提请人民政府关闭。 《有毒作业条例》给予警告，责令限期改正；逾期不改正的，处2万元以上5万元以下的罚款；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1107', '1', '5.4 检测、评价结果处理', '用人单位应当落实职业病危害检测、现状评价报告中提出的建议和措施，并将检测、评价报告及整改情况存入用人单位的职业卫生档案，定期向所在地安全生产监督管理部门报告并向劳动者公布', '查看职业卫生档案和安监部门的备案记录，检测、评价报告复印件，现场检查公告栏', '2、隐瞒、伪造、篡改、毁损工作场所职业病危害因素检测评价结果', '', '《职业病防治法》第73条（十）项、《监督管理规定》第51条(九)项', '给予警告，责令限期改正；逾期未改正的，处5万元以上20万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1108', '1', '4.3 工作场所与生活场所分开', '工作场所与生活场所分开，工作场所不得住人依据《工业企业设计卫生标准》（GBZ1），生活场所包括：工作场所办公室、生产卫生室（浴室、存衣室、盥洗室、洗衣房），生活室（休息室、食堂、厕所）、女工卫生室等辅助用室使用有毒物品工作场所与生活场所分开，工作场所不得住人', '查看职业卫生档案，认定使用有毒物品场所，现场检', '工作场所未与生活场所分开或者工作场所住人的', '《有毒作业条例》第11条第2款（一）项、《监督管理规定》第12条（二）项', '《有毒作业条例》第66条（一）项、《监督管理规定》第48条（一）项', '《监督管理规定》给予警告，责令限期改正，可以并处5千元以上2万元以下的罚款。 《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪、危险物品肇事罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1109', '1', '5.5 职业病危害因素超标处理', '用人单位在日常的职业病危害监测或者定期检测、现状评价过程中，发现工作场所职业病危害因素不符合国家职业卫生标准和卫生要求时，应当立即采取相应治理措施，确保其符合职业卫生环境和条件的要求；仍然达不到国家职业卫生标准和卫生要求的，必须停止存在职业病危害因素的作业；职业病危害因素经治理后，符合国家职业卫生标准和卫生要求的，方可重新作业', '查看职业卫生档案，监测、检测和现状评价报告，超标岗位治理建议和计划，有关领导或部门提出的治理批复、治理方案和采取的治理措施，治理后的检测报告', '工作场所职业病危害因素经治理仍不符合国家职业卫生标准和卫生要求而未停止作业', '《职业病防治法》第27条第4款、《有毒作业条例》第26条第3款、《监督管理规定》第22条', '《职业病防治法》第73条（五）项、《有毒作业条例》第61条（一）项、《监督管理规定》第51条（五）项', '《职业病防治法》《监督管理规定》给予警告，责令限期改正；逾期未改正的，处5万元以上20万元以下罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭                        《有毒作业条例》给予警告，责令限期改正，处5万元以上20万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪或者其他罪的规定，依法追究刑', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1110', '1', '7.5 放射工作场所防护', '生产、销售、使用放射性同位素和射线装置场所的入口处，应当按照国家有关安全和防护标准的要求，设置安全和防护设施以及必要的防护安全联锁、报警装置或者工作信号放射性装置的生产调试和使用场所，应当具有防止误操作、防止工作人员受到意外照射的安全措施用人单位必须配备与辐射类型和辐射水平相适应的防护用品和监测仪器，包括个人剂量测量报警、固定式和便携式辐射监测、表面污染监测、流出物监测等设备，并保证可能接触放射线的工作人员佩戴个人剂量计', '查阅职业卫生档案，查阅有关场所的防护设施、报警设施和监测仪器台账，查阅有关工作场所防护用品采购计划和发放、领取记录，查阅辐射检测和个人剂量检测报告，现场检查', '未按照规定在放射工作场所配置防护设备、报警装置和个人剂量计的', '《职业病防治法》第26条第2款、《监督管理规定》第17条第4款', '《职业病防治法》第76条（三）项、《监督管理规定》第52条（三）项', '责令限期治理，并处5万元以上30万元以下罚款；情节严重的，责令停止产生职业病危害作业，或者提请人民政府按照国务院规定的权限关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1111', '1', '12.4 职业病病人治疗、康复', '用人单位应当按照国家有关规定，安排职业病病人进行治疗、康复和定期检查', '询问当事人，查询当地职业病诊断机构', '未按照国家有关规定，安排职业病病人进行治疗、康复和定期检查', '《职业病防治法》第57条第2款', '《职业病防治法》第73条（六）项', '给予警告，责令限期改正，逾期不改正的，处5万元以上20万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1112', '1', '', '建设项目没有进行试运行的，应当在其完工后委托具有相应资质的职业卫生技术服务机构进行职业病危害控制效果评价建设单位应当为评价活动提供符合检测、评价标准和要求的受检场所、设备和设施', '', '', '《监督管理规定》第14条、《“三同时”监督管理暂行办法》第26条', '', '', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('1113', '1', '7.3 密闭作业的防护  泄漏或逸出有毒有害物质密闭作业防护', '在可能突然泄漏或者逸出大量有害物质的密闭或者半密闭工作场所，用人单位应当设置报警装置，配置现场急救用品、冲洗设备、应急撤离通道和必要的泄险区现场急救用品、冲洗设备等应当设在可能发生急性职业损伤的工作场所或者临近地点，并在醒目位置设置清晰的标识在可能突然泄漏或者逸出大量有害物质的密闭或者半密闭工作场所，用人单位还应当安装事故通风装置以及与事故排风系统相连锁的泄漏报警装置', '查阅职业卫生档案，认定的密闭作业场所、查阅报警装置、应急救援设施和事故通风装置的台账和现场急救用品的清单，现场检查', '可能发生急性职业损伤的有毒、有害工作场所不符合《职业病防治法》第26条规定的', '《职业病防治法》第26条第1款、《有毒作业条例》第11条（三）项、《监督管理规定》第17条第1、2、3款', '《职业病防治法》第76条（三）项、《有毒作业条例》第60条（一）项、《监督管理规定》第52条（三）项', '《职业病防治法》《监督管理规定》责令限期治理，并处5万元以上30万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请人民政府按照国务院规定的权限责令关闭 《有毒作业条例》给予警告，责令限期改正，处5万元以上30万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大责任事故罪、重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '2,3,4,5', '', NULL, '', '', NULL);
INSERT INTO `t_check_list` VALUES ('c63ad42f-5bbf-4bd8-a7f9-6d0574542d1c', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '是否按规定进行全员安全生产教育培训', '对涉粉尘岗位新进从业人员、离岗６个月以上的或者换岗的从业人员,及时进行上岗前安全生产教育和培训；对在岗人员应当定期组织安全生产再教育培训活动', '查阅企业员工名单、培训记录、安全培训档案；现场随机抽取涉粉尘岗位3人询问培训情况', '未按规定对从业人员进行安全生产教育培训', '《安全生产法》第二十五条，《山东省生产经营单位安全生产主体责任规定》（山东省人民政府令第303号）第二十四条', '《安全生产法》第九十四条第三项', '责令限期改正，可以处五万元以下的罚款', '2,5', '已按规定对从业人员进行安全生产教育培训', 4, '教育培训', '企业员工名单、培训记录、安全培训档案；现场随机抽取3人询问培训情况', NULL);
INSERT INTO `t_check_list` VALUES ('8a3cbf73-8093-448e-aeb9-112d5374ce38', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '是否组织制定安全生产规章制度和操作规程', '企业制定的安全生产管理制度应当涵盖303号令中规定的安全生产会议等13项制度，制度中还应有粉尘定期清扫制度、除尘系统使用维护制度', '查看安全生产管理制度、安全操作规程以及内容设置情况', '未组织制定安全生产规章制度和操作规程', '《安全生产法》第十八条第二项，《山东省生产经营单位安全生产主体责任规定》（山东省人民政府令第303号）第七条', '《安全生产事故隐患排查治理暂行规定》（国家安全生产监督管理总局令第16号）第二十六条第六项', '给予警告，并处三万元以下的罚款', '2,5', '已组织制定安全生产规章制度和操作规程', 3, '安全生产规章制度', '询问笔录、现场检查记录', NULL);
INSERT INTO `t_check_list` VALUES ('5ba091ca-3070-418f-b7a1-72573579879c', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '是否设置安全生产管理机构或配备安全生产管理人员', '涉粉尘爆炸的工贸企业从业人员超过100人的，应当设置安全生产管理机构或配备专职安全生产 管理人员', '查看员工花名册等能够证明员工数量的资料；查看机构设立文件；安全生产管理人员任命文件；查看资格证书；现场调查了解有关人员', '未按规定设置安全生产管理机构或配备安全生产管理人员', '《安全生产法》第二十一条', '《安全生产法》第九十四条', '责令限期改正，可以处五万元以下的罚款；逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2,5', '已按规定设置安全生产管理机构或配备安全生产管理人员', 1, '安全生产管理机构', '员工花名册、机构设立文件、安全生产管理人员任命文件、资格证书，现场调查了解有关人员', NULL);
INSERT INTO `t_check_list` VALUES ('2578c95b-e1cc-4d2a-b9a5-bb431974139c', 'fe8ff8d5-b38a-4195-844f-7516101cb336', '是否设立安委会并按规定开展工作', '从业人员在1000人以上的企业，应当建立安全生产委员会', '查看安委会成立文件；查阅安委会组成部门；查看安委会季度会议通知、纪要等资料', '未设立安全生产委员会', '《山东省生产经营单位安全生产主体责任规定》（山东省人民政府令第303号）第十三条', '《山东省生产经营单位安全生产主体责任规定》（山东省人民政府令第303号）第三十五条', '责令限期改正，可以处五万元以下的罚款；逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2,5', '已设立安全生产委员会', 2, '安全生产管理机构', '安委会成立文件', NULL);
INSERT INTO `t_check_list` VALUES ('5b446e93-92de-426a-9f62-2ff76ed02a77', 'd2926b08-a9f3-4cf9-9503-bc54511f84c2', '对从事接触职业病危害的作业的劳动者，用人单位应当按照国务院安全生产监督管理部门、卫生行政部门的规定组织上岗前、在岗期间和离岗时的职业健康检查，并将检查结果书面告知劳动者。职业健康检查费用由用人单位承担', '已按照规定组织上岗前、在岗期间、离岗时的职业健康检查，已按照国家标准要求的检查周期进行职业健康检查，查体人数充足，查体项目无漏项，用人单位已对劳动者进行告知', '检查职业健康监护制度、年度计划、实施方案、职业健康检查机构委托书、职业健康投入的列支情况，对照劳动者名册及职业病危害项目申报表，查看劳动者职业健康监护档案，体检报告。检查职业健康监护制度、在岗劳动者名册，查看在岗劳动者职业健康监护档案、承担职业健康检查的医疗卫生机构资质证明、委托书，职业健康检查体检结果报告。查阅书面告知书，查阅劳动者领取查体报告复印件签名表，查阅职业健康档案，询问劳动者', '未按照规定组织上岗前、在岗期间、离岗时的职业健康检查，未按照国家标准要求的检查周期进行职业健康检查，查体人数不足，查体项目漏项，用人单位未对劳动者进行告知', '《职业病防治法》第三十五条第一款', '《职业病防治法》第七十一条第四项', '由安全生产监督管理部门责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2,', '已按照规定组织上岗前、在岗期间、离岗时的职业健康检查，已按照国家标准要求的检查周期进行职业健康检查，查体人数充足，查体项目无漏项，用人单位已对劳动者进行告知', 16, '在岗期间及离岗时的职业健康检查', '职业健康监护年度计划、劳动者职业健康监护档案、体检报告、医疗卫生机构资质证明、委托书、书面告知书或劳动者领取查体报告复印件签名表', NULL);
