INSERT INTO `t_check_list` VALUES ('4c1bca01-2495-48a6-ab0e-cc11624114c6', 'd2926b08-a9f3-4cf9-9503-bc54511f84c2', '用人单位工作场所存在职业病目录所列职业病的危害因素的，应当及时、如实向所在地安全生产监督管理部门申报危害项目，接受监督', '用人单位进行职业病危害项目申报，所申报职业病危害因素齐全，所申报场所及职业病危害因素与检测报告对应，引进建设项目已进行变更申报，用人单位工作场所、名称、法定代表人或者主要负责人发生变化的已进行变更申报', '查看申报回执，查阅申报表，核对工作场所职业病危害因素检测和评价报告是否一致，现场检查接害岗位和接害种类', '用人单位未进行职业病危害项目申报，所申报职业病危害因素不全，所申报场所及职业病危害因素与检测报告不对应，引进建设项目未进行变更申报，用人单位工作场所、名称、法定代表人或者主要负责人发生变化的未进行变更申报', '《职业病防治法》第十六条第二款', '《职业病防治法》第七十一条第一项', '由安全生产监督管理部门责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2,5', '用人单位进行职业病危害项目申报，所申报职业病危害因素齐全，所申报场所及职业病危害因素与检测报告对应，引进建设项目已进行变更申报，用人单位工作场所、名称、法定代表人或者主要负责人发生变化的已进行变更申报', 1, ' 申报', '申报回执、申报表、工作场所职业病危害因素检测和评价报告', NULL);
