INSERT INTO `t_check_list_copy` VALUES ('631c4d08-140b-47c4-8792-7634e1cd0f37', '9e1b5f8b-fbe2-4fd4-853a-c4c9dd5618ae', '查体结果异常处理措施', '根据查体情况是否对有职业禁忌的劳动者，调离或者暂时脱离原工作岗位；对需要复查的劳动者，按照职业健康检查机构要求的时间安排复查和医学观察；对疑似职业病病人，按照职业健康检查机构的建议安排其进行医学观察或者职业病诊断。', '查看查体报告书，确认查体结果异常人员，核实用人单位根据查体结果采取相应措施的情况。', '未根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施', '《用人单位职业健康监护监督管理办法》第十七条', '《用人单位职业健康监护监督管理办法》第二十六条第五项', '给予警告，责令限期改正，可以并处3万元以下的罚款', '2', '已根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施', 19, '职业健康检查', '查体报告，复查报告，调离通知单，监护档案。');
