INSERT INTO `t_check_list_copy` VALUES ('77420a71-ef3f-4eb0-ae6e-14f87405ce9b', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '氨气浓度传感器', '对采用氨直接蒸发的成套快速冻结装置，是否在加工间内应布置氨气浓度报警装置。', '现场查看', '未在加工间内应布置氨气浓度报警装置', '《冷库设计规范》（GB50072）7.3.19', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '已在加工间内应布置氨气浓度报警装置', 59, '速冻装置区域', NULL);
INSERT INTO `t_check_list_copy` VALUES ('cc53a8d8-cf6d-4a3d-ab3b-7527a4d28a7c', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '氨气浓度传感器', '当氨气浓度达到100ppm或150ppm时，是否自动发出报警信号、自动开启事故排风机、自动停止成套冻结装置的运行，漏氨信号应同时传送至制冷机房控制室报警。', '现场查看', '设置氨气浓度报警装置，但不能自动停止成套冻结装置的运行；漏氨信号不能同时传送至制冷机房控制室报警', '《冷库设计规范》（GB50072）7.3.19', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '氨气浓度报警装置能自动停止成套冻结装置的运行；漏氨信号能同时传送至制冷机房控制室报警', 60, '速冻装置区域', NULL);
