INSERT INTO `t_check_list_copy` VALUES ('e56534db-6777-4423-9f17-f5fe911edb8a', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '安全出口', '布置于穿堂附近附属的办公室、安保值班室、烘衣室、更衣室、休息室及卫生间等与库房生产、管理直接有关的辅助房间，是否至少有一个独立的安全出口', '现场查看', '辅助房间未设独立的安全出口', '《冷库设计规范》（GB50072）4.2.16', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '辅助房间已设独立的安全出口', 73, '冷库区域', NULL);
