INSERT INTO `t_check_list_copy` VALUES ('a6ade165-4e6d-42c4-9183-96c82c2c141b', '3cf9fbe1-fac0-4a56-b74b-afb4958ebab4', '防火门', '库房的楼梯间应设在穿堂附近，并应采用不燃材料建造，通向穿堂的门是否为乙级防火门', '现场查看', '库房的楼梯间不满足防火要求', '《冷库设计规范》（GB50072）4，2，10', '《安全生产法》第六十二条第一款第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定；', '2', '库房的楼梯间已满足防火要求', 75, '冷库区域', NULL);
