INSERT INTO `t_check_list_copy` VALUES ('67179cbb-e070-4118-b8a0-eb8cf72c7f42', '0c3d9ebf-bac8-4a58-8d92-41f399788657', '供电电压', '架空线电力线路边导线至建筑物的最小距离，满足下列要求：线路电压35KV，最小距离3.0m;线路电压3～10KV，最小距离1.5m；线路电压3KV以下，最小距离1.0m。\n', '现场检查', '架空线电力线路边导线至建筑物的最小距离，不满足下列要求：线路电压35KV，最小距离3.0m；线路电压3～10KV，最小距离1.5m；线路电压3KV以下，最小距离1.0m。', 'GB50061-2010《66KV及以下架空电力线路设计规范》', 'GB50061-2010《66KV及以下架空电力线路设计规范》', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '架空线电力线路边导线至建筑物的最小距离，已满足下列要求：线路电压35KV，最小距离3.0m；线路电压3～10KV，最小距离1.5m；线路电压3KV以下，最小距离1.0m', 19, '供电', '');
INSERT INTO `t_check_list_copy` VALUES ('108ac61e-712c-4dcb-86cc-3f687f8f1a01', '0c3d9ebf-bac8-4a58-8d92-41f399788657', '标志设置', '在有较大危险因素的生产经营场所和有关设施、设备上应设置明显的安全警示标志。', '现场检查', '在有较大危险因素的生产经营场所和有关设施、设备上未设置明显的安全警示标志。', '《安全生产法》第三十二条、《金属非金属矿山安全规程》（GB16423-2006）4.6、4.13', '《小型露天采石场安全管理与监督检查规定》第四十条', '责令限期改正，可以处五万元以下的罚款；逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款；情节严重的，责令停产停业整顿；构成犯罪的，依照刑法有关规定追究刑事责任', '2', '在有较大危险因素的生产经营场所和有关设施、设备上已设置明显的安全警示标志', 20, '警示标志', '');
INSERT INTO `t_check_list_copy` VALUES ('a11ace33-97b1-4e03-80ba-7e74ed3d8d70', '265021b6-5c04-4035-a736-7243b7c87018', '查体结果异常处理措施', '根据查体情况对有职业禁忌的劳动者，调离或者暂时脱离原工作岗位；对健康损害可能与所从事的职业相关的劳动者，进行妥善安置；对需要复查的劳动者，按照职业健康检查机构要求的时间安排复查和医学观察；对疑似职业病病人，按照职业健康检查机构的建议安排其进行医学观察或者职业病诊断。', '查看查体报告书，确认查体结果异常人员，核实用人单位根据查体结果采取相应措施的情况。', '未根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施。', '《用人单位职业健康监护监督管理办法》第十七条', '《用人单位职业健康监护监督管理办法》第二十六条第五项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施', 22, '职业健康检查', '查体报告，复查报告，调离通知单，监护档案');
INSERT INTO `t_check_list_copy` VALUES ('f3089bf1-cbff-44c1-9015-ffdb8f6e3c7c', '265021b6-5c04-4035-a736-7243b7c87018', '定期检测', '委托具有相应资质机构检测。', '查看检测机构资质证明及检测报告', '未委托有相应资质机构进行检测', '《职业病防治法》第二十六条第二款，《用人单位职业病危害因素定期检测管理规范》第四条，《职业病危害因素分类目录》', '《职业病防治法》第七十二条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已委托有相应资质机构进行检测', 24, '职业病危害因素检测', '');
INSERT INTO `t_check_list_copy` VALUES ('2e4708fd-d448-4841-8b10-b6d0fba9d285', '265021b6-5c04-4035-a736-7243b7c87018', '定期检测', '每年至少进行一次全面检测。', '查阅职业卫生档案，确定存在职业病危害的场所和危害因素', '未定期对工作场所进行职业病危害因素检测', '《职业病防治法》第二十六条第二款，《用人单位职业病危害因素定期检测管理规范》第四条，《职业病危害因素分类目录》。', '《职业病防治法》第七十二条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已定期对工作场所进行职业病危害因素检测', 25, '职业病危害因素检测', '');
INSERT INTO `t_check_list_copy` VALUES ('d483b4e6-2b21-4079-ad54-bb596feee67b', '265021b6-5c04-4035-a736-7243b7c87018', '定期检测', '全面检测。', '查阅职业卫生档案，确定存在职业病危害的场所和危害因素；查看检测机构资质证明及检测报告，现场核对职业病危害场所和因素是否进行全面检测。', '存在职业病危害的场所和危害因素检测不全\n', '《职业病防治法》第二十六条第二款，《用人单位职业病危害因素定期检测管理规范》第四条，《职业病危害因素分类目录》', '《职业病防治法》第七十二条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '存在职业病危害的场所和危害因素的检测已健全', 26, '职业病危害因素检测', '');
INSERT INTO `t_check_list_copy` VALUES ('2856d0da-76b0-4c88-8db1-a46466e603c0', '265021b6-5c04-4035-a736-7243b7c87018', '超标处理', '检测结果中职业病危害因素浓度或强度超过职业接触限值的，用人单位应结合实际情况立即进行整改。', '查看检测报告，明确职业病危害因素浓度或强度超标岗位；查看超标岗位治理方案和整改落实情况。', '工作场所职业病危害因素经治理仍不符合国家职业卫生标准和卫生要求而未停止作业。', '《职业病防治法》第二十六条第四款', '《职业病防治法》第七十二条第五项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '工作场所职业病危害因素经治理已符合国家职业卫生标准和卫生要求', 27, '职业病危害因素检测', '');
INSERT INTO `t_check_list_copy` VALUES ('79f5eb05-4e06-48b6-a051-6429bf1fcf4c', '265021b6-5c04-4035-a736-7243b7c87018', '初次申报', '及时、如实申报产生职业病危害的项目。申报内容：用人单位的基本情况（单位名称、注册地址、工作场所地址、法定代表人、劳动者总人数）、职业病危害因素种类、接触人数以及分布情况（作业场所名称、职业病危害因素名称及现场浓度(强度)、接触人员）等。', '查阅职业卫生档案，现场检查接害岗位和工种，核对申报内容与工作场所职业病危害实际状况是否一致。', '未及时、如实申报产生职业病危害项目', '《职业病防治法》第十六条第二款', '《职业病防治法》第七十一条第一项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已如实申报产生职业病危害项目', 28, '职业病危害项目申报', '营业执照，人员花名册，职业卫生检测和评价报告，申报表，查体报告');
INSERT INTO `t_check_list_copy` VALUES ('6993326c-7926-4661-b60e-39f94d9ac8b4', '265021b6-5c04-4035-a736-7243b7c87018', '变更申报', '按规定申报变更职业病危害项目内容（用人单位因技术、工艺、设备或者材料等发生变化导致原申报的职业病危害因素及其相关内容发生重大变化，工作场所、名称、法定代表人或者主要负责人发生变化，应当自发生变化之日起15日内进行变更申报；经过职业病危害因素检测、评价，发现原申报内容发生变化的，应当自收到有关检测、评价结果之日起15日内进行变更申报；进行新建、改建、扩建、技术改造或技术引进建设项目的，自建设项目竣工验收之日起30日内进行变更申报）。', '查阅职业卫生档案，申报回执，询问相关负责人，确认用人单位基本情况、技术、工艺、设备、职业病危害因素是否发生变化，是否存在新建、改建、扩建、技术改造等建设项目。', '未申报变更职业病危害项目内容。', '《职业病危害项目申报办法》第八条', '《职业病危害项目申报办法》第十五条', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已申报变更职业病危害项目内容', 29, '职业病危害项目申报', '营业执照，人员花名册，职业卫生检测和评价报告，申报表，查体报告');
INSERT INTO `t_check_list_copy` VALUES ('2f1a7a1e-e39f-4fe1-9f26-55715e0bc663', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '建构筑物结构及安全间距是否合规', '多层建筑物宜采用框架结构；不能使用框架结构的建筑物应在墙上设置符合要求的泄爆口，泄压（口）的朝向应避开人员密集场所和主要交通道路。', '检查企业平面布置图、消防验收手续。现场查看建构筑物结构与布局，建构筑物及设备的安全间距是否符合要求，询问员工相关情况', '粉尘爆炸危险场所设置在非轻型结构的单层建筑内或非框架结构的多层建构筑物内。', '《粉尘防爆安全规程》(GB15577-2007)第5.1、5.2、5.3、5.4、5.5条；《建筑设计防火规范》(GB50016-2014)第3.4.1条;《全省深化冶金等工贸行业粉尘防爆专项整治工作方案》（鲁安办发〔2016〕19号）三一。', NULL, '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '粉尘爆炸危险场所已搬离出非轻型结构的单层建筑内和非框架结构的多层建构筑物内。', 2, '建构筑物', '企业平面布置图');
INSERT INTO `t_check_list_copy` VALUES ('575d6dcd-d911-412a-8a53-d61bf2b42992', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '建构筑物结构及安全间距是否合规', '厂房建筑物内设有粉尘涉爆生产加工区，建筑物与居民区、教育、医院、商业等重要公共建筑之间的防火间距≥50m，与民用建筑之间的防火间距≥25m，与明火或散发火花地点宜＞30m。', '检查企业平面布置图、消防验收手续。现场查看建构筑物结构与布局，建构筑物及设备的安全间距是否符合要求，询问员工相关情况', '粉尘爆炸危险场所与居民区、员工宿舍等人员密集场所安全距离不足。', '《粉尘防爆安全规程》(GB15577-2007)第5.1、5.2、5.3、5.4、5.5条；《建筑设计防火规范》(GB50016-2014)第3.4.1条;《全省深化冶金等工贸行业粉尘防爆专项整治工作方案》（鲁安办发〔2016〕19号）三一。', '《安全生产法》第一百零二条第一项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '粉尘爆炸危险场所与居民区、员工宿舍等人员密集场所安全距离已足够。', 3, '建构筑物', '企业平面布置图');
INSERT INTO `t_check_list_copy` VALUES ('41bd1ab1-76b9-4cbe-ab9d-4321420ab7e1', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '建构筑物结构及安全间距是否合规', '粉尘爆炸危险的区域不得设置办公室、会议室、休息室、危险化学品仓库等。', '检查企业平面布置图、消防验收手续。现场查看建构筑物结构与布局，建构筑物及设备的安全间距是否符合要求，询问员工相关情况', '粉尘爆炸危险场所与居民区、员工宿舍等人员密集场所安全距离不足。', '《粉尘防爆安全规程》(GB15577-2007)第5.1、5.2、5.3、5.4、5.5条；《建筑设计防火规范》(GB50016-2014)第3.4.1条;《全省深化冶金等工贸行业粉尘防爆专项整治工作方案》（鲁安办发〔2016〕19号）三一。', '《安全生产法》第一百零二条第一项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '粉尘爆炸危险场所与居民区、员工宿舍等人员密集场所安全距离已足够。', 4, '建构筑物', '企业平面布置图');
INSERT INTO `t_check_list_copy` VALUES ('aefcbe9b-99bc-4ed6-94e9-715120a45d06', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否按规定设置应急出口、疏散通道等', '生产经营场所应当设有符合紧急疏散要求的应急门、疏散通道、应急照明、标志明显，应保持安全通道畅通，不得堆放任何物品，应符合(GB50016-2014)的相关规定。', '查看安全警示标志一览表及检查记录。现场查看疏散通道、安全出口、应急照明设置情况是否符合要求，询问员工检查情况。', '涉粉尘爆炸的生产经营场所未设置符合紧急疏散要求、标志明显、保持畅通的出口。', '《安全生产法》第三十九条《爆炸危险环境电力装置设计规范》（GB 50058）第4.1.4条；《建筑设计防火规范》(GB50016) 3.7.2条。《安全生产法》第三十二条', '《安全生产法》第一百零二条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '涉粉尘爆炸的生产经营场所已设置符合紧急疏散要求、标志明显、保持畅通的出口。', 5, '安全出口', '现场检查照片');
INSERT INTO `t_check_list_copy` VALUES ('329f9331-45dd-4997-95b3-107a5a713660', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否按规定设置应急出口、疏散通道等', '爆炸危险区域应设有两个以上安全出口，其中至少有一个通向非爆炸危险区域，其安全出口的门应当向爆炸危险性较小的区域侧开启。', '查看安全警示标志一览表及检查记录。现场查看疏散通道、安全出口、应急照明设置情况是否符合要求，询问员工检查情况。', '涉粉尘爆炸的生产经营场所未设置符合紧急疏散要求、标志明显、保持畅通的出口。', '《安全生产法》第三十九条《爆炸危险环境电力装置设计规范》（GB 50058）第4.1.4条；《建筑设计防火规范》(GB50016) 3.7.2条。《安全生产法》第三十二条', '《安全生产法》第一百零二条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '涉粉尘爆炸的生产经营场所已设置符合紧急疏散要求、标志明显、保持畅通的出口。', 6, '安全出口', '现场检查照片');
INSERT INTO `t_check_list_copy` VALUES ('9a2bc6c4-d004-4e36-ae52-82dafd407d6e', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否按规定设置应急出口、疏散通道等', '生产经营单位应当在有粉尘爆炸危险因素的生产经营场所和除尘器、风管等设备上设置明显的安全警示标志。', '查看安全警示标志一览表及检查记录。现场查看疏散通道、安全出口、应急照明设置情况是否符合要求，询问员工检查情况。', '涉粉尘爆炸的生产经营场所未设置符合紧急疏散要求、标志明显、保持畅通的出口。', '《安全生产法》第三十九条《爆炸危险环境电力装置设计规范》（GB 50058）第4.1.4条；《建筑设计防火规范》(GB50016) 3.7.2条。《安全生产法》第三十二条', '《安全生产法》第一百零二条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '涉粉尘爆炸的生产经营场所已设置符合紧急疏散要求、标志明显、保持畅通的出口。', 7, '安全出口', '现场检查照片');
INSERT INTO `t_check_list_copy` VALUES ('a067cb02-4430-4341-bdcf-fae96c350b5c', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '粉尘燃爆场所是否使用防爆电器', '企业应正确划分爆炸危险区域，根据不同的防爆等级，采用相应的粉尘防爆型电气设备及线路，表面及内部无积尘。粉尘燃爆环境插座开口的一面应朝下，且与垂直面的角度不应大于60度。', '检查企业防爆区域图，电气设备（变电室、配电柜（箱）、开关箱插座）防爆合格证等档案。现场检查防爆区域内电气设备是否符合防爆要求。', '粉尘爆炸危险场所的20区未使用防爆电气设备设施。', '《爆炸危险环境电力装置设计规范》GB50058）第4.1.4.7；5.1.1.6《防止静电事故通用导则》GB12158）6.2.3；《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273）第10, 4.2、4.3条；可燃性粉尘场所用电设备（GB12476.2）第6.4', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '粉尘爆炸危险场所的20区已使用防爆电气设备设施。', 8, '电器防爆', '粉尘防爆危险区域划分图，现场电气设备照片。');
INSERT INTO `t_check_list_copy` VALUES ('9f62612d-f038-488f-add5-743684fd7707', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '粉尘燃爆场所是否使用防爆电器', '20、21、22区的电气设备必须符合GB 12476.1、GB 12476.2规定的防爆类型和级别要求；电气设备的铭牌标识清楚，有防爆标志、防爆合格证号，外壳无裂缝、损伤，电机不得漏油。', '检查企业防爆区域图，电气设备（变电室、配电柜（箱）、开关箱插座）防爆合格证等档案。现场检查防爆区域内电气设备是否符合防爆要求。', '粉尘爆炸危险场所的20区未使用防爆电气设备设施。', '《爆炸危险环境电力装置设计规范》GB50058）第4.1.4.7；5.1.1.6《防止静电事故通用导则》GB12158）6.2.3；《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273）第10, 4.2、4.3条；可燃性粉尘场所用电设备（GB12476.2）第6.4', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '粉尘爆炸危险场所的20区已使用防爆电气设备设施。', 9, '电器防爆', '粉尘防爆危险区域划分图，现场电气设备照片。');
INSERT INTO `t_check_list_copy` VALUES ('642d29b7-ed6f-4f00-bef6-7a38204d5f8b', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '粉尘燃爆场所是否使用防爆电器', '粉尘爆炸危险场所电气设备应进行保护接地，除尘系统的风管不得作为电气设备的接地导体。', '检查企业防爆区域图，电气设备（变电室、配电柜（箱）、开关箱插座）防爆合格证等档案。现场检查防爆区域内电气设备是否符合防爆要求。', '粉尘爆炸危险场所的20区未使用防爆电气设备设施。', '《爆炸危险环境电力装置设计规范》GB50058）第4.1.4.7；5.1.1.6《防止静电事故通用导则》GB12158）6.2.3；《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273）第10, 4.2、4.3条；可燃性粉尘场所用电设备（GB12476.2）第6.4', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '粉尘爆炸危险场所的20区已使用防爆电气设备设施。', 10, '电器防爆', '粉尘防爆危险区域划分图，现场电气设备照片。');
INSERT INTO `t_check_list_copy` VALUES ('5f3fc482-9b2d-4a1b-bc5f-b4ca0ab4d0d0', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '防止摩擦、碰撞火花装置是否完好、有效', '在粉碎、研磨、造粒等易产生机械点火源的进料处，应安装能除去混入料中杂物的磁选、气动分离器或筛子，防止杂物与设备碰撞，磁选器应每班定期清理干净并保存清理记录； ', '查看定期清理记录，打开磁选器现场查看。了解企业风管及火花探测报警、熄灭装置等等防爆装置设置情况、合格证及竣工验收相关资料，现场检查符合情况。', '在粉碎、研磨、造粒等易于产生机械点火源的工艺设备前，未按规范设置去除铁、石等异物的装置。', '《粉尘防爆安全规程》(GB15577-2007) 第6.4.1、6.4.2、6.4.3、6.4.4条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.7；6.3条。《木材加工系统粉尘防爆安全规范》（AQ4228）第6.2.1.2。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '在粉碎、研磨、造粒等易于产生机械点火源的工艺设备前，已按规范设置去除铁、石等异物的装置。', 11, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('a4156762-067b-4e26-9d36-0cfa4aec9d46', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '防止摩擦、碰撞火花装置是否完好、有效', '应采取有效措施防止铝、镁、钛、锆等金属粉末或含有这些金属的粉末与锈钢摩擦产生火花； ', '查看定期清理记录，打开磁选器现场查看。了解企业风管及火花探测报警、熄灭装置等等防爆装置设置情况、合格证及竣工验收相关资料，现场检查符合情况。', '在粉碎、研磨、造粒等易于产生机械点火源的工艺设备前，未按规范设置去除铁、石等异物的装置。', '《粉尘防爆安全规程》(GB15577-2007) 第6.4.1、6.4.2、6.4.3、6.4.4条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.7；6.3条。《木材加工系统粉尘防爆安全规范》（AQ4228）第6.2.1.2。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '在粉碎、研磨、造粒等易于产生机械点火源的工艺设备前，已按规范设置去除铁、石等异物的装置。', 12, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('45c6a807-1d09-46d9-92f4-b5c42eeb5d50', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '防止摩擦、碰撞火花装置是否完好、有效', '没有与明火作业等效的保护措施。不应使用旋转磨轮和旋转切盘进行研磨和切割；', '查看定期清理记录，打开磁选器现场查看。了解企业风管及火花探测报警、熄灭装置等等防爆装置设置情况、合格证及竣工验收相关资料，现场检查符合情况。', '木制品加工企业，与砂光机连接的风管未规范设置火花探测报警装置。', '《粉尘防爆安全规程》(GB15577-2007) 第6.4.1、6.4.2、6.4.3、6.4.4条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.7；6.3条。《木材加工系统粉尘防爆安全规范》（AQ4228）第6.2.1.2。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '木制品加工企业，与砂光机连接的风管已规范设置火花探测报警装置。', 13, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('ca9abf85-fc4e-413e-a87f-08ee9359cbff', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '防止摩擦、碰撞火花装置是否完好、有效', ' 粉尘爆炸危险场所采用防碰撞火花作业工具；', '查看定期清理记录，打开磁选器现场查看。了解企业风管及火花探测报警、熄灭装置等等防爆装置设置情况、合格证及竣工验收相关资料，现场检查符合情况。', '木制品加工企业，与砂光机连接的风管未规范设置火花探测报警装置。', '《粉尘防爆安全规程》(GB15577-2007) 第6.4.1、6.4.2、6.4.3、6.4.4条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.7；6.3条。《木材加工系统粉尘防爆安全规范》（AQ4228）第6.2.1.2。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '木制品加工企业，与砂光机连接的风管已规范设置火花探测报警装置。', 14, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('6ad3a104-d7e3-4163-b240-d4b241801109', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '防止摩擦、碰撞火花装置是否完好、有效', '存在经由吸尘罩或吸尘柜吸入火花危险的风管，应采用阻隔火花进入风管及除尘器的措施。宜在风管上安装火花探测报警装置、火花熄灭装置，且两者应联锁保护。如：木制品加工企业与砂光机连接的风管必须安装以上装置。', '查看定期清理记录，打开磁选器现场查看。了解企业风管及火花探测报警、熄灭装置等等防爆装置设置情况、合格证及竣工验收相关资料，现场检查符合情况。', '木制品加工企业，与砂光机连接的风管未规范设置火花探测报警装置。', '《粉尘防爆安全规程》(GB15577-2007) 第6.4.1、6.4.2、6.4.3、6.4.4条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.7；6.3条。《木材加工系统粉尘防爆安全规范》（AQ4228）第6.2.1.2。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '木制品加工企业，与砂光机连接的风管已规范设置火花探测报警装置。', 15, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('ba907ca7-c36f-4eb2-ac47-39d5674a7c02', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '除尘系统是否按规定分布及分区设置情况', '除尘器布置应远离明火≥25米，应按生产工艺分片（分区域）设置相对独立的除尘系统，并保证除尘系统有足够的风量，风管中不应有粉尘沉降。', '查文件：除尘器设计、安装单位资质；现场检查除尘系统符合情况。', '未按工艺分片设置相对独立的除尘系统', '《建筑设计防火规范》(GB50016-2014)第9.3.6条；《粉尘防爆安全规程》（GB15577-2007） 第6.6条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第11.2、11.3、11.4、4.1、4.5、4.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '已按工艺分片设置相对独立的除尘系统', 16, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('4e84b89d-be38-4de1-a60f-11d1ee58aa60', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '除尘系统是否按规定分布及分区设置情况', '可燃性粉尘与可燃气体等易加剧爆炸危险的介质不得共用一套除尘系统，不同防火分区的除尘系统不得互联互通', '查文件：除尘器设计、安装单位资质；现场检查除尘系统符合情况。', '除尘系统采用正压吹送粉尘，且未采取可靠的防范点燃源的措施。', '《建筑设计防火规范》(GB50016-2014)第9.3.6条；《粉尘防爆安全规程》（GB15577-2007） 第6.6条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第11.2、11.3、11.4、4.1、4.5、4.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '除尘系统采用正压吹送粉尘，且已采取可靠的防范点燃源的措施。', 17, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('529357f9-9f57-4559-be65-17193c2656a3', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '除尘系统是否按规定分布及分区设置情况', '铝镁粉尘及木粉尘除尘器应在负压下工作，其它除尘系统若采用正压吹送粉尘，则应采取可靠的防范点燃源的措施。', '查文件：除尘器设计、安装单位资质；现场检查除尘系统符合情况。', '除尘系统采用正压吹送粉尘，且未采取可靠的防范点燃源的措施。', '《建筑设计防火规范》(GB50016-2014)第9.3.6条；《粉尘防爆安全规程》（GB15577-2007） 第6.6条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第11.2、11.3、11.4、4.1、4.5、4.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '除尘系统采用正压吹送粉尘，且已采取可靠的防范点燃源的措施。', 18, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('2cb3f2b8-d718-4e1a-8e1a-fb97356c0acf', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '除尘系统是否按规定分布及分区设置情况', '除尘系统不应采用粉尘沉降室除尘，不应采用干式巷道式构筑物作为除尘风道。', '查文件：除尘器设计、安装单位资质；现场检查除尘系统符合情况。', '除尘系统采用粉尘沉降室除尘，或者采用干式巷道式构筑物作为除尘风道。', '《建筑设计防火规范》(GB50016-2014)第9.3.6条；《粉尘防爆安全规程》（GB15577-2007） 第6.6条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第11.2、11.3、11.4、4.1、4.5、4.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '除尘系统采已用粉尘沉降室除尘，或者采用干式巷道式构筑物作为除尘风道。', 19, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('344e86bd-3c03-4472-bba8-5e7da89fe4ee', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '除尘系统是否按规定分布及分区设置情况', '净化有爆炸危险粉尘的干式除尘器宜安装在室外，室外除尘器进风管应与建筑外墙保持90度、或180度夹角的除尘器侧面、顶部或正面位置，进风管弯头处设置卸爆口且不朝向厂房建筑物内部；除尘器若布置在室内应满足AQ4273-2016第11.2相关要求。', '查文件：除尘器设计、安装单位资质；现场检查除尘系统符合情况。', '除尘系统采用粉尘沉降室除尘，或者采用干式巷道式构筑物作为除尘风道。', '《建筑设计防火规范》(GB50016-2014)第9.3.6条；《粉尘防爆安全规程》（GB15577-2007） 第6.6条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第11.2、11.3、11.4、4.1、4.5、4.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '除尘系统采已用粉尘沉降室除尘，或者采用干式巷道式构筑物作为除尘风道。', 20, '除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('06d3dd4a-f423-4bc8-b5c2-7356ee58fe00', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否采取防爆措施', '存在粉尘爆炸危险的干式除尘系统（包括除尘器、除尘器进风管）、粉体加工设备、料仓、斗式提升机等设备设施必须按规范采用泄爆、', '现场检查：干式除尘器和过滤器的布置情况及安全间距。除尘器、管道的泄压装置及布置情况。爆破片出厂合格证。', '干式除尘系统未规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', '建筑设计防火规范》（GB50016-2014）第9.3.7、3.8条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.2；9；11条。《粉尘爆炸泄压指南》（GB/T15605）第9；6.1.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '干式除尘系统已规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', 21, '干式除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('99d0dfff-e209-424a-98b7-b7dcac755204', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否采取防爆措施', '除尘器、过滤器、管道等应设置泄压装置，泄爆口应按规定设置，并布置在系统的负压段。', '现场检查：干式除尘器和过滤器的布置情况及安全间距。除尘器、管道的泄压装置及布置情况。爆破片出厂合格证。', '干式除尘系统未规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', '建筑设计防火规范》（GB50016-2014）第9.3.7、3.8条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.2；9；11条。《粉尘爆炸泄压指南》（GB/T15605）第9；6.1.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '干式除尘系统已规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', 22, '干式除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('ce652fd5-fd8d-477e-ba27-c28da837c793', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否采取防爆措施', '干式除尘器如安装在室内，其泄爆导管应直通室外，且长度小于3m，泄压面的轴线与导管夹角应≤20°。', '现场检查：干式除尘器和过滤器的布置情况及安全间距。除尘器、管道的泄压装置及布置情况。爆破片出厂合格证。', '干式除尘系统未规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', '建筑设计防火规范》（GB50016-2014）第9.3.7、3.8条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.2；9；11条。《粉尘爆炸泄压指南》（GB/T15605）第9；6.1.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '干式除尘系统已规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', 23, '干式除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('1a12a095-919e-4373-8bd8-20d6ee724e47', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否采取防爆措施', '存在爆炸危险的设备的泄压装置泄压口应通往室外安全区域。若泄压装置泄压口设在厂房内，应采用无火焰泄压装置。', '现场检查：干式除尘器和过滤器的布置情况及安全间距。除尘器、管道的泄压装置及布置情况。爆破片出厂合格证。', '干式除尘系统未规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', '建筑设计防火规范》（GB50016-2014）第9.3.7、3.8条。《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273-2016）第4.2；9；11条。《粉尘爆炸泄压指南》（GB/T15605）第9；6.1.6条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '干式除尘系统已规范采用泄爆、隔爆、惰化、抑爆等任一种控爆措施。', 24, '干式除尘系统', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('6834060b-8365-47bb-8cb9-fce4ec11b081', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '是否安装锁气卸灰装置', '干式除尘器应设置锁气卸灰装置，该装置工作周期满足灰斗内无粉尘堆积，应设置运行异常及故障停机状况时监控、报警装置及发出信号。', '现场检查除尘器、排风设备的布置情况及日常维护保养和清灰记录，现场检查除尘器类型是否合理，有无各类安全报警及联锁保护，是否及时清灰，打开除尘器查看滤袋表面积尘情况。', '除尘器灰斗下部未设锁气卸灰装置。', '《粉尘爆炸危险场所用除尘系统安全技术规范（AQ4273-2016）第5.1.5、1.6；5.2.2、2.3条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '除尘器灰斗下部已设锁气卸灰装置。', 25, '锁气卸灰', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('e3ffe7f7-9c1e-4cd0-bb28-28b3c5c80dbc', 'd9ac630a-c2a4-4d66-865d-55aa6fb74cd7', '现场是否定期清理粉尘', '隔爆、惰化、抑爆等控爆措施。', '查看定期清扫粉尘的责任人及清扫记录，询问员工采用的清扫方式及工具，现场查看现场积尘情况。', '作业现场积尘未及时规范清理；磁选器未定期清理。', '《全省深化冶金等工贸行业粉尘防爆专项整治工作方案》（鲁安办发〔2016〕19号）四,《粉尘爆炸危险场所用除尘系统安全技术规范》（AQ4273）第12.2条。', '《安全生产法》第九十六条第二项', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '作业现场积尘已及时规范清理；磁选器未定期清理。', 26, '作业安全', '现场照片');
INSERT INTO `t_check_list_copy` VALUES ('5c3730b6-879b-4fb4-b3ac-75c8fc5d2302', '99536433-0f23-4938-95af-37eca4d154b2', '按规定设置安全管理机构或配备安全管理人员情况', '配备原则：一不足100人的，配备专职安全生产管理人员；二100人以上不足300人的，设置安全生产管理机构，并配备2名以上专职安全生产管理人员，其中至少应当有1名注册安全工程师；三300人以上不足1000人的，设置专门的安全生产管理机构，并按不低于从业人员5‰但最低不少于3名的比例配备专职安全生产管理人员，其中至少应当有2名注册安全工程师；四1000人以上的，应当设置专门的安全生产管理机构，并按不低于从业人员5‰的比例配备专职安全生产管理人员，其中至少应当有3名注册安全工程师。', '检查企业员工花名册、安全管理机构或配备安全管理人员的文件和配备注册安全工程师的文件。', '未按规定设置安全管理机构或配备安全管理人员。', '《安全生产法》第二十四条第三款；《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第九条', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2', '已按规定设置安全管理机构或配备安全管理人员', 1, '组织机构', '公司员工花名册、安全管理机构设置和安全管理人员任命文件、注安师的证书及聘用合同。');
INSERT INTO `t_check_list_copy` VALUES ('9e89811a-a899-4e29-836d-a7c0d4af05f2', '99536433-0f23-4938-95af-37eca4d154b2', '设置安全总监', '从业人员在300人以上的高危生产经营单位应当设置安全总监。安全总监协助本单位主要负责人履行安全生产管理职责，专项分管本单位安全生产管理工作。', '检查配备安全总监的文件。', '未按规定设置安全总监。', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第十二条', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第三十五条第四项', '责令限期改正，可处以5000元以上2万元以下的罚款，对其主要负责人处以1000元以上1万元以下的罚款', '2', '已按规定设置安全总监', 2, '组织机构', '从业人员花名册、任命文件复印件。');
INSERT INTO `t_check_list_copy` VALUES ('b3653f13-eaf9-4071-a12e-0cf9b03ae025', '99536433-0f23-4938-95af-37eca4d154b2', '主要负责人和安全管理人员培训', '主要负责人和安全生产管理人员，自任职之日起6个月内，必须经安全生产监管监察部门对其安全生产知识和管理能力考核合格。', '查看主要负责人和安全管理人员的安全合格证书。', '主要负责人和安全生产管理人员未按规定经考核合格或安全合格证未按时复审。', '《生产经营单位安全培训规定》（总局3号令）第二十四条', '《安全生产法》第九十四条第二项;《生产经营单位安全培训规定》第三十条', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2', '主要负责人和安全生产管理人员已按规定经考核合格或安全合格证已复审', 3, '安全培训', '主要负责人、安全管理人员任命文件及培训证书复印件等。');
INSERT INTO `t_check_list_copy` VALUES ('18924237-02a8-4e69-9cb2-6c9bdd59bc48', '99536433-0f23-4938-95af-37eca4d154b2', '特种作业人员培训档案和持证上岗情况', '①应当加强对本单位特种作业人员的管理，建立健全特种作业人员培训、复审档案。', '①检查企业特种作业人员管理档案；②现场抽查部分特种作业人员信息是否与台账一致。', '未建立健全特种作业人员档案。', '《特种作业人员安全技术培训考核管理规定》（总局30号令）第五条、第三十四条', '《特种作业人员安全技术培训考核管理规定》（总局30号令）第三十八条', '给予警告，并处1万元以下的罚款', '2', '已建立健全特种作业人员档案', 4, '安全培训', '特种作业人员台账、资格证（煤气作业、电工、电焊工等作业人员）原件或复印件、询问笔录等');
INSERT INTO `t_check_list_copy` VALUES ('7ebf9943-b43f-4b5b-aec9-e99de99bc5f2', '99536433-0f23-4938-95af-37eca4d154b2', '特种作业人员培训档案和持证上岗情况', '②特种作业人员必须经专门的安全技术培训并考核合格，取得《中华人民共和国特种作业操作证》后，方可上岗作业。③金属冶炼企业主要涉及电工、电焊工、煤气作业（包括煤气生产、储存、输送、使用、维护检修的作业）。', '①检查企业特种作业人员管理档案；②现场抽查部分特种作业人员信息是否与台账一致。', '特种作业人员未按规定经专门的安全作业培训并取得相应资格，上岗作业。', '《特种作业人员安全技术培训考核管理规定》（总局30号令）第五条、第三十四条', '《安全生产法》第九十四条第七项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2', '特种作业人员已按规定经专门的安全作业培训并取得相应资格，上岗作业', 5, '安全培训', '特种作业人员现场照片、特种作业人员操作资格证书复印件、作业票据、询问笔录等');
INSERT INTO `t_check_list_copy` VALUES ('ebb5a073-489d-45ec-b34b-fda3ab13e043', '99536433-0f23-4938-95af-37eca4d154b2', '从业人员安全生产教育和培训档案', '①应建立安全生产教育和培训档案，如实记录安全生产教育和培训的时间、内容、参加人员以及考核结果等情况。', '抽查4名高温熔融、煤气作业等重点岗位人员的安全培训档案，查记录是否齐全及培训范围、学时是否符合要求。', '未如实记录安全生产教育和培训情况。', '《生产经营单位安全培训规定》（总局3号令）第二十二条、第十三条第二款，《炼铁安全规程》4.11', '《安全生产法》第九十四条第四项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2', '已如实记录安全生产教育和培训情况', 6, '安全培训', '所抽查的员工培训档案、试卷、培训记录等资料复印件、询问笔录等。');
INSERT INTO `t_check_list_copy` VALUES ('d7bb2736-2ef2-401a-a387-a0fc15c0ea52', '99536433-0f23-4938-95af-37eca4d154b2', '从业人员安全生产教育和培训档案', '②新上岗的从业人员安全培训时间不少于72学时，每年再培训时间不少于20学时。③调换工种和脱岗三个月以上重新上岗的人员，应事先进行岗位安全培训，并经考核合格方可上岗。', '抽查4名高温熔融、煤气作业等重点岗位人员的安全培训档案，查记录是否齐全及培训范围、学时是否符合要求。', '未按照规定对从业人员进行安全生产教育和培训', '《生产经营单位安全培训规定》（总局3号令）第二十二条、第十三条第二款，《炼铁安全规程》4.11', '《生产经营单位安全培训规定》（总局3号令）第三十条第二项', '责令其限期改正,可以处5万元以下的罚款', '2', '已按照规定对从业人员进行安全生产教育和培训', 7, '安全培训', '所抽查的员工培训档案、培训记录等资料复印件、询问笔录等。');
INSERT INTO `t_check_list_copy` VALUES ('034e1e2e-49d6-4687-9268-187b75c4005a', '99536433-0f23-4938-95af-37eca4d154b2', '重大危险源登记建档情况', '生产经营单位对重大危险源应当登记建档，进行定期检测、评估、监控。评估报告的有效期为三年，重大危险源的安全设施和安全监测监控系统定期检测并在有效期内。', '查看企业重大危险源相关档案资料、评估报告、相关检测报告。', '对重大危险源未登记建档，或者未进行定期评估、监控', '《危险化学品重大危险源监督管理暂行规定》（总局40号令）第二十二条、第十一条、第十五条', '《安全生产法》第九十八条第二项', '责令限期改正，可以处十万元以下的罚款', '2', '对重大危险源已登记建档，或者已进行定期评估、监控', 8, '重大危险源', '现场照片，辨识记录、评价报告、应急预案、设备设施的设计文件等复印件、询问笔录等');
INSERT INTO `t_check_list_copy` VALUES ('3a44d8b2-2fc3-4489-aa03-cec1fa9dd78d', '99536433-0f23-4938-95af-37eca4d154b2', '重大危险源备案情况', '①企业应将重大危险源安全评估报告或者评价报告、备案申请表、重大危险源档案材料报送所在地县级安监部门备案。②企业重大危险源评估报告满三年、装置设施新（改、扩）建、危险化学品种类、数量、储存方式及重要设备设施发生变化，应重新备案。', '查看企业的重大危险源备案回执和评估报告的期限以及与现场相符性。', '未按照规定进行重大危险源备案', '《危险化学品重大危险源监督管理暂行规定》（总局40号令）第二十三条', '《危险化学品重大危险源监督管理暂行规定》（总局40号令）第三十四条第四项', '给予警告，可以并处5000元以上3万元以下的罚款', '2', '已按照规定进行重大危险源备案', 9, '重大危险源', '现场照片、重大危险源评估报告、备案回执、询问笔录等');
INSERT INTO `t_check_list_copy` VALUES ('69b11649-b8f1-48be-81c5-51c1be78ac35', '99536433-0f23-4938-95af-37eca4d154b2', '签订安全管理协议情况', '①生产经营单位对生产经营项目、场所、设备发包或者出租时，应对承包单位、承租单位的安全生产条件或者资质进行审查。', '①查阅承包方资质等相关材料；②查阅安全管理协议或者承包合同，中是否有关于安全管理方面的规定。', '将生产经营项目、场所、设备发包或者出租给不具备安全生产条件或相应资质的单位或个人', '《安全生产法》第四十六条', '《安全生产法》第一百条', '责令限期改正，可以处五万元以下的罚款，对其直接负责的主管人员和其他直接责任人员可以处一万元以下的罚款', '2', '已将生产经营项目、场所、设备发包或者出租给具备安全生产条件或相应资质的单位或个人', 10, '外包管理', '企业的出租、发包合同或协议、相关资质的复印件、询问笔录等。');
INSERT INTO `t_check_list_copy` VALUES ('a9f3020a-c1f4-457c-86f1-901618d2df1d', '99536433-0f23-4938-95af-37eca4d154b2', '签订安全管理协议情况', '②企业应当与承包单位、承租单位签订专门的安全生产管理协议，或者在承包合同、租赁合同中约定各自的安全生产管理职责。', '①查阅承包方资质等相关材料；②查阅安全管理协议或者承包合同，中是否有关于安全管理方面的规定。', '未与承包单位、承租单位签订专门的安全生产管理协议，或者未在承包合同、租赁合同中明确各自的安全生产管理职责', '《安全生产法》第四十六条', '《安全生产法》第一百条', '责令限期改正，可以处五万元以下的罚款，对其直接负责的主管人员和其他直接责任人员可以处一万元以下的罚款', '2', '已与承包单位、承租单位签订专门的安全生产管理协议，或者已在承包合同、租赁合同中明确各自的安全生产管理职责', 11, '外包管理', '企业的出租、发包合同或协议、相关资质的复印件、询问笔录等。');
INSERT INTO `t_check_list_copy` VALUES ('51cb87bb-7529-421f-b5be-b55c03ecfd13', '99536433-0f23-4938-95af-37eca4d154b2', '危险作业管理制度制定情况', '安全生产管理制度应当涵盖本单位的煤气、动火、有限空间、高处作业、抽堵盲板等危险作业管理，以及法律、法规、规章规定的其他内容。', '查阅制度文本。', '企业未制定动火、有限空间、高处作业、抽堵盲板等危险作业管理制度', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第七条', '《山东省生产经营单位安全生产主体责任规定》（省政府令第303号）第七条;《生产经营单位安全培训规定》第三十条', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2', '企业已制定动火、有限空间、高处作业、抽堵盲板等危险作业管理制度', 12, '危险作业管理', '辨识记录、管理台账、作业票证、作业方案及审批记录、现场照片、监护人员培训记录等');
INSERT INTO `t_check_list_copy` VALUES ('dcd7de3c-ebc1-4a0d-a3b1-cc661b1bdc7d', '99536433-0f23-4938-95af-37eca4d154b2', '企业危险作业审批、实施情况', '①动火作业必须按程序办理动火审批，取得作业许可证；许可证中要包括作业对象和环境的危害分析和可燃气体检测分析以及现场安全措施确认等内容。', '查看企业的危险作业票，作业票中检测、审批、安全措施确认等内容填写是否齐全。', '动火、有限空间等作业未按批准权限履行审批权限', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第三十一条。《工贸企业有限公司作业安全管理与监督管理规定》（总局59号令）第八条、第九条、第十二条、第十三条', '《工贸企业有限公司作业安全管理与监督管理规定》（总局59号令）第三十条第二、三项;《生产经营单位安全培训规定》第三十条', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2', '动火、有限空间等作业已按批准权限履行审批权限', 13, '危险作业管理', '辨识记录、管理台账、作业票证、作业方案及审批记录、现场照片、监护人员培训记录等');
INSERT INTO `t_check_list_copy` VALUES ('506a111a-4be2-4aca-a031-a288666606ad', '99536433-0f23-4938-95af-37eca4d154b2', '企业危险作业审批、实施情况', '②有限空间作业必须办理作业许可证，许可证中应体现作业现场通风、照明、警戒、防护、应急等措施落实情况，作业前对现场有毒有害气体检测情况；配足个体安全防护设备设施配备情况以及安排专人监护等情况。③煤气作业环节必须实行严格的审批管理制度。', '查看企业的危险作业票，作业票中检测、审批、安全措施确认等内容填写是否齐全。', '动火、有限空间作业前未按规定进行检测以及安全措施确认', '《山东省生产经营单位安全生产主体责任规定》（省政府令第260号）第三十一条。《工贸企业有限公司作业安全管理与监督管理规定》（总局60号令）第八条、第九条、第十二条、第十三条', '《工贸企业有限公司作业安全管理与监督管理规定》（总局60号令）第三十条第二、三项;《生产经营单位安全培训规定》第三十条', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2', '动火、有限空间作业前已按规定进行检测以及安全措施确认', 14, '危险作业管理', '辨识记录、管理台账、作业票证、作业方案及审批记录、现场照片、监护人员培训记录等');
INSERT INTO `t_check_list_copy` VALUES ('86f40697-faa8-482c-82cd-629107997704', '99536433-0f23-4938-95af-37eca4d154b2', '安全设备定期维护保养及检测情况', '金属冶炼企业必须对CO气体检测报警仪、可燃气体检测报警仪、空气呼吸器、压力表、安全阀等安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测应当作好记录，并由有关人员签字。', '检查企业安全设备台账及维护保养记录、安全设备定期检测报告', '未对CO气体检测报警仪、可燃气体检测报警仪、空气呼吸器、压力表、安全阀等安全设备进行经常性维护、保养，并定期检测', '《安全生产法》第三十三条', '《安全生产法》第九十六条第三项', '责令限期改正，可以处五万元以下的罚款', '2', '已对CO气体检测报警仪、可燃气体检测报警仪、空气呼吸器、压力表、安全阀等安全设备进行经常性维护、保养，并定期检测', 15, '安全设备管理', '企业的安全设备台账、维护保养记录、检测报告、特种设备合格证等复印件。');
INSERT INTO `t_check_list_copy` VALUES ('5efe9099-94e5-4a15-a8b9-69e56e4f618e', '99536433-0f23-4938-95af-37eca4d154b2', '劳动防护用品配备情况', '必须为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '查制度、配备标准及采购、发放台账；现场检查实物和佩戴情况。', '未为从业人员提供符合国家标准或者行业标准的劳动防护用品。', '《安全生产法》第四十二条、《山东省劳动防护用品配备标准》（DB37/1922—2011）、《用人单位劳动防护用品管理规范》', '《安全生产法》第九十六条第四项。', '责令限期改正，可以处五万元以下的罚款', '2', '已为从业人员提供符合国家标准或者行业标准的劳动防护用品', 16, '劳动防护用品管理', '劳保用品管理制度、配备标准、发放记录、标志、维护保养记录，现场照片。');
INSERT INTO `t_check_list_copy` VALUES ('8628e234-b126-40a9-85b1-1ea95b47aa29', '99536433-0f23-4938-95af-37eca4d154b2', '应急预案编制、评审、备案情况', '①生产经营单位应当根据有关法律、法规和国家标准结合本单位的危险源状况、危险性分析情况和可能发生的事故特点（如火灾、爆炸、触电和毒物逸散等事故类型），制定相应的应急预案，分为综合应急预案、专项应急预案和现场处置方案。', '查看企业的应急救援预案文本、评审记录以及备案登记表。', '未按照规定制定生产安全事故应急救援预案', '《生产安全事故应急预案管理办法》（总局88号令）第六条、第十二条、第二十一条、第二十六条。《生产经营单位生产安全事故应急预案编制导则》（GB/T29639-2013）', '《安全生产法》第九十四条第六项;《生产安全事故应急预案管理办法》第四十四条第一项', '责令其限期改正;逾期未改正的，责令停产停业整顿，并处2万元以下的罚款', '2', '已按照规定制定生产安全事故应急救援预案', 17, '应急管理', '企业应急预案文本、重要设备设施台账等资料的复印件。');
INSERT INTO `t_check_list_copy` VALUES ('b63e634c-e653-461b-ad8e-beb858264f72', '99536433-0f23-4938-95af-37eca4d154b2', '应急预案编制、评审、备案情况', '②应当对本单位编制的应急预案进行评审，并形成书面评审纪要。', '查看企业的应急救援预案文本、评审记录以及备案登记表。', '未按规定开展应急预案评审', '《生产安全事故应急预案管理办法》（总局88号令）第六条、第十二条、第二十一条、第二十六条。《生产经营单位生产安全事故应急预案编制导则》（GB/T29639-2013）', '《生产安全事故应急预案管理办法》（总局88号令）第四十五条第二项', '责令限期改正，可以处1万元以上3万元以下罚款', '2', '已按规定开展应急预案评审', 18, '应急管理', '企业应急预案书面评审记录、专家签字复印件、询问笔录等');
INSERT INTO `t_check_list_copy` VALUES ('c18de7c0-8cf4-4051-9ef3-4570a437690e', '99536433-0f23-4938-95af-37eca4d154b2', '应急预案编制、评审、备案情况', '③应当按照隶属关系报所在地县级以上地方人民政府安全生产监督管理部门备案。', '查看企业的应急救援预案文本、评审记录以及备案登记表。', '未按照规定进行应急预案备案', '《生产安全事故应急预案管理办法》（总局88号令）第六条、第十二条、第二十一条、第二十六条。《生产经营单位生产安全事故应急预案编制导则》（GB/T29639-2013）', '《生产安全事故应急预案管理办法》（总局88号令）第四十五条第三项', '责令限期改正，可以处1万元以上4万元以下罚款', '2', '已按照规定进行应急预案备案', 19, '应急管理', '企业应急预案公布文件、备案登记表等复印件');
INSERT INTO `t_check_list_copy` VALUES ('92c3db3c-b218-4705-b423-9937d2fc1df5', '99536433-0f23-4938-95af-37eca4d154b2', '应急演练情况', '应当制定应急预案演练计划，每年至少组织一次综合或者专项应急预案演练，每半年至少组织一次现场处置方案演练。', '查看企业的演练计划和2016、2017年度应急演练台账、记录。', '未按照规定定期组织应急预案演练', '《生产安全事故应急预案管理办法》（总局88号令）第33条', '《生产安全事故应急预案管理办法》（总局88号令）第四十四条第二项', '责令限期改正，可以处五万元以下的罚款；逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款', '2', '已按照规定定期组织应急预案演练', 20, '应急管理', '企业的应急演练记录、签到表、照片等复印件。');
INSERT INTO `t_check_list_copy` VALUES ('32c8b299-aaf7-4d95-995b-93264723ce3d', '99536433-0f23-4938-95af-37eca4d154b2', '建立健全煤气系统安全管理制度情况', '至少包括：煤气设施技术档案管理制度；煤气设施运行管理制度；煤气设施检查制度；煤气设施检修管理制度；进入涉煤气有限空间管理制度；煤气设施动火审批管理制度等。', '查看企业煤气作业管理制度文本。', '未制定煤气设施技术档案管理、运行管理、检查、检修管理、进入涉煤气有限空间管理、动火审批等制度', '《工业企业煤气安全规程》（GB6222-2005）', '《工业企业煤气安全规程》（GB6222-2005）', '21', '2', '已制定煤气设施技术档案管理、运行管理、检查、检修管理、进入涉煤气有限空间管理、动火审批等制度', 21, '煤气管理', '企业的安全设备台账、维护保养记录、检测报告、特种设备合格证等复印件。');
INSERT INTO `t_check_list_copy` VALUES ('19cfe36f-dd88-42f2-abb4-5787b14b551d', '99536433-0f23-4938-95af-37eca4d154b2', '煤气防护机构设置情况', '每个生产、供应和使用煤气的企业，应设煤气防护站或煤气防护组，并配备必要的人员，建立紧急救护体系。', '查看设立文件，现场查看防护站设置情况。', '生产、供应和使用煤气的企业，未设置煤气防护站或煤气防护组', '《工业企业煤气安全规程》（GB6222）', '《工业企业煤气安全规程》（GB6222-2005）', '22', '2', '生产、供应和使用煤气的企业，已设置煤气防护站或煤气防护组', 22, '煤气管理', '企业的安全设备台账');
INSERT INTO `t_check_list_copy` VALUES ('9dee14a0-bfb8-4efb-a2a2-a1f5a8b091e8', '99536433-0f23-4938-95af-37eca4d154b2', '煤气设备设施档案台账管理情况', '主要包括：煤气设备设施设计文件；煤气主要设备图纸；煤气设备、管道检验检测报告；煤气设备、管道日常检查检测记录；煤气系统工艺流程图；煤气设备设施巡检记录；煤气动火审批记录、动火证；煤气管道壁厚检测、支架检测、气柜基础沉降检测记录和评估报告；煤气监测、防护、救护设备台账等。', '查看煤气设备设施台账。', '未建立健全煤气设备设施档案台账', '《关于进一步加强全省金属冶炼企业安全生产工作的意见》（鲁安监发〔2016〕77号）', '《工业企业煤气安全规程》（GB6222-2005）第4.9条', '23', '2', '已建立健全煤气设备设施档案台账', 23, '煤气管理', '企业的安全设备台账');
INSERT INTO `t_check_list_copy` VALUES ('1b21079d-811c-4944-b5dc-136245f46c62', '99536433-0f23-4938-95af-37eca4d154b2', '查体', '委托取得相应资质的卫生机构进行在岗期间的职业健康检查；', '查阅职业卫生档案，明确查体人员、查体项目和周期；查看医疗卫生机构资质证书，确认资质符合要求；查看查体报告书，确认检查情况是否符合要求；从接害人员名单中随机抽取3人询问查体情况。', '未委托取得相应资质的机构对接害人员进行在岗期间职业健康检查', '《职业病防治法》第三十五条第一款，《职业健康监护技术规范GBZ188-2014》', '《职业病防治法》第七十一条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已委托取得相应资质的机构对接害人员进行在岗期间职业健康检查', 24, '职业健康检查', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('085a2c3c-251d-4890-aac6-5b08eb7c6731', '99536433-0f23-4938-95af-37eca4d154b2', '查体', '对全部接害劳动者进行在岗期间的职业健康检查；', '查阅职业卫生档案，明确查体人员、查体项目和周期；查看医疗卫生机构资质证书，确认资质符合要求；查看查体报告书，确认检查情况是否符合要求；从接害人员名单中随机抽取4人询问查体情况。', '未组织全部接害人员进行职业健康检查', '《职业病防治法》第三十五条第一款，《职业健康监护技术规范GBZ188-2014》', '《职业病防治法》第七十一条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已组织全部接害人员进行职业健康检查', 25, '职业健康检查', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('e13a6a00-b1a0-4604-a89c-b0fdb2d67b7b', '99536433-0f23-4938-95af-37eca4d154b2', '查体', '职业健康检查项目符合《GBZ188》等要求（化学因素：粉尘；物理因素；特殊作业人员：电工，高处）。', '查阅职业卫生档案，明确查体人员、查体项目和周期；查看医疗卫生机构资质证书，确认资质符合要求；查看查体报告书，确认检查情况是否符合要求；从接害人员名单中随机抽取5人询问查体情况。', '职业健康检查周期超过规定期限', '《职业病防治法》第三十五条第一款，《职业健康监护技术规范GBZ188-2014》', '《职业病防治法》第七十一条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '组织了新的职业健康检查', 26, '职业健康检查', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('dd84e81a-efa0-451c-8007-0a0f63e64287', '99536433-0f23-4938-95af-37eca4d154b2', '查体', '职业健康检查项目和检查周期符合《GBZ188》等要求（化学因素：铅及其无机化合物，血铅含量400μ/L～600μ/L或者尿铅70μ/L～120μ/L，每3个月复查血铅或者尿铅1次，血铅含量<400μ/L或者尿铅<70μ/L，查体周期1年，锰及其无机化合物1年，一氧化碳3年；粉尘：矽尘Ⅰ级作业2年、Ⅱ级及以上1年，煤尘Ⅰ级作业3年、Ⅱ级及以上2年，其他无机粉尘（铸造粉尘、铝尘、电焊烟尘）Ⅰ级作业4年、Ⅱ级及以上2~3年，棉尘Ⅰ级作业4~5年、Ⅱ级及以上作业2~3年；物理因素：噪音（8h等效声级≥85dB，1年；8h等效声级≥80dB，< 85dB，2年）、手动传动2年、高温1年、紫外辐射2年、微波2年；特殊作业人员：电工作业2年，高处作业1年）。', '查阅职业卫生档案，明确查体人员、查体项目和周期；查看医疗卫生机构资质证书，确认资质符合要求；查看查体报告书，确认检查情况是否符合要求；从接害人员名单中随机抽取6人询问查体情况。', '职业健康检查项目不全', '《职业病防治法》第三十五条第一款，《职业健康监护技术规范GBZ188-2014》', '《职业病防治法》第七十一条第四项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '组织了全部的职业健康检查项目', 27, '职业健康检查', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('05e3040e-72cb-4f9d-b3e1-a611e0088a72', '99536433-0f23-4938-95af-37eca4d154b2', '查体结果异常处理措施', '根据查体情况对有职业禁忌的劳动者，调离或者暂时脱离原工作岗位；对健康损害可能与所从事的职业相关的劳动者，进行妥善安置；对需要复查的劳动者，按照职业健康检查机构要求的时间安排复查和医学观察；对疑似职业病病人，按照职业健康检查机构的建议安排其进行医学观察或者职业病诊断。', '查看查体报告书，确认查体结果异常人员，核实用人单位根据查体结果采取相应措施的情况。', '未根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施', '《用人单位职业健康监护监督管理办法》第十七条', '《用人单位职业健康监护监督管理办法》第二十六条第五项', '给予警告，责令限期改正，可以并处3万元以下的罚款：', '2', '已根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施', 28, '职业健康检查', '查体报告，复查报告，调离通知单，监护档案。');
INSERT INTO `t_check_list_copy` VALUES ('b7ff55c4-b151-4918-a0a2-0c98005c620f', '99536433-0f23-4938-95af-37eca4d154b2', '定期检测', '委托具有相应资质机构对其存在职业病危害因素的工作场所进行检测。', '查阅职业卫生档案，确定存在职业病危害的场所和危害因素；查看检测机构资质证明及检测报告，现场核对职业病危害场所和因素是否进行全面检测。', '未委托具有相应资质的机构进行检测', '《职业病防治法》第二十六条第二款，《用人单位职业病危害因素定期检测管理规范》第四条，《职业病危害因素分类目录》', '《职业病防治法》第七十二条第四项', '给予警告，责令限期改正，逾期不改正的，处五万元以上二十万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2', '已委托具有相应资质的机构进行检测', 29, '职业病危害因素检测', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('cc2f6bc8-5034-4293-a8d7-2a7c30540ebd', '99536433-0f23-4938-95af-37eca4d154b2', '定期检测', '每年至少一次全面检测', '查阅职业卫生档案，确定存在职业病危害的场所和危害因素；查看检测机构资质证明及检测报告，现场核对职业病危害场所和因素是否进行全面检测。', '未定期对工作场所进行职业病危害因素检测', '《职业病防治法》第二十六条第二款，《用人单位职业病危害因素定期检测管理规范》第四条，《职业病危害因素分类目录》', '《职业病防治法》第七十二条第四项', '给予警告，责令限期改正，逾期不改正的，处五万元以上二十万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2', '已定期对工作场所进行职业病危害因素检测', 30, '职业病危害因素检测', '职业卫生检测和评价报告，申报表');
INSERT INTO `t_check_list_copy` VALUES ('f90cdb5d-b269-475c-ab3e-f85656778dd9', '99536433-0f23-4938-95af-37eca4d154b2', '定期检测', '进行全面检测', '查阅职业卫生档案，确定存在职业病危害的场所和危害因素；查看检测机构资质证明及检测报告，现场核对职业病危害场所和因素是否进行全面检测。', '存在职业病危害的场所和危害因素检测不全。', '《职业病防治法》第二十六条第二款，《用人单位职业病危害因素定期检测管理规范》第四条，《职业病危害因素分类目录》', '《职业病防治法》第七十二条第四项', '给予警告，责令限期改正，逾期不改正的，处五万元以上二十万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2', '存在职业病危害的场所和危害因素已经全部检测', 31, '职业病危害因素检测', '职业卫生检测和评价报告，申报表');
INSERT INTO `t_check_list_copy` VALUES ('e41eb229-5b03-4c77-8677-49f333597f20', '99536433-0f23-4938-95af-37eca4d154b2', '超标处理', '检测结果中职业病危害因素浓度或强度超过职业接触限值的，用人单位应结合实际情况立即进行整改。', '查看检测报告，明确职业病危害因素浓度或强度超标岗位；查看超标岗位治理方案和整改情况。', '工作场所职业病危害因素经治理仍不符合国家职业卫生标准和卫生要求而未停止作业', '《职业病防治法》第二十六条第四款', '《职业病防治法》第七十二条第五项', '给予警告，责令限期改正，逾期不改正的，处五万元以上二十万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭', '2', '工作场所职业病危害因素经治理符合国家职业卫生标准', 32, '职业病危害因素检测', '职业卫生检测和评价报告，申报表');
INSERT INTO `t_check_list_copy` VALUES ('b771f0b5-9315-434a-989f-71e735a8b1a4', '99536433-0f23-4938-95af-37eca4d154b2', '初次申报', '及时、如实申报产生职业病危害的项目。申报内容：用人单位的基本情况（单位名称、注册地址、工作场所地址、法定代表人、劳动者总人数）、职业病危害因素种类、接触人数以及分布情况（作业场所名称、职业病危害因素名称及现场浓度(强度)、接触人员）等。', '查阅职业卫生档案，现场检查接害岗位和工种，核对申报内容与工作场所职业病危害实际状况是否一致。', '未及时、如实申报产生职业病危害项目', '《职业病防治法》第十六条第二款', '《职业病防治法》第七十一条第一项', '责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款', '2', '已申报产生职业病危害项目', 33, '职业病危害项目申报', '营业执照，人员花名册，职业卫生检测和评价报告，申报表，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('e0407f1c-f6af-4a57-9bfc-282972ba937d', '99536433-0f23-4938-95af-37eca4d154b2', '变更申报', '按规定申报变更职业病危害项目内容（用人单位因技术、工艺、设备或者材料等发生变化导致原申报的职业病危害因素及其相关内容发生重大变化，工作场所、名称、法定代表人或者主要负责人发生变化，应当自发生变化之日起15日内进行变更申报；经过职业病危害因素检测、评价，发现原申报内容发生变化的，应当自收到有关检测、评价结果之日起15日内进行变更申报；进行新建、改建、扩建、技术改造或技术引进建设项目的，自建设项目竣工验收之日起30日内进行变更申报）。', '查阅职业卫生档案，申报回执，询问相关负责人，确认用人单位基本情况、技术、工艺、设备、职业病危害因素是否发生变化，是否存在新改扩建、技术改造等建设项目。', '未申报变更职业病危害项目内容', '《职业病危害项目申报办法》第八条', '《职业病危害项目申报办法》第十五条', '责令限期改正，可以并处5000元以上3万元以下的罚款', '2', '已申报变更职业病危害项目内容', 34, '职业病危害项目申报', '营业执照，人员花名册，职业卫生检测和评价报告，申报表，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('8cfe946b-6c34-4d1f-8107-709b66b64c22', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全生产管理机构设置和安全生产管理人员配备', '①从业人员不足100人的，应当设置安全生产管理机构，配备专职安全生产管理人员；', '查看能证明员工数量的相关资料；查看任命文件、注册安全工程师执业证。', '①未设置安全生产管理机构或从业人员在300人以上的未设置专门的安全生产管理机构；', '《安全生产法》第21、24条、《山东省生产经营单位安全生产主体责任规定》第9条。', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '①已设置安全生产管理机构或从业人员在300人以上的未设置专门的安全生产管理机构；', 1, '组织机构', '机构组建文件、任命文件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('31f43a7d-b555-4c87-8839-4304cd2af323', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全生产管理机构设置和安全生产管理人员配备', '②从业人员在100人以上不足300人的，设置安全生产管理机构，并配备2名以上专职安全生产管理人员，其中至少应当有1名注册安全工程师；', '查看能证明员工数量的相关资料；查看任命文件、注册安全工程师执业证。', '②配备专职安全生产管理人员数量不符合规定；', '《安全生产法》第21、24条、《山东省生产经营单位安全生产主体责任规定》第9条。', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '②配备专职安全生产管理人员数量已符合规定；', 2, '组织机构', '机构组建文件、任命文件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('b3b0474f-58f6-4573-8e83-c50ee2311a73', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全生产管理机构设置和安全生产管理人员配备', '③从业人员在300人以上不足1000人的，应当设置专门的安全生产管理机构，并按不低于从业人员5‰但最低不少于3名的比例配备专职安全生产管理人员，其中至少应当有2名注册安全工程师；', '查看能证明员工数量的相关资料；查看任命文件、注册安全工程师执业证。', '③专职安全生产管理人员名单与任命文件不符；', '《安全生产法》第21、24条、《山东省生产经营单位安全生产主体责任规定》第9条。', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '③专职安全生产管理人员名单与任命文件相符；', 3, '组织机构', '机构组建文件、任命文件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('86b4f114-38e6-4b50-a8b2-eadd99af7992', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全生产管理机构设置和安全生产管理人员配备', '④从业人员在应当1000人以上的，应当设置专门的安全生产管理机构，并按不低于从业人员5‰的比例配备专职安全生产管理人员，其中至少应当有3名注册安全工程师。', '查看能证明员工数量的相关资料；查看任命文件、注册安全工程师执业证。', '④未按照规定数量配备注册安全工程师；', '《安全生产法》第21、24条、《山东省生产经营单位安全生产主体责任规定》第9条。', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '④已按照规定数量配备注册安全工程师；', 4, '组织机构', '机构组建文件、任命文件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('35c4c9ad-9464-4934-aab6-17e267f6bae0', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全生产管理机构设置和安全生产管理人员配备', '⑤提供的注册安全工程师不是本公司专职安全生产管理人员，且未与中介机构签订安全生产技术、管理服务委托协议。', '查看能证明员工数量的相关资料；查看任命文件、注册安全工程师执业证。', '⑤提供的注册安全工程师不是本公司专职安全生产管理人员，且未与中介机构签订安全生产技术、管理服务委托协议。', '《安全生产法》第21、24条、《山东省生产经营单位安全生产主体责任规定》第9条。', '《安全生产法》第九十四条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '⑤提供的注册安全工程师是本公司专职安全生产管理人员，且已与中介机构签订安全生产技术、管理服务委托协议。', 5, '组织机构', '机构组建文件、任命文件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('b3535c30-acdd-4c10-b9de-9acc4de4334f', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', ' 安全生产委员会建立及安全总监设置', '①从业人员在300人以上的危化品生产企业应当设置安全总监并建立本单位的安全生产委员会；', '查看任命文件、机构组建文件及会议通知、纪要等资料。', '未按规定设置安全生产委员会、安全总监。', '《山东省生产经营单位安全生产主体责任规定》第11条、第12条。', '《山东省生产经营单位安全生产主体责任规定》第三十五条第四项', '由安全生产监督管理部门或者有关部门责令限期改正，可处以5000元以上2万元以下的罚款，对其主要负责人处以1000元以上1万元以下的罚款；逾期不改正的，责令限期整顿，可处以2万元以上3万元以下的罚款，对其主要负责人处以1万元以上2万元以下的罚款；涉嫌犯罪的，依法追究刑事责任：', '2', '已按规定设置安全生产委员会、安全总监。', 6, '组织机构', '机构组建文件、任命文件、安全组织网络图及会议通知、纪要等资料。');
INSERT INTO `t_check_list_copy` VALUES ('faf64031-b964-49f5-a899-9ce510ba36c9', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', ' 安全生产委员会建立及安全总监设置', '②安全生产委员会由本单位的主要负责人、分管安全生产的负责人(安全总监)、相关负责人、专门的安全生产管理机构及相关机构负责人、安全生产管理人员和工会代表以及从业人员代表组成；', '查看任命文件、机构组建文件及会议通知、纪要等资料。', '未按规定设置安全生产委员会、安全总监。', '《山东省生产经营单位安全生产主体责任规定》第11条、第12条。', '《山东省生产经营单位安全生产主体责任规定》第三十五条第四项', '由安全生产监督管理部门或者有关部门责令限期改正，可处以5000元以上2万元以下的罚款，对其主要负责人处以1000元以上1万元以下的罚款；逾期不改正的，责令限期整顿，可处以2万元以上3万元以下的罚款，对其主要负责人处以1万元以上2万元以下的罚款；涉嫌犯罪的，依法追究刑事责任：', '2', '已按规定设置安全生产委员会、安全总监。', 7, '组织机构', '机构组建文件、任命文件、安全组织网络图及会议通知、纪要等资料。');
INSERT INTO `t_check_list_copy` VALUES ('b0b7ef7e-fd21-4559-bbbd-2846c6f07073', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', ' 安全生产委员会建立及安全总监设置', '③安全生产委员会每季度至少召开一次会议，会议应当有书面记录。', '查看任命文件、机构组建文件及会议通知、纪要等资料。', '未按规定设置安全生产委员会、安全总监。', '《山东省生产经营单位安全生产主体责任规定》第11条、第12条。', '《山东省生产经营单位安全生产主体责任规定》第三十五条第四项', '由安全生产监督管理部门或者有关部门责令限期改正，可处以5000元以上2万元以下的罚款，对其主要负责人处以1000元以上1万元以下的罚款；逾期不改正的，责令限期整顿，可处以2万元以上3万元以下的罚款，对其主要负责人处以1万元以上2万元以下的罚款；涉嫌犯罪的，依法追究刑事责任：', '2', '已按规定设置安全生产委员会、安全总监。', 8, '组织机构', '机构组建文件、任命文件、安全组织网络图及会议通知、纪要等资料。');
INSERT INTO `t_check_list_copy` VALUES ('f2eeddba-8c52-4a4e-9993-87dc7ae0e346', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '主要负责人及安全管理人员培训', '主要负责人、分管安全生产的负责人或者安全总监、安全生产管理人员经过培训，并由负有安全生产监督管理职责的主管部门对其安全生产知识和管理能力考核合格。', '查阅相关安全培训合格证书。', '主要负责人、分管安全生产的负责人或者安全总监、安全生产管理人员未经过培训，并由负有安全生产监督管理职责的主管部门对其安全生产知识和管理能力考核合格。', '《安全生产法》第24条、《山东省生产经营单位安全生产主体责任规定》第25条、《生产经营单位安全培训规定》第9条。', '《安全生产法》第九十四条第二项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '主要负责人、分管安全生产的负责人或者安全总监、安全生产管理人员已经过培训，并由负有安全生产监督管理职责的主管部门对其安全生产知识和管理能力考核合格。', 9, '教育培训', '任命文件复印件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('58a8539f-c581-4f28-9ca6-f8b0aa321cdc', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '特种作业人员培训', '①特种作业人员按照国家有关规定经专门的安全作业培训，取得相应资格后上岗作业，包括电工作业操作证、焊接与热切割作业操作证、高处作业操作证、制冷与空调作业操作证、危险化学品安全作业操作证等（重点检查15种危险化工工艺过程操作及化工自动化控制仪表作业操作证）；', '查看特种作业人员档案；', '特种作业人员未按照国家有关规定经专门的安全作业培训并取得相应资格上岗作业。①未建立健全特种作业人员管理档案的；②危险化工工艺岗位现场操作人员（主、副操）无证上岗；③危险化工工艺岗位DCS自控操作人员（主、副操）无证上岗；④化工自动化控制仪表安装、维修、维护作业人员无证上岗；⑤特种作业操作证有效期届满未延期换证；⑥特种作业操作证因未按期复审而失效。', '《安全生产法》第27条，', '《特种作业人员安全技术培训考核管理规定》第三十八条《安全生产法》第九十四条第七项', '给予警告，并处1万元以下的罚款。责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '特种作业人员已按照国家有关规定经专门的安全作业培训并取得相应资格上岗作业。①已建立健全特种作业人员管理档案的；②危险化工工艺岗位现场操作人员（主、副操）持证上岗；③危险化工工艺岗位DCS自控操作人员（主、副操）持证上岗；④化工自动化控制仪表安装、维修、维护作业人员持证上岗；⑤特种作业操作证有效期届满已换证；⑥特种作业操作证已按期复审未失效', 10, '教育培训', '人员花名册、现场作业、操作记录图像、影像资料；询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('5563c690-f548-402a-96a7-87a9b9f60921', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '特种作业人员培训', '②生产经营单位应当加强对本单位特种作业人员的管理，建立健全特种作业人员培训、复审档案，做好申报、培训、考核、复审的组织工作和日常的检查工作。', '对照企业花名册和职业危害因素检测报告，对涉及危险工艺岗位操作人员逐一检查操作证，要求全查无遗漏。', '特种作业人员未按照国家有关规定经专门的安全作业培训并取得相应资格上岗作业。①未建立健全特种作业人员管理档案的；②危险化工工艺岗位现场操作人员（主、副操）无证上岗；③危险化工工艺岗位DCS自控操作人员（主、副操）无证上岗；④化工自动化控制仪表安装、维修、维护作业人员无证上岗；⑤特种作业操作证有效期届满未延期换证；⑥特种作业操作证因未按期复审而失效。', '《特种作业人员安全技术培训考核管理规定》第5、34条。', '《特种作业人员安全技术培训考核管理规定》第三十八条《安全生产法》第九十四条第七项', '给予警告，并处1万元以下的罚款。责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '特种作业人员已按照国家有关规定经专门的安全作业培训并取得相应资格上岗作业。①已建立健全特种作业人员管理档案的；②危险化工工艺岗位现场操作人员（主、副操）持证上岗；③危险化工工艺岗位DCS自控操作人员（主、副操）持证上岗；④化工自动化控制仪表安装、维修、维护作业人员持证上岗；⑤特种作业操作证有效期届满已换证；⑥特种作业操作证已按期复审未失效', 11, '教育培训', '人员花名册、现场作业、操作记录图像、影像资料；询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('d9edd0cb-5ec6-416e-b6f2-a61ead73442b', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '全员教育培训', '①定期组织全员安全生产教育培训，包括岗前、岗中、转岗教育等，并建立安全生产教育和培训档案，如实记录安全生产教育和培训的时间、内容、参加人员以及考核结果等情况。', '查阅企业员工名单、劳务派遣合同；', '①未对从业人员进行安全生产教育培训；②教育培训学时或培训内容达不到要求；③在本单位内调整工作岗位或离岗一年以上重新上岗;④未进行车间级和班组级培训；⑤单位采用新工艺、新技术、新材料或者使用新设备时，未对有关从业人员重新进行有针对性的安全培训；⑥未对被派遣劳动者或接收的中等职业学校、高等学校实习学生进行必要的安全生产教育和培训;', '《安全生产法》第25条、《山东省生产经营单位安全生产主体责任规定》第24条、《安全生产培训管理办法》第10条。', '《安全生产法》第九十四条第（三)项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '①已对从业人员进行安全生产教育培训；②教育培训学时或培训内容已达到要求；③在本单位内调整工作岗位或离岗一年以上不得重新上岗;④已进行车间级和班组级培训；⑤单位采用新工艺、新技术、新材料或者使用新设备时，已对有关从业人员重新进行有针对性的安全培训；⑥已对被派遣劳动者或接收的中等职业学校、高等学校实习学生进行必要的安全生产教育和培训;', 12, '教育培训', '企业员工花名册、相关员工操作记录、员工培训档案复印件；询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('ebfe854d-c51a-40cb-bc21-963ef581ca01', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '全员教育培训', '②生产经营单位使用被派遣劳动者的，应当将被派遣劳动者纳入本单位从业人员统一管理，对被派遣劳动者进行岗位安全操作规程和安全操作技能的教育和培训。劳务派遣单位应当对被派遣劳动者进行必要的安全生产教育和培训。', '抽查近2年来新进、岗中、转岗教育培训档案，共抽查10份，涵盖三类人员。', '①未对从业人员进行安全生产教育培训；②教育培训学时或培训内容达不到要求；③在本单位内调整工作岗位或离岗一年以上重新上岗;④未进行车间级和班组级培训；⑤单位采用新工艺、新技术、新材料或者使用新设备时，未对有关从业人员重新进行有针对性的安全培训；⑥未对被派遣劳动者或接收的中等职业学校、高等学校实习学生进行必要的安全生产教育和培训;', '《安全生产法》第25条、《山东省生产经营单位安全生产主体责任规定》第24条、《安全生产培训管理办法》第10条。', '《安全生产法》第九十四条第（三)项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '①已对从业人员进行安全生产教育培训；②教育培训学时或培训内容已达到要求；③在本单位内调整工作岗位或离岗一年以上不得重新上岗;④已进行车间级和班组级培训；⑤单位采用新工艺、新技术、新材料或者使用新设备时，已对有关从业人员重新进行有针对性的安全培训；⑥已对被派遣劳动者或接收的中等职业学校、高等学校实习学生进行必要的安全生产教育和培训;', 13, '教育培训', '企业员工花名册、相关员工操作记录、员工培训档案复印件；询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('1968b2f4-6112-46d8-af70-2056eec469ac', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '培训档案', '建立健全从业人员安全培训档案；', '查看从业人员安全培训档案', '①未建立健全从业人员安全培训档案；', '《安全生产法》第二十五条', '《安全生产法》第九十四条第四项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '①已建立健全从业人员安全培训档案；', 14, '教育培训', '培训记录、人员台帐、资格证书、考核记录等档案、资料复印件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('022fa6ff-ff98-42cf-9ad2-71d9a4e5b472', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '培训档案', '详细、准确安全记录培训的时间、内容、参加人员以及考核情况。', '查看从业人员安全培训档案', '②未详细、准确安全记录培训的时间、内容、参加人员以及考核情况。', '《安全生产法》第二十五条', '《安全生产法》第九十四条第四项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '②已详细、准确安全记录培训的时间、内容、参加人员以及考核情况。', 15, '教育培训', '培训记录、人员台帐、资格证书、考核记录等档案、资料复印件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('7a9ac22d-0670-444f-9515-8a66e6d0fba4', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '辨识、登记建档、备案', '①对重大危险源进行辨识，确定重大危险源等级并进行登记建档；', '查看重大危险源档案及备案资料。', '未按照标准对重大危险源进行辨识。①未对重大危险源及时、逐项登记建档或档案建立不齐全，缺少重大危险源基本特征表、涉及的所有化学品安全技术说明书、区域位置图、平面布置图、工艺流程图和主要设备一览表、重大危险源安全管理规章制度及安全操作规程、重大危险源关键装置、重点部位的责任人、责任机构名称、重大危险源场所安全警示标志的设置情况等；②未对重大危险源进行监控；③制定重大危险源专项应急预案；', '《安全生产法》第37条、《危险化学品重大危险源监督管理暂行规定》第7、8、22、23条。', '《危险化学品重大危险源监督管理暂行规定》第三十四条第一项《安全生产法》第九十八条第二项', '由县级以上人民政府安全生产监督管理部门给予警告，可以并处5000元以上3万元以下的罚款：责令限期改正，可以处十万元以下的罚款;逾期未改正的，责令停产停业整顿，并处十万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处二万元以上五万元以下的罚款;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '已按照标准对重大危险源进行辨识。①已对重大危险源及时、逐项登记建档或档案建立不齐全，缺少重大危险源基本特征表、涉及的所有化学品安全技术说明书、区域位置图、平面布置图、工艺流程图和主要设备一览表、重大危险源安全管理规章制度及安全操作规程、重大危险源关键装置、重点部位的责任人、责任机构名称、重大危险源场所安全警示标志的设置情况等；②已对重大危险源进行监控；③已制定重大危险源专项应急预案；', 16, '重大危险源', '重大危险源档案、监控记录、应急预案等资料复印件，询问相关人员。重大危险源装置、区域的图像、影像资料及能证明装置内危险化学品使用、储存量构成重大危险源的相关资料，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('11f74758-a2e2-4172-98de-63f371f4670d', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '辨识、登记建档、备案', '②生产经营单位应当按照国家有关规定将本单位重大危险源及有关安全措施、应急措施报有关地方人民政府安全生产监督管理部门和有关部门备案。', '查看重大危险源档案及备案资料。', '未按照标准对重大危险源进行辨识。①未对重大危险源及时、逐项登记建档或档案建立不齐全，缺少重大危险源基本特征表、涉及的所有化学品安全技术说明书、区域位置图、平面布置图、工艺流程图和主要设备一览表、重大危险源安全管理规章制度及安全操作规程、重大危险源关键装置、重点部位的责任人、责任机构名称、重大危险源场所安全警示标志的设置情况等；②未对重大危险源进行监控；③制定重大危险源专项应急预案；', '《安全生产法》第37条、《危险化学品重大危险源监督管理暂行规定》第7、8、22、23条。', '《危险化学品重大危险源监督管理暂行规定》第三十四条第一项《安全生产法》第九十八条第二项', '由县级以上人民政府安全生产监督管理部门给予警告，可以并处5000元以上3万元以下的罚款：责令限期改正，可以处十万元以下的罚款;逾期未改正的，责令停产停业整顿，并处十万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处二万元以上五万元以下的罚款;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '已按照标准对重大危险源进行辨识。①已对重大危险源及时、逐项登记建档或档案建立不齐全，缺少重大危险源基本特征表、涉及的所有化学品安全技术说明书、区域位置图、平面布置图、工艺流程图和主要设备一览表、重大危险源安全管理规章制度及安全操作规程、重大危险源关键装置、重点部位的责任人、责任机构名称、重大危险源场所安全警示标志的设置情况等；②已对重大危险源进行监控；③已制定重大危险源专项应急预案；', 17, '重大危险源', '重大危险源档案、监控记录、应急预案等资料复印件，询问相关人员。重大危险源装置、区域的图像、影像资料及能证明装置内危险化学品使用、储存量构成重大危险源的相关资料，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('d231e45f-97a9-4aba-ab1a-f3fc6ea55ad4', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '应急预案', '①是否依法制定重大危险源事故应急预案，建立应急救援组织或者配备应急救援人员，配备必要的防护装备及应急救援器材、设备、物资，并保障其完好和方便使用。', '查看重大危险源应急救援预案及演练计划、记录等资料。', '重大危险源专项应急预案未按规定进行备案。', '《危险化学品重大危险源监督管理暂行规定》第20条、第21条。', '《危险化学品重大危险源监督管理暂行规定》第三十四条第四项', '由县级以上人民政府安全生产监督管理部门给予警告，可以并处5000元以上3万元以下的罚款：', '2', '重大危险源专项应急预案已按规定进行备案。', 18, '重大危险源', '应急预案演练计划、已经进行的预案演练记录、评估报告等资料复印件，对相关人员进行询问。');
INSERT INTO `t_check_list_copy` VALUES ('90824ae7-cf97-4e8d-a12f-00a3e41f250e', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '应急预案', '②专项应急预案，每年至少进行一次；现场处置方案，每半年至少进行一次。', '查看重大危险源应急救援预案及演练计划、记录等资料。', '重大危险源专项应急预案未按规定进行备案。', '《危险化学品重大危险源监督管理暂行规定》第20条、第21条。', '《危险化学品重大危险源监督管理暂行规定》第三十四条第四项', '由县级以上人民政府安全生产监督管理部门给予警告，可以并处5000元以上3万元以下的罚款：', '2', '重大危险源专项应急预案已按规定进行备案。', 19, '重大危险源', '应急预案演练计划、已经进行的预案演练记录、评估报告等资料复印件，对相关人员进行询问。');
INSERT INTO `t_check_list_copy` VALUES ('5d300cfe-7b95-44c0-8d5a-4f2b83dbc0c8', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '预案演练', '生产经营单位应当制定本单位生产安全事故应急救援预案，并定期组织演练。', '查阅应急救援演练记录；', '①未按规定进行应急救援演练，或应急救援演练频次不符合要求（每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练）；②未按规定开展重大危险源事故应急预案演练，或救援演练频次不符合要求（对重大危险源专项应急预案每年至少进行一次，对重大危险源现场处置方案每半年至少进行一次）；③未对应急预案演练效果进行评估，无评估报告。', '《安全生产法》第78条、《生产安全事故应急预案管理办法》第33条。', '《危险化学品重大危险源监督管理暂行规定》第三十四条第六项、《安全生产法》第九十四条第六项', '由县级以上人民政府安全生产监督管理部门给予警告，可以并处5000元以上3万元以下的罚款：责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '①已按规定进行应急救援演练，或应急救援演练频次已符合要求（每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练）；②已按规定开展重大危险源事故应急预案演练，或救援演练频次已符合要求（对重大危险源专项应急预案每年至少进行一次，对重大危险源现场处置方案每半年至少进行一次）；③已对应急预案演练效果进行评估，有评估报告。', 20, '应急救援', '应急预案演练计划、已经进行的预案演练记录、评估报告等资料复印件，对相关人员进行询问。');
INSERT INTO `t_check_list_copy` VALUES ('ee966b08-df42-4361-873c-0f0a0c1fb65a', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '预案演练', '生产经营单位应当制定本单位的应急预案演练计划，根据本单位的事故风险特点，每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练。', '现场询问相关参加演练人员。', '①未按规定进行应急救援演练，或应急救援演练频次不符合要求（每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练）；②未按规定开展重大危险源事故应急预案演练，或救援演练频次不符合要求（对重大危险源专项应急预案每年至少进行一次，对重大危险源现场处置方案每半年至少进行一次）；③未对应急预案演练效果进行评估，无评估报告。', '《安全生产法》第78条、《生产安全事故应急预案管理办法》第33条。', '《危险化学品重大危险源监督管理暂行规定》第三十四条第六项、《安全生产法》第九十四条第六项', '由县级以上人民政府安全生产监督管理部门给予警告，可以并处5000元以上3万元以下的罚款：责令限期改正，可以处五万元以下的罚款;逾期未改正的，责令停产停业整顿，并处五万元以上十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款：', '2', '①已按规定进行应急救援演练，或应急救援演练频次已符合要求（每年至少组织一次综合应急预案演练或者专项应急预案演练，每半年至少组织一次现场处置方案演练）；②已按规定开展重大危险源事故应急预案演练，或救援演练频次已符合要求（对重大危险源专项应急预案每年至少进行一次，对重大危险源现场处置方案每半年至少进行一次）；③已对应急预案演练效果进行评估，有评估报告。', 21, '应急救援', '应急预案演练计划、已经进行的预案演练记录、评估报告等资料复印件，对相关人员进行询问。');
INSERT INTO `t_check_list_copy` VALUES ('71f40f58-3a49-4553-9871-8be1fec3db5e', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '劳动防护用品', '①是否按规定制定劳动防护用品配备标准；', '查看制度、标准、发放台账、培训记录；结合现场由专家检查佩戴情况。', '未为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '《安全生产法》第42、44条、《山东省生产经营单位安全生产主体责任规定》第21条。', '《安全生产法》第九十六条第四项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '已为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', 22, '安全防护', '发放标准、台账；人员现场作业图像、影像资料。');
INSERT INTO `t_check_list_copy` VALUES ('b124917b-18ac-4b5b-9e6a-86d091bd7f56', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '劳动防护用品', '②是否按规定采购、保管、发放、使用、更换、报废劳动防护用品；', '查看制度、标准、发放台账、培训记录；结合现场由专家检查佩戴情况。', '未为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '《安全生产法》第42、44条、《山东省生产经营单位安全生产主体责任规定》第21条。', '《安全生产法》第九十六条第四项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '已为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', 23, '安全防护', '发放标准、台账；人员现场作业图像、影像资料。');
INSERT INTO `t_check_list_copy` VALUES ('b29fdd15-7a12-4409-9baf-076913bfab34', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '劳动防护用品', '③是否为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '查看制度、标准、发放台账、培训记录；结合现场由专家检查佩戴情况。', '未为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', '《安全生产法》第42、44条、《山东省生产经营单位安全生产主体责任规定》第21条。', '《安全生产法》第九十六条第四项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '已为从业人员提供符合国家标准或者行业标准的劳动防护用品，并监督、教育从业人员按照使用规则佩戴、使用。', 24, '安全防护', '发放标准、台账；人员现场作业图像、影像资料。');
INSERT INTO `t_check_list_copy` VALUES ('a631c7aa-3ddb-4762-abb7-28e67187f725', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '资质审查', '生产经营单位将生产经营项目、场所、设备及交通运输工具发包或者出租的，应当对承包单位、承租单位的安全生产条件或者相应的资质进行审查，对不具备安全生产条件或者相应资质的，不得发包、出租。', '查阅承包单位、承租单位的相关资质文件。', '将生产经营项目、场所、设备发包或者出租给不具备安全生产条件或者相应资质的单位或者个人。', '《安全生产法》第46条', '《安全生产法》第一百条第一款', '责令限期改正，没收违法所得;违法所得十万元以上的，并处违法所得二倍以上五倍以下的罚款;没有违法所得或者违法所得不足十万元的，单处或者并处十万元以上二十万元以下的罚款;对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;导致发生生产安全事故给他人造成损害的，与承包方、承租方承担连带赔偿责任。', '2', '未将生产经营项目、场所、设备发包或者出租给不具备安全生产条件或者相应资质的单位或者个人。', 25, '承包租赁', '发包出租协议，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('01002b3d-20f8-44ea-a5a0-eac4db8f258a', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全生产管理协议', '生产经营项目、场所发包或者出租给其他单位的，生产经营单位应当与承包单位、承租单位签订专门的安全生产管理协议，或者在承包合同、租赁合同中约定各自的安全生产管理职责。', '查看安全管理协议或承包租赁合同。', '①未与承包单位、承租单位签订专门的安全生产管理协议或者未在承包合同、租赁合同中明确各自的安全生产管理职责；②未对承包、承租单位的安全生产统一协调、管理。', '《安全生产法》第46条', '《安全生产法》第一百条第二款', '责令限期改正，没收违法所得;违法所得十万元以上的，并处违法所得二倍以上五倍以下的罚款;没有违法所得或者违法所得不足十万元的，单处或者并处十万元以上二十万元以下的罚款;对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;导致发生生产安全事故给他人造成损害的，与承包方、承租方承担连带赔偿责任。', '2', '①已与承包单位、承租单位签订专门的安全生产管理协议或者未在承包合同、租赁合同中明确各自的安全生产管理职责；②已对承包、承租单位的安全生产统一协调、管理。', 26, '承包租赁', '承包承租合同、承包单位作业图像、影像、检查记录等资料复印件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('4446c16f-6ee8-4634-ae71-6c3e7d0d6895', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '协调、管理', '生产经营单位对承包单位、承租单位的安全生产工作统一协调、管理，定期进行安全检查，发现安全问题的，应当及时督促整改；', '查阅相关安全检查、督促整改及培训资料。', '①未与承包单位、承租单位签订专门的安全生产管理协议或者未在承包合同、租赁合同中明确各自的安全生产管理职责；②未对承包、承租单位的安全生产统一协调、管理。', '《安全生产法》第46条、《危险化学品从业单位安全标准化通用规范》。', '《安全生产法》第一百条第二款', '责令限期改正，没收违法所得;违法所得十万元以上的，并处违法所得二倍以上五倍以下的罚款;没有违法所得或者违法所得不足十万元的，单处或者并处十万元以上二十万元以下的罚款;对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;导致发生生产安全事故给他人造成损害的，与承包方、承租方承担连带赔偿责任。', '2', '①已与承包单位、承租单位签订专门的安全生产管理协议或者未在承包合同、租赁合同中明确各自的安全生产管理职责；②已对承包、承租单位的安全生产统一协调、管理。', 27, '承包租赁', '承包承租合同、承包单位作业图像、影像、检查记录等资料复印件，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('f6a24ab8-a343-43be-98b7-6e53be49806c', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '职业健康检查', '①委托取得相应资质的卫生机构对全部接害劳动者进行在岗期间的职业健康检查；', '查阅职业卫生档案，明确查体人员、查体项目和周期；查看医疗卫生机构资质证书，确认资质符合要求；查看查体报告书，确认检查情况是否符合要求；从接害人员名单中随机抽取3人询问查体情况。', '①未委托取得相应资质的机构对接害人员进行在岗期间的职业健康检查；②未组织全部接害人员进行职业健康检查；③职业健康检查周期超过规定期限；④职业健康检查项目不全。', '《职业病防治法》第三十五条第一款、《职业健康监护技术规范GBZ188》。', '《职业病防治法》第七十一条第四项', '由安全生产监督管理部门责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款：', '2', '①已委托取得相应资质的机构对接害人员进行在岗期间的职业健康检查；②已组织全部接害人员进行职业健康检查；③职业健康检查周期超过规定期限；④职业健康检查项目齐全。', 28, '职业卫生', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('cacf7abd-575f-4816-a3a7-a0d6dd04c555', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '职业健康检查', '②职业健康检查项目和检查周期符合《GBZ188》等要求（化学因素：苯、二硫化碳、汽油、氯气、氯丙烯、氮氧化物、氨、甲醛、焦炉逸散物查体周期1年，甲醇、四氯化碳、光气、一氧化碳、硫化氢、有机氟、氰及腈类化合物、酚类化合物、有机磷和氨基甲酸酯类杀虫剂3年，全血或红细胞胆碱酯酶活性测定半年1次（杀虫剂）；粉尘：电焊烟尘Ⅰ级作业4年、Ⅱ级及以上2～3年；物理因素：噪音（8h等效声级≥85dB，1年；8h等效声级≥80dB，<85dB，2年）、手动传动2年、高温1年、紫外辐射2年、微波2年；特殊作业人员：电工、压力容器作业2年，高处作业1年）。', '查阅职业卫生档案，明确查体人员、查体项目和周期；查看医疗卫生机构资质证书，确认资质符合要求；查看查体报告书，确认检查情况是否符合要求；从接害人员名单中随机抽取3人询问查体情况。', '①未委托取得相应资质的机构对接害人员进行在岗期间的职业健康检查；②未组织全部接害人员进行职业健康检查；③职业健康检查周期超过规定期限；④职业健康检查项目不全。', '《职业病防治法》第三十五条第一款、《职业健康监护技术规范GBZ188》。', '《职业病防治法》第七十一条第四项', '由安全生产监督管理部门责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款：', '2', '①已委托取得相应资质的机构对接害人员进行在岗期间的职业健康检查；②已组织全部接害人员进行职业健康检查；③职业健康检查周期超过规定期限；④职业健康检查项目齐全。', 29, '职业卫生', '人员花名册，未查体人员职业健康监护档案，查体机构资质证明、委托书，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('2d6bd264-a55c-42c9-a8ac-5c19811b4db9', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '查体结果异常处理措施', '根据查体情况对有职业禁忌的劳动者，调离或者暂时脱离原工作岗位；对健康损害可能与所从事的职业相关的劳动者，进行妥善安置；对需要复查的劳动者，按照职业健康检查机构要求的时间安排复查和医学观察；对疑似职业病病人，按照职业健康检查机构的建议安排其进行医学观察或者职业病诊断。', '查看查体报告书，确认查体结果异常人员，核实用人单位根据查体结果采取相应措施的情况。', '未根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施。', '《用人单位职业健康监护监督管理办法》第十七条', '《用人单位职业健康监护监督管理办法》第二十六条第五项', '给予警告，责令限期改正，可以并处3万元以下的罚款：', '2', '已根据职业健康检查情况采取相应（调离原工作岗位、复查、医学观察、职业病诊断等）措施。', 30, '职业卫生', '查体报告，复查报告，调离通知单，监护档案。');
INSERT INTO `t_check_list_copy` VALUES ('4508f2f0-534d-4af8-a6a8-98dc2e1a84fe', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '定期检测', '每年至少委托具有相应资质机构对其存在职业病危害因素的工作场所进行一次全面检测。', '查阅职业卫生档案，确定存在职业病危害的场所和危害因素；查看机构资质证明及检测报告，现场核对职业病危害场所和因素是否进行全面检测。', '①未委托具有相应资质的机构进行检测；②未定期对工作场所进行职业病危害因素检测；③存在职业病危害的场所和危害因素检测不全。', '《职业病防治法》第二十六条第二款、《用人单位职业病危害因素定期检测管理规范》第四条、《职业病危害因素分类目录》。', '《职业病防治法》第七十二条第四项', '由安全生产监督管理部门给予警告，责令限期改正，逾期不改正的，处五万元以上二十万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭：', '2', '①已委托具有相应资质的机构进行检测；②已定期对工作场所进行职业病危害因素检测；③存在职业病危害的场所和危害因素检测齐全。', 31, '职业卫生', NULL);
INSERT INTO `t_check_list_copy` VALUES ('70eea715-2f1f-4f09-b6f9-6cff7215008d', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '超标处理', '检测结果中职业病危害因素浓度或强度超过职业接触限值的，用人单位应结合实际情况立即进行整改。', '查看检测报告，明确职业病危害因素浓度或强度超标岗位；查看超标岗位治理方案和整改落实情况。', '工作场所职业病危害因素经治理仍不符合国家职业卫生标准和卫生要求而未停止作业。', '《职业病防治法》第二十六条第四款', '《职业病防治法》第七十二条第五项', '由安全生产监督管理部门给予警告，责令限期改正，逾期不改正的，处五万元以上二十万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令关闭：', '2', '工作场所职业病危害因素经治理仍不符合国家职业卫生标准和卫生要求已停止作业。', 32, '职业卫生', NULL);
INSERT INTO `t_check_list_copy` VALUES ('6f159149-adf2-46ff-8385-6b3b55214c6c', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '初次申报', '及时、如实申报产生职业病危害的项目。申报内容：用人单位的基本情况（单位名称、注册地址、工作场所地址、法定代表人、劳动者总人数）、职业病危害因素种类、接触人数以及分布情况（作业场所名称、职业病危害因素名称及现场浓度(强度)、接触人员）等。', '查阅职业卫生档案，现场检查接害岗位和工种，核对申报内容与工作场所职业病危害实际状况是否一致。', '未及时、如实申报产生职业病危害项目。', '《职业病防治法》第十六条第二款', '《职业病防治法》第七十一条第一项', '由安全生产监督管理部门责令限期改正，给予警告，可以并处五万元以上十万元以下的罚款：', '2', '已及时、如实申报产生职业病危害项目。', 33, '职业卫生', '营业执照，人员花名册，职业卫生检测和评价报告，申报表，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('fce81c89-8711-4ff6-b266-eeb8d4415cc9', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '变更申报', '按规定申报变更职业病危害项目内容（用人单位因技术、工艺、设备或者材料等发生变化导致原申报的职业病危害因素及其相关内容发生重大变化，工作场所、名称、法定代表人或者主要负责人发生变化，应当自发生变化之日起15日内进行变更申报；经过职业病危害因素检测、评价，发现原申报内容发生变化的，应当自收到有关检测、评价结果之日起15日内进行变更申报；进行新建、改建、扩建、技术改造或技术引进建设项目的，自建设项目竣工验收之日起30日内进行变更申报）。', '查阅职业卫生档案，申报回执，询问相关负责人，确认用人单位基本情况、技术、工艺、设备、职业病危害因素是否发生变化，是否存在新、改、扩建、技术改造等建设项目。', '未申报变更职业病危害项目内容。', '《职业病危害项目申报办法》第八条', '《职业病危害项目申报办法》第十五条', '责令限期改正，可以并处5千元以上3万元以下的罚款', '2', '已申报变更职业病危害项目内容。', 34, '职业卫生', '营业执照，人员花名册，职业卫生检测和评价报告，申报表，查体报告。');
INSERT INTO `t_check_list_copy` VALUES ('41ebd434-dc1d-4daa-a9fc-567355589fd1', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '淘汰工艺设备', '未使用国家明令淘汰的危及生产安全的工艺、设备。', '现场查看', '使用国家明令淘汰的危及生产安全的工艺、设备。', NULL, '《安全生产法》第九十六条第六项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '不再使用国家明令淘汰的危及生产安全的工艺、设备。', 35, '设施设备', '对现场在用设备进行拍照、摄像，询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('c3b104b4-441d-4296-8725-c3763d95599c', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '维护保养', '对安全设备进行经常性维护、保养和定期检测。', '查看对安全设备进行经常性维护、保养和定期检测记录', '未对安全设备进行经常性维护、保养和定期检测。', NULL, '《安全生产法》第九十六条第三项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '已对安全设备进行经常性维护、保养和定期检测。', 36, '设施设备', '对现场安全设备进行拍照、摄像，并询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('f13ee45f-289d-40e8-bcfa-50d6b409b45c', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '电气防爆', '易燃易爆区域使用防爆电气设备。', '现场查看', '易燃易爆区域使用非防爆电气设备。', NULL, '《安全生产法》第九十六条第二项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '易燃易爆区域已使用防爆电气设备。', 37, '设施设备', '对爆炸危险区域图进行拍照、复印等；对现场在用非防爆电器进行拍照、摄像等。');
INSERT INTO `t_check_list_copy` VALUES ('303b1224-0a1e-472d-8ceb-e085c136254d', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '安全联锁', '安全联锁正常投用或经审批摘除以及经审批后临时摘除未超过一个月恢复。', '现场查看或查看审批批文', '安全联锁未正常投用或未经审批摘除以及经审批后临时摘除超过一个月未恢复。', NULL, '《安全生产法》第九十六条第二项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '安全联锁已正常投用或已经审批摘除以及经审批后临时摘除未超过一个月已恢复。', 38, '设施设备', '对现场安全联锁装置、设计资料、安全联锁摘除审批资料进行拍照、摄像、复印等，并询问相关人员。');
INSERT INTO `t_check_list_copy` VALUES ('1cada0a6-43f8-43ab-9863-a738bbe64b66', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '标志设置', '在有较大危险因素的生产经营场所和有关设施、设备上设置明显的安全警示标志的。', '现场查看', '在有较大危险因素的生产经营场所和有关设施、设备上未设置明显的安全警示标志的。', NULL, '《安全生产法》第九十六条第一项', '责令限期改正，可以处五万元以下的罚款;逾期未改正的，处五万元以上二十万元以下的罚款，对其直接负责的主管人员和其他直接责任人员处一万元以上二万元以下的罚款;情节严重的，责令停产停业整顿;构成犯罪的，依照刑法有关规定追究刑事责任：', '2', '在有较大危险因素的生产经营场所和有关设施、设备上已设置明显的安全警示标志的。', 39, '警示标志', '对相关现场进行拍照、摄像等。');
INSERT INTO `t_check_list_copy` VALUES ('d69f6d41-c532-4d6f-9a02-1fe94250a88e', '90d8eba5-a0a9-4058-bec4-df99419ceb0b', '储存管理', '危险化学品储存在专用仓库内或按照标准分区、分类、分库存放，或不存在超量、超品种以及相互禁忌物质混放混存。', '现场查看', '危险化学品未储存在专用仓库内或未按照标准分区、分类、分库存放，或存在超量、超品种以及相互禁忌物质混放混存。', NULL, '《危险化学品安全管理条例》第八十条第四、五项', '由安全生产监督管理部门责令改正，处5万元以上10万元以下的罚款；拒不改正的，责令停产停业整顿直至由原发证机关吊销其相关许可证件，并由工商行政管理部门责令其办理经营范围变更登记或者吊销其营业执照；有关责任人员构成犯罪的，依法追究刑事责任：', '2', '危险化学品已储存在专用仓库内或已按照标准分区、分类、分库存放，或不存在超量、超品种以及相互禁忌物质混放混存。', 40, '危化品储存', '储存登记、出入库台账等资料复印件；现场储存情况图像、影像资料。');
INSERT INTO `t_check_list_copy` VALUES ('26218288-f9ab-43ef-862b-ac24f36b337e', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '铁水罐 钢水罐 中间罐 渣罐 铜包', '罐（包）修砌后，应保持干燥，并烘烤至要求温度方可使用。', '查看操作及检查记录，现场验证', '罐（包）修砌后，未烘烤，未达到规定要求温度投入使用', '《炼铁安全规程》14.3.1、8.1.4', '《炼铁安全规程》14.3.1、8.1.4', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '罐（包）修砌后，已烘烤，并已达到规定要求温度投入使用', 1, '高温熔融金属', '现场设备照片、起升设备的技术资料以及产品合格证等材料复印件');
INSERT INTO `t_check_list_copy` VALUES ('407e04e5-eca1-455e-80de-5c3fc89769ab', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '铁水罐 钢水罐 中间罐 渣罐 铜包', '罐（包）内不应有积水、潮湿杂物和易燃易爆物。', '现场验证', '罐（包）内有积水、潮湿杂物和易燃易爆物。', '《炼铁安全规程》14.3.3', '《炼铁安全规程》14.3.3', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '罐（包）内已无积水、潮湿杂物和易燃易爆物。', 2, '高温熔融金属', '现场设备照片、起升设备的技术资料以及产品合格证等材料复印件');
INSERT INTO `t_check_list_copy` VALUES ('109e4948-9dcc-4029-8995-166c16610b34', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '铁水罐 钢水罐 中间罐 渣罐 铜包', '耳轴部位应定期进行探伤检测（铁水罐、铜包每年一次）。凡耳轴出现内裂纹、壳体焊缝开裂、明显变形、耳轴磨损大于直径的10％、机械失灵、衬砖损坏超过规定，均应报修或报废。', '查看检测报告、检查记录，现场验证', '耳轴部位未定期进行探伤检测（铁水罐、铜包每年一次）。现场发现耳轴出现内裂纹、壳体焊缝开裂、明显变形、耳轴磨损大于直径的10％、机械失灵、衬砖损坏等违规使用情况。', '《炼铁安全规程》14.3.4、8.1.3；《铜冶炼安全生产规范》5.3.2.4', '《炼铁安全规程》14.3.4、8.1.3；《铜冶炼安全生产规范》5.3.2.4', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '耳轴部位已定期进行探伤检测（铁水罐、铜包每年一次）。现场已未发现耳轴出现内裂纹、壳体焊缝开裂、明显变形、耳轴磨损大于直径的10％、机械失灵、衬砖损坏等违规使用情况。', 3, '高温熔融金属', '现场设备照片、起升设备的技术资料以及产品合格证等材料复印件');
INSERT INTO `t_check_list_copy` VALUES ('4f2dcbd2-35a8-4721-aa0e-681ce46bd42e', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '起重机械', '用于炼钢厂的铸造起重机，在吊运重罐钢水、铁水或液渣时，应使用带有固定龙门钩的铸造起重机。', '查看检测报告、产品质量证明书、合格证等，检查各类记录，现场验证', '炼钢、连铸等工序在吊运重罐钢水、铁水或液渣时未使用带有固定龙门钩的铸造起重机。', '《冶金起重机技术条件第5部分：铸造起重机》4.3.2', '《冶金起重机技术条件第5部分：铸造起重机》4.3.2', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '炼钢、连铸等工序在吊运重罐钢水、铁水或液渣时已使用带有固定龙门钩的铸造起重机。', 4, '高温熔融金属', '现场设备照片、起升设备的技术资料以及产品合格证等材料复印件');
INSERT INTO `t_check_list_copy` VALUES ('421b39a5-0b40-4158-b173-9b94fe83a7d2', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '起重机械', '吊运熔融金属的起升机构，每套独立驱动装置应装有两个支持制动器（工作制动器）。', '查看检测报告、产品质量证明书、合格证等，检查各类记录，现场验证', '吊运熔融金属的起升机构，未设置两个支持制动器（工作制动器）。', '《起重机械安全规程》4.1.1、《起重机械安全技术监察规程－桥式起重机》第六十七条', '《起重机械安全规程》4.1.1、《起重机械安全技术监察规程－桥式起重机》第六十七条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '吊运熔融金属的起升机构，已设置两个支持制动器（工作制动器）。', 5, '高温熔融金属', '现场设备照片、起升设备的技术资料以及产品合格证等材料复印件');
