INSERT INTO `t_check_list_copy` VALUES ('69457e3a-8808-4275-8613-2d073d0d7ced', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '起重机械', '以电动葫芦作为起升机构吊运熔融的起重机额定起重量不得大于10t。', '查看检测报告、产品质量证明书、合格证等，检查各类记录，现场验证', '以电动葫芦作为起升机构吊运熔融的起重机额定起重量超过10t。', '《起重机械安全技术监察规程-桥式起重机》第六条', '《起重机械安全技术监察规程-桥式起重机》第六条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '以电动葫芦作为起升机构吊运熔融的起重机额定起重量已低于10t。', 7, '高温熔融金属', '现场设备照片、起升设备的技术资料以及产品合格证等材料复印件');
