INSERT INTO `t_check_list_copy` VALUES ('b8b1710a-6fd4-41c2-b2b9-481dbd1a3ca1', 'c1cd0041-48ec-44d0-a9ab-1fea6577e012', '电炉', '④电炉炉后出钢操作室，不应正对出钢方向开门，其窗户应采取防喷溅措施。', '查看安全评价报告，检查记录，现场验证', '④炉后出钢操作室正对出钢方向开门，其窗户未采取防喷溅措施。', '《炼钢安全规程》10.1.3、10.1.8、6.2.8、10.1.14', '《炼钢安全规程》10.1.3、10.1.8、6.2.8、10.1.14', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '④炉后出钢操作室正对出钢方向开门，其窗户已采取防喷溅措施。', 61, '炼钢单元', '现场照片');
