INSERT INTO `t_check_list_copy` VALUES ('620beb0c-abd4-4250-8620-eaa4c961ca47', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '安全标识', '化工装置区、油库、罐区、化学危险品仓库等危险区是否设置永久性“严禁烟火”标志。', '现场检查', '化工装置区、油库、罐区、化学危险品仓库等危险区应未设置永久性“严禁烟火”标志。', '《化工企业安全卫生设计规范》HG20571-2014第6.2.2条', '《化工企业安全卫生设计规范》HG20571-2014第6.2.2条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '化工装置区、油库、罐区、化学危险品仓库等危险区应已设置永久性“严禁烟火”标志。', 21, '现场管理', '对相关现场进行拍照、摄像等');
INSERT INTO `t_check_list_copy` VALUES ('a3b95946-658c-4366-b116-3f38a6e830d4', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '安全标识', '厂区内是否设置限高、限速标志。', '现场检查', '厂区内未设置限高、限速标志。', 'GB 4387-2008《工业企业厂内铁路、道路运输安全规程》第6.1.3条', 'GB 4387-2008《工业企业厂内铁路、道路运输安全规程》第6.1.3条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '厂区内已设置限高、限速标志。', 22, '现场管理', '对相关现场进行拍照、摄像等');
INSERT INTO `t_check_list_copy` VALUES ('01a0454a-0971-404c-859b-2b65fe75ad4e', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '设备及管理', '生产经营单位是否对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测应当作好记录，并由有关人员签字。', '查阅检验检测及维护保养资料，现场检查相关设施的运行情况。', '生产经营单位未对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测未作记录，未签字。', '《安全生产法》第33条', '《安全生产法》第33条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '生产经营单位已对安全设备进行经常性维护、保养，并定期检测，保证正常运转。维护、保养、检测已作记录，并签字。', 23, '设备安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('f6edb020-7708-4910-a36c-735b2a89e8aa', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '设备及管理', '在用装置（设施）安全阀或泄压排放系统等是否正常投用，安全附件校验是否按规定进行，相关台账资料是否齐全。', '查阅检验检测及维护保养资料，现场检查相关设施的运行情况。', '在用装置（设施）安全阀或泄压排放系统等未正常投用，安全附件校验未按规定进行，相关台账资料不齐全。', '《安全生产法》第33条、《固定式压力容器安全技术监察规程》第8.3.5条', '《安全生产法》第33条、《固定式压力容器安全技术监察规程》第8.3.5条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '在用装置（设施）安全阀或泄压排放系统等已正常投用，安全附件校验已按规定进行，相关台账资料已齐全。', 24, '设备安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('616a6e96-6b2e-4f89-8b13-90a34be3d885', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '设备及管理', '高速旋转或往复运动的机械零部件是否安装可靠的防护设施、挡板或安全围栏。', '现场检查', '高速旋转或往复运动的机械零部件未安装可靠的防护设施、挡板或安全围栏。', '《化工企业安全卫生设计规范》HG20571-2014第4.6.2条', '《化工企业安全卫生设计规范》HG20571-2014第4.6.2条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '高速旋转或往复运动的机械零部件已安装可靠的防护设施、挡板或安全围栏。', 25, '设备安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('5bdf507c-21ea-4695-9b26-d71b175d7658', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '设备及管理', '人员易触及的可动零部件，是否封闭或隔离。对运行过程中可能超过极限位置的生产设备或零部件，是否配置可靠的限位装置。', '现场检查', '人员易触及的可动零部件，未封闭或隔离。对运行过程中可能超过极限位置的生产设备或零部件，未配置可靠的限位装置。', '《生产设备安全卫生设计总则》GB5083-1999第6.1.1，6.1.3条', '《生产设备安全卫生设计总则》GB5083-1999第6.1.1，6.1.3条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '人员易触及的可动零部件，已封闭和隔离。对运行过程中可能超过极限位置的生产设备和零部件，已配置可靠的限位装置。', 26, '设备安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('24868751-8f24-4e61-a74a-8c240bc3f24d', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '电缆敷设', '爆炸性环境电缆和导线的选择是否符合下列规定：在爆炸危险区内，除在配电盘、接线箱或采用金属导管配线系统内，无护套的电线不应作为供配电线路。', '现场检查', '爆炸性环境电缆和导线的选择不符合下列规定：在爆炸危险区内，除在配电盘、接线箱或采用金属导管配线系统内，无护套的电线不应作为供配电线路。', '《爆炸危险环境电力装置设计规范》第5.4.1条', '《爆炸危险环境电力装置设计规范》第5.4.1条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '爆炸性环境电缆和导线的选择已符合下列规定：在爆炸危险区内，除在配电盘、接线箱或采用金属导管配线系统内，无护套的电线不应作为供配电线路。', 27, '电气安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('3e01ddf6-a358-45f1-8bb7-e06f4f0a8c4e', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '配电室', '变压器室、配电室、电容器室的门是否向外开启。相邻配电室之间有门时，此门是否能双向开启。', '现场检查', '变压器室、配电室、电容器室的门未向外开启。相邻配电室之间有门时，此门是不能双向开启。', '《20KV及以下变电所设计规范》GB50053-2013第6.2.2条', '《20KV及以下变电所设计规范》GB50053-2013第6.2.2条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '变压器室、配电室、电容器室的门已向外开启。相邻配电室之间有门时，此门是不能双向开启。', 28, '电气安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('accf20fb-7733-43c8-8855-f0369561a7e7', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '配电室', '高压配电室是否设置安全防护栏杆。', '现场检查', '高压配电室未设置安全防护栏杆。', '《3-110kv高压配电装置设计规范》（GB50060-2008）第5.4.8条 ', '《3-110kv高压配电装置设计规范》（GB50060-2008）第5.4.8条 ', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '高压配电室已设置安全防护栏杆。', 29, '电气安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('e4a6c77e-1000-455c-b7fa-8fea308b05f4', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '配电室', '绝缘靴、绝缘手套、绝缘拉杆等绝缘器材是否定期检测。', '现场检查', '绝缘靴、绝缘手套、绝缘拉杆等绝缘器材未定期检测。', '《带电作业用绝缘工具试验导则》（DL/T878-2004）第5.6条', '《带电作业用绝缘工具试验导则》（DL/T878-2004）第5.6条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '绝缘靴、绝缘手套、绝缘拉杆等绝缘器材已定期检测。', 30, '电气安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('c8ea8377-09a8-4d61-b384-f25264a103cb', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '危险化学品管理', '检查危险化学品储存是否在专用仓库、专用场地或者专用储存室（专用仓库）内，并由专人负责管理；剧毒化学品以及储存数量构成重大危险源的其他危险化学品，是否在专用仓库内单独存放，并实行双人收发、双人保管制度。剧毒化学品是否实行“五双”管理，即“双人保管、双人领取、双人使用、双把锁、双本帐”的管理制度。储存危险化学品的单位是否建立危险化学品出入库核查、登记制度。', '现场检查，查制度；着重询问相关人员对仓库/罐区物质的了解程度，如果相关人员无法准确回答所储存的物料名称、应急处置措施等内容，要加强对该人员教育培训的内容的检查。', '检查危险化学品储存未在专用仓库、专用场地或者专用储存室（专用仓库）内，并由专人负责管理；剧毒化学品以及储存数量构成重大危险源的其他危险化学品，未在专用仓库内单独存放，未实行双人收发、双人保管制度。剧毒化学品未实行“五双”管理，即“双人保管、双人领取、双人使用、双把锁、双本帐”的管理制度。储存危险化学品的单位未建立危险化学品出入库核查、登记制度。', '《危险化学品安全管理条例》第二十四、二十五条', '《危险化学品安全管理条例》第二十四、二十五条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '检查危险化学品储存已在专用仓库、专用场地或者专用储存室（专用仓库）内，并由专人负责管理；剧毒化学品以及储存数量构成重大危险源的其他危险化学品，已在专用仓库内单独存放，已实行双人收发、双人保管制度。剧毒化学品已实行“五双”管理，即“双人保管、双人领取、双人使用、双把锁、双本帐”的管理制度。储存危险化学品的单位已建立危险化学品出入库核查、登记制度。', 31, '储运安全', NULL);
INSERT INTO `t_check_list_copy` VALUES ('dda73127-6174-4de7-8e7f-217d54fb4ed7', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '①重大危险源是否配备温度、压力、液位、流量、组份等信息的不间断采集和监测系统以及可燃气体和有毒有害气体泄漏检测报警装置，并具备信息远传、连续记录、事故预警、信息存储等功能；', '现场检查，查阅记录', '①重大危险源未配备温度、压力、液位、流量、组份等信息的不间断采集和监测系统以及可燃气体和有毒有害气体泄漏检测报警装置，并具备信息远传、连续记录、事故预警、信息存储等功能；一级或者二级重大危险源，不具备紧急停车功能；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '①重大危险源已配备温度、压力、液位、流量、组份等信息的不间断采集和监测系统以及可燃气体和有毒有害气体泄漏检测报警装置，并具备信息远传、连续记录、事故预警、信息存储等功能；一级或者二级重大危险源，已具备紧急停车功能；', 32, '重大危险源', NULL);
INSERT INTO `t_check_list_copy` VALUES ('60a62c7f-4842-42dc-8253-fe9ec6fb51ff', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '②重大危险源的化工生产装置装备是否满足安全生产要求的自动化控制系统；一级或者二级重大危险源，装备紧急停车系统；', '现场检查，查阅记录', '②重大危险源的化工生产装置装备不满足安全生产要求的自动化控制系统；一级或者二级重大危险源，未装备紧急停车系统；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '②重大危险源的化工生产装置装备已满足安全生产要求的自动化控制系统；一级或者二级重大危险源，已装备紧急停车系统；', 33, '重大危险源', NULL);
INSERT INTO `t_check_list_copy` VALUES ('c3bf1954-af3f-4edb-b3e3-55bc949dcb68', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '③对重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施，是否设置紧急切断装置；毒性气体的设施，是否设置泄漏物紧急处置装置。涉及毒性气体、液化气体、剧毒液体的一级或者二级重大危险源，是否配备独立的安全仪表系统（SIS）；', '现场检查，查阅记录', '③对重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施，未设置紧急切断装置；毒性气体的设施，未设置泄漏物紧急处置装置。涉及毒性气体、液化气体、剧毒液体的一级或者二级重大危险源，未配备独立的安全仪表系统（SIS）；', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十三条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '③对重大危险源中的毒性气体、剧毒液体和易燃气体等重点设施，已设置紧急切断装置；毒性气体的设施，已设置泄漏物紧急处置装置。涉及毒性气体、液化气体、剧毒液体的一级或者二级重大危险源，已配备独立的安全仪表系统（SIS）；', 34, '重大危险源', NULL);
