INSERT INTO `t_check_list_copy` VALUES ('8990d6c3-d8f4-4304-a919-3d1fb685c992', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '③涉及易燃易爆气体或者易燃液体蒸气的重大危险源，检查是否配备一定数量的便携式可燃气体检测设备。', '现场检查', '③涉及易燃易爆气体或者易燃液体蒸气的重大危险源，未配备便携式可燃气体检测设备。', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第二十条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第二十条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '③涉及易燃易爆气体或者易燃液体蒸气的重大危险源，已配备便携式可燃气体检测设备。', 38, '重大危险源', NULL);
INSERT INTO `t_check_list_copy` VALUES ('c74235ce-e67c-4ae4-af40-3b301732efef', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '重大危险源监控及管理', '是否在重大危险源所在场所设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '现场检查', '危险化学品单位未在重大危险源所在场所设置明显的安全警示标志，写明紧急情况下的应急处置办法。', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十八条', '《危险化学品重大危险源监督管理暂行规定》国家安全生产监督管理总局令[2011]第40号第十八条', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '危险化学品单位已在重大危险源所在场所设置明显的安全警示标志，写明紧急情况下的应急处置办法。', 39, '重大危险源', NULL);
INSERT INTO `t_check_list_copy` VALUES ('231d74d3-f156-4596-b330-ebf0c1790515', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '是否落实特殊作业审批制度；特殊作业票填写是否符合规范，作业票与作业内容是否一致，签审是否齐全。', '查阅记录和现场检查、询问', '未落实特殊作业审批制度；特殊作业票填写不符合规范，作业票与作业内容不一致，签审不齐全。', '《化学品生产单位特殊作业安全规范》GB30871-2014', '《化学品生产单位特殊作业安全规范》GB30871-2014', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '已落实特殊作业审批制度；特殊作业票填写已符合规范，作业票与作业内容一致，签审已齐全。', 40, '特殊作业', NULL);
INSERT INTO `t_check_list_copy` VALUES ('24b49bef-6f8f-41a4-9b09-cfbcf6a7914b', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '特殊作业场所是否配备可燃、有毒气体（物质）检测报警仪、氧含量分析仪器、空气呼吸器。', '查阅记录和现场检查、询问', '特殊作业场所未配备可燃、有毒气体（物质）检测报警仪、氧含量分析仪器、空气呼吸器。', '《化学品生产单位特殊作业安全规范》GB30871-2014', '《化学品生产单位特殊作业安全规范》GB30871-2014', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '特殊作业场所已配备可燃、有毒气体（物质）检测报警仪、氧含量分析仪器、空气呼吸器。', 41, '特殊作业', NULL);
INSERT INTO `t_check_list_copy` VALUES ('4cf17dc6-f655-4863-8f8e-cc5cc6402c0f', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '动火作业是否按规定进行可燃气体分析，受限空间作业是否按规定进行可燃气体、氧含量和有毒气体分析，检验、检测项目与作业票规定是否一致。', '查阅记录和现场检查、询问', '动火作业未按规定进行可燃气体分析，受限空间作业按规定进行可燃气体、氧含量和有毒气体分析，检验、检测项目与作业票规定不一致。', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '动火作业已按规定进行可燃气体分析，受限空间作业按规定进行可燃气体、氧含量和有毒气体分析，检验、检测项目与作业票规定一致。', 42, '特殊作业', NULL);
INSERT INTO `t_check_list_copy` VALUES ('a8660fc0-2ce7-452b-b761-ae73fb8694a8', '0a6ce945-cb68-4f20-90fc-f80aebd3b23e', '特殊作业审批及现场管理', '特殊作业现场是否向作业人员进行安全技术交底；特殊作业过程是否有人持续监护，安全防护措施是否落实，是否持特殊作业票进行作业。', '查阅记录和现场检查、询问', '特殊作业现场未向作业人员进行安全技术交底；特殊作业过程无人员持续监护，安全防护措施未落实，未持特殊作业票进行作业。', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '《安全生产法》第40条、《化学品生产单位特殊作业安全规范》GB30871', '对检查中发现的安全生产违法行为，当场予以纠正或者要求限期改正；对依法应当给予行政处罚的行为，依照本法和其他有关法律、行政法规的规定作出行政处罚决定', '2', '特殊作业现场已向作业人员进行安全技术交底；特殊作业过程已有人员持续监护，安全防护措施已落实，并持特殊作业票进行作业。', 43, '特殊作业', NULL);
