INSERT INTO `t_check_result` VALUES ('cd420fa5-408d-44de-82d5-5b81cb22a3ae', '1', '371341201803070020', '913713003128266531', '168', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:47', '2,5', '1', '建设项目选址、生产规模、工艺、职业病危害因素种类、职业病防护设施发生重大变更时，未对变更内容重新进行职业病防护设施设计并办理有关手续，进行施工的', '《“三同时”监督管理暂行办法》第22条', '《“三同时”监督管理暂行办法》第35条（二）项', '给予警告，责令限期改正；逾期不改正的，处3万元以下的罚款', '', '1', '2018-03-07 21:47:15', '3.2 建设项目职业病防护设施设计和审查', '建设项目选址、生产规模、工艺、职业病危害因素种类、职业病防护设施发生重大变更时，已对变更内容重新进行职业病防护设施设计并办理有关手续，进行施工', '对照原防护设计，检查建设项目的生产规模、工艺或者职业病危害因素的种类等查看是否发生重大变更，查看职业病防护设施设计专篇、安全生产监督管理部门批准文件。', '建设项目职业病防护设施设计经审查同意后，建设项目的生产规模、工艺或者职业病危害因素的种类等发生重大变更的，建设单位应当根据变更的内容，重新进行职业病防护设施设计，并在变更之日起30日内按规定办理相应的审查手续。', NULL, NULL);
INSERT INTO `t_check_result` VALUES ('0711d9c3-7026-4f3b-9296-bf584fe5d0ca', '1', '371341201803070020', '913713003128266531', '114', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:47', '2,3,4,5', '1', '建设单位在职业病防护设施设计评审中弄虚作假的', '《“三同时”监督管理暂行办法》第18条第2款', '《“三同时”监督管理暂行办法》第36条', '责令改正，并处5千元以上3万元以下的罚款', '', '1', '2018-03-07 21:47:17', '3.2 建设项目职业病防护设施设计和审查', '建设单位会同设计单位已对职业病防护设施设计专篇进行完善，并对其真实性、合法性和实用性负责', '检查职业病防护设施设计专篇、评审专家组成、评审会相关材料。', '建设单位应当会同设计单位对职业病防护设施设计专篇进行完善，并对其真实性、合法性和实用性负责。', NULL, NULL);
