INSERT INTO `t_check_result` VALUES ('13fa046e-985d-4a4c-9b90-38ce3a6ae719', '1', '371341201803070020', '913713003128266531', '197', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:48', '2,3,5', '1', '职业病危害一般的建设项目竣工验收后，未经安全监管部门备案同意，擅自投入使用的', '《职业病防治法》第18条第3款、 《有毒作业条例》第13条第1款、 《监督管理规定》第14条、《“三同时”监督管理暂行办法》第28条', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭。 《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '', '1', '2018-03-07 21:47:32', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收', '职业病危害一般的建设项目竣工验收后，已经安全监管部门备案同意，投入使用', '查看安全生产监督管理部门职业病防护设施竣工备案回执等相关文件', '职业病危害一般的建设项目竣工验收时，由建设单位自行组织职业病防护设施的竣工验收，并自验收完成之日起30日内按照规定向安全生产监督管理部门申请职业病防护设施竣工备案提交下列文件、资料：（一）建设项目职业病防护设施竣工备案申请书；（二）建设项目职业病危害预评价报告备案通知书（复印件）；（三）建设项目立项审批文件（复印件）；（四）建设项目职业病防护设施设计专篇；（五）建设项目职业病危害控制效果评价机构的资质证明（影印件）；（六）建设项目职业病危害控制效果评价报告；（七）职业卫生专家对职业病危害控制效果评价报告的评审意见。（八）建设单位对职业病危害控制效果评价报告的评审意见；（九）建设项目职业病防护设施竣工自行验收情况报告；（十）法律、行政法规、规章规定的其他文件、资料。', NULL, NULL);
