INSERT INTO `t_check_result` VALUES ('f4c58a03-751b-41f3-b953-8724c235ef96', '1', '371341201803070020', '913713003128266531', '140', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:48', '2,5', '1', '职业病危害较重的建设项目职业病防护设施未经安监部门验收或验收不合格，擅自投入使用的', '《职业病防治法》第18条第3款、《有毒作业条例》第13条第1款、《监督管理规定》第14条、《“三同时”监督管理暂行办法》第29条', '《职业病防治法》第70条（四）项、《有毒作业条例》第58条（三）项、《“三同时”监督管理暂行办法》第34条（四）项', '《职业病防治法》《“三同时”监督管理暂行办法》给予警告，责令限期改正；逾期不改正的，处10万元以上50万元以下的罚款；情节严重的，责令停止产生职业病危害的作业，或者提请有关人民政府按照国务院规定的权限责令停建、关闭《有毒作业条例》给予警告，责令限期改正，处10万元以上50万元以下的罚款；逾期不改正的，提请有关人民政府按照国务院规定的权限责令停建、予以关闭；造成严重职业中毒危害或者导致职业中毒事故发生的，对负有责任的主管人员和其他直接责任人员依照刑法关于重大劳动安全事故罪或者其他罪的规定，依法追究刑事责任', '', '1', '2018-03-07 21:47:47', '3.3 建设项目职业病危害控制效果评价和防护设施竣工验收（续）', '职业病危害较重的建设项目职业病防护设施已经安监部门验收合格，并投入使用', '查看安全生产监督管理部门职业病防护设施竣工验收批文', '职业病危害较重的建设项目竣工验收时，建设单位应当按规定向安全生产监督管理部门申请建设项目职业病防护设施竣工验收，并提交下列文件、资料：（一）建设项目职业病防护设施竣工验收申请书；（二）建设项目职业病危害预评价报告审核批复文件；（三）建设项目职业病危害控制效果评价机构资质证明（影印件）；（四）建设项目立项审批文件（复印件）；（五）建设项目职业病防护设施设计专篇；（六）建设项目职业病危害控制效果评价报告；（七）职业卫生专家对职业病危害控制效果评价报告的审查意见； （八）建设单位对职业病危害控制效果评价报告的评审意见；（九）建设项目职业病防护设施竣工自行验收情况报告；(十）法律、行政法规、规章规定的其他文件、资料。', NULL, NULL);
