INSERT INTO `t_check_result` VALUES ('2bd9f640-7037-4f09-a8de-bac78177a570', '1', '371341201803070020', '913713003128266531', '189', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:50', '2,3,5', '1', '未将职业健康检查结果如实告知劳动者', '《职业病防治法》第36条第1款、《监督管理规定》第30条', '《职业病防治法》第72条（四）项、《监督管理规定》第50条（四）项', '责令限期改正，给予警告，可以并处5万元以上10万元以下罚款', '', '1', '2018-03-07 21:49:52', '9.4 职业健康检查结果告知', '已将职业健康检查结果如实告知劳动者', '查阅体检报告，查阅书面告知书，查阅职业健康档案，询问劳动者', '对从事接触职业病危害作业的劳动者，用人单位应按照国务院安全生产监督管理部门的规定组织上岗前、在岗期间、离岗时和应急职业健康检查，并将检查结果以书面形式如实告知劳动者', NULL, NULL);
INSERT INTO `t_check_result` VALUES ('181a6f73-3061-493d-86fc-df2c2cbb960d', '1', '371341201803070020', '913713003128266531', '157', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:50', '2,5', '1', '用人单位主要负责人、职业卫生管理人员未接受安全生产监督管理部门职业卫生培训的', '《职业病防治法》第35条第1款、《监督管理规定》第9条', '《监督管理规定》第48条（二）项', '给予警告，责令限期改正，可以并处5千元以上2万元以下罚款', '', '1', '2018-03-07 21:49:59', '10.1 主要负责人与管理人员培训', '用人单位主要负责人、职业卫生管理人员已接受安全生产监督管理部门职业卫生培训', '查阅培训证明', '用人单位的主要负责人和职业卫生管理人员应当具备与本单位所从事的生产经营活动相适应的职业卫生知识和管理能力，并接受职业卫生培训培训的主要内容：(一）、职业卫生相关法律、法规和国家职业卫生标准；（二）职业病危害预防和控制的基本知识；（三）职业卫生管理相关知识；（四)国家安全生产监督管理总局规定的其他内容', NULL, NULL);
INSERT INTO `t_check_result` VALUES ('5b4783a7-8033-4cc1-93ce-3328d9105441', '1', '371341201803070020', '913713003128266531', '132', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-07 21:47', '2018-03-07 21:50', '2,5', '1', '未按照规定组织劳动者进行职业卫生培训', '《职业病防治法》第35条第2款、《有毒作业条例》第19条第2款、《监督管理规定》第10条第1款、3款', '《职业病防治法》第71条（四）项、《监督管理规定》第49条（七）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '', '1', '2018-03-07 21:50:08', '10.2 劳动者培训 在岗期间职业卫生培训', '已按照规定组织劳动者进行职业卫生培训', '查阅人事工作与季度工作计划 ，核查新招工人、转岗工人人数与去向，查阅培训通知和培训计划，查阅培训签到簿、记录和培训教材、培训教员、考核成绩等培训档案资料，现场询问劳动者', '用人单位应定期对在岗期间的劳动者进行职业卫生培训做出明确规定培训的内容应包括职业卫生法律、法规、规章、操作规程、所在岗位的职业病危害及其防护设施、个人职业病防护用品的使用和维护、应急救援知识、劳动者所享有的职业卫生权利等内容根据用人单位实际情况制定培训计划，确定培训周期应做好记录及存档工作，存档内容包括培训通知、教材、教员、试卷、考核成绩等，档案资料应有专人负责保管', NULL, NULL);
