INSERT INTO `t_check_result` VALUES ('5044d0a1-d2c7-419f-a497-5869a134cd0a', '1', '371341201803040006', '913713003128266531', '157', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-04 13:36', '2018-03-04 13:41', '2,5', '1', '用人单位主要负责人、职业卫生管理人员未接受安全生产监督管理部门职业卫生培训的', '《职业病防治法》第35条第1款、《监督管理规定》第9条', '《监督管理规定》第48条（二）项', '给予警告，责令限期改正，可以并处5千元以上2万元以下罚款', '', '1', '2018-03-04 13:40:57', '10.1 主要负责人与管理人员培训', '用人单位主要负责人、职业卫生管理人员已接受安全生产监督管理部门职业卫生培训', '查阅培训证明', '用人单位的主要负责人和职业卫生管理人员应当具备与本单位所从事的生产经营活动相适应的职业卫生知识和管理能力，并接受职业卫生培训培训的主要内容：(一）、职业卫生相关法律、法规和国家职业卫生标准；（二）职业病危害预防和控制的基本知识；（三）职业卫生管理相关知识；（四)国家安全生产监督管理总局规定的其他内容', NULL, NULL);
