INSERT INTO `t_check_result` VALUES ('fc750eae-9d91-4dd0-813f-18a5620f4582', '2', '371341201803040003', '91371300312827584Q', '123', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-04 15:56', '2018-03-04 15:56', '2,3,4,5,6', '1', '未设置或者指定职业卫生管理机构或者组织', '《职业病防治法》第21条(一)项、《监督管理规定》第8条', '《职业病防治法》第71条（二）项、《监督管理规定》第49条（二）项', '给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款', '', '1', '2018-03-04 15:56:19', '1.1 职业卫生管理机构或者组织', '已经按照规定设置职业卫生管理机构', '查阅组织机构及职责文件，查阅工作情况记录，询问有关人员。', '用人单位应设置或者指定职业卫生管理机构或者组织，负责本单位的职业病防治工作。 职业病危害严重的用人单位，应当设置或者指定职业卫生管理机构或者组织；其他存在职业病危害的用人单位，劳动者超过100人的，应当设置或者指定职业卫生管理机构或者组织。', NULL, NULL);
INSERT INTO `t_check_result` VALUES ('03c721d9-2212-4b1e-b665-7f71d46bd8e1', '1', '371341201803040003', '91371300312827584Q', '122', 'c02325c7-21ff-4e1f-83bc-c28a80ec8004', NULL, '2018-03-04 15:56', '2018-03-04 15:56', '2,5', '1', '未配备专职或者兼职职业卫生管理人员', '《职业病防治法》第21条(一)项、 《有毒作业条例》第17条第2款、 《监督管理规定》第8条', '《职业病防治法》第71条（二）项、《有毒作业条例》第69条（一）项、《监督管理规定》第49条（二）项', '《职业病防治法》、《监督管理规定》给予警告，责令限期改正；逾期不改正的，处10万元以下的罚款。 《有毒作业条例》给予警告，责令限期改正，处5000元以上2万元以下的罚款；逾期不改正的，责令停止使用有毒物品作业，或者提请有关人民政府按照国务院规定的权限予以关闭', '', '1', '2018-03-04 15:56:19', '1.2 职业卫生管理人员', '已经配备专职职业卫生管理人员', '检查职业卫生管理人员的书面聘用文件、个人资质（职业卫生专业知识背景、工作经历和执业医师资格）文件和专业档案，查阅与服务机构签订的合同', '用人单位应配备专职或者兼职的职业卫生管理人员。 职业病危害严重的用人单位，应配备专职职业卫生管理人员其他存在职业病危害的用人单位，劳动者超过100人的，应当配备专职职业卫生管理人员；劳动者在100人以下的，应当配备专职或者兼职的职业卫生管理人员。 从事使用高毒物品作业的用人单位，应当配备专职的或者兼职的职业卫生医师和护士；不具备配备专职的或者兼职的职业卫生医师和护士条件的，应当与依法取得资质认可的职业卫生技术服务机构签订合同，由其提供职业卫生服务。', NULL, NULL);
