INSERT INTO `t_check_result` VALUES ('932149ff-102c-491c-ba50-c2f74f7b4575', '1', '371341201803040002', '92371300MA3FC83H16', '1da3687e-430c-493f-b9ee-666ab1c9f2f8', '265021b6-5c04-4035-a736-7243b7c87018', NULL, '2018-03-04 16:07', '2018-03-04 16:07', '2,', '2', '营业执照中单位名称、主要负责人、单位地址、经济类型、许可范围等信息与安全生产许可证中对应信息不一致', '《非煤矿矿山企业安全生产许可证实施办法》国家安全生产监督管理总局令第20号第八条', '《非煤矿矿山企业安全生产许可证实施办法》国家安全生产监督管理总局令第20号第44条第一款', '责令限期办理变更手续，并处1万元以上3万元以下罚款', '', '2', '2018-03-04 16:07:32', '证照  ', '营业执照中单位名称、主要负责人、单位地址、经济类型、许可范围等信息与安全生产许可证中对应信息一致', '查营业执照、采矿许可证、安全生产许可证、爆破作业单位许可证或爆破协议', ' 生产矿山应具有营业执照、采矿许可证、安全生产许可证，并在有效期内；基建矿山应具有营业执照、采矿许可证，并在有效期内；自主爆破的矿山应具备爆破作业单位许可证，并在有效期内，其他矿山（非自主爆破的矿山）应与有资质的单位签订爆破协议。\n', NULL, NULL);
